function calculate({ total_annual_energy_demand, local_renewable_generation, local_non_renewable_generation, external_grid_imports, energy_export_to_grid, average_energy_storage_dispatch }) {
    // Ensure all inputs are non-negative
    total_annual_energy_demand = Math.max(0, total_annual_energy_demand);
    local_renewable_generation = Math.max(0, local_renewable_generation);
    local_non_renewable_generation = Math.max(0, local_non_renewable_generation);
    external_grid_imports = Math.max(0, external_grid_imports);
    energy_export_to_grid = Math.max(0, energy_export_to_grid);
    average_energy_storage_dispatch = Math.max(0, average_energy_storage_dispatch);

    let independence_score = 0;
    let renewable_penetration_rate = 0;
    let import_dependency_rate = 0;

    // 1. Calculate Effective Local Renewable Supply (ELRS)
    // This includes locally generated renewables and energy dispatched from local storage (often charged by renewables).
    const effective_local_renewable_supply = local_renewable_generation + average_energy_storage_dispatch;

    // 2. Calculate Net External Imports (NEI)
    // Imports minus any exports, ensuring net imports don't go negative (meaning we're a net exporter).
    const net_external_imports = Math.max(0, external_grid_imports - energy_export_to_grid);

    // 3. Calculate Total Energy for Grid Interaction (TEGI) for the Independence Score
    // This represents the sum of internally renewable-driven energy and externally imported energy,
    // forming the basis for the 'versus' comparison.
    const total_energy_for_grid_interaction = effective_local_renewable_supply + net_external_imports;

    // 4. Calculate Renewable Energy Grid Independence Score
    if (total_energy_for_grid_interaction > 0) {
        independence_score = (effective_local_renewable_supply / total_energy_for_grid_interaction) * 100;
    } else if (effective_local_renewable_supply > 0 && net_external_imports === 0) {
        // Special case: Only local renewables, no imports/exports. Perfectly independent regarding this metric.
        independence_score = 100; // All energy is local renewable, no external interaction.
    } else {
        independence_score = 0; // No relevant energy flow or complete dependency.
    }

    // 5. Calculate Renewable Penetration Rate
    // Percentage of total annual energy demand met by effective local renewable supply.
    if (total_annual_energy_demand > 0) {
        renewable_penetration_rate = (effective_local_renewable_supply / total_annual_energy_demand) * 100;
        renewable_penetration_rate = Math.min(100, renewable_penetration_rate); // Cap at 100% penetration
    }

    // 6. Calculate Import Dependency Rate
    // Percentage of total annual energy demand met by net external imports.
    if (total_annual_energy_demand > 0) {
        import_dependency_rate = (net_external_imports / total_annual_energy_demand) * 100;
        import_dependency_rate = Math.min(100, import_dependency_rate); // Cap at 100% dependency
    }

    return {
        independence_score: independence_score,
        renewable_penetration_rate: renewable_penetration_rate,
        import_dependency_rate: import_dependency_rate
    };
}
