function calculate() {
    const initial_re_capacity_mw = Math.max(0, inputs.initial_re_capacity_mw);
    const re_capex_per_mw = Math.max(0, inputs.re_capex_per_mw);
    const grid_integration_capex_per_re_mw = Math.max(0, inputs.grid_integration_capex_per_re_mw);
    const re_opex_fixed_per_mw_year = Math.max(0, inputs.re_opex_fixed_per_mw_year);
    const annual_capacity_factor_percent = Math.max(0, Math.min(100, inputs.annual_capacity_factor_percent));
    const avoided_fossil_fuel_cost_per_mwh = Math.max(0, inputs.avoided_fossil_fuel_cost_per_mwh);
    const avoided_co2_price_per_ton = Math.max(0, inputs.avoided_co2_price_per_ton);
    const displaced_co2_per_mwh = Math.max(0, inputs.displaced_co2_per_mwh);
    const discount_rate_percent = Math.max(0, inputs.discount_rate_percent);
    const project_lifespan_years = Math.max(1, inputs.project_lifespan_years);

    // Convert percentages to decimals
    const discount_rate = discount_rate_percent / 100;
    const capacity_factor = annual_capacity_factor_percent / 100;

    // 1. Calculate Total Initial CAPEX
    const re_plant_capex = initial_re_capacity_mw * re_capex_per_mw;
    const grid_capex = initial_re_capacity_mw * grid_integration_capex_per_re_mw;
    const total_initial_capex = re_plant_capex + grid_capex;

    // 2. Calculate Annual Energy Generation
    const annual_re_generation_mwh = initial_re_capacity_mw * capacity_factor * 8760; // 8760 hours in a year
    if (annual_re_generation_mwh === 0) {
        return {
            total_initial_capex: total_initial_capex,
            total_lifetime_opex_npv: 0,
            total_lifetime_benefits_npv: 0,
            net_present_value: -total_initial_capex,
            levelized_cost_of_energy: Infinity,
            payback_period_years: Infinity
        };
    }

    // 3. Calculate Annual Operational Expenditures
    const annual_re_opex_fixed = initial_re_capacity_mw * re_opex_fixed_per_mw_year;
    // Assuming no variable Opex tied directly to MWh generated from inputs, only fixed Opex.
    // If variable Opex was an input: const re_opex_variable_per_mwh = ...; const annual_re_opex_variable = annual_re_generation_mwh * re_opex_variable_per_mwh;
    const total_annual_opex = annual_re_opex_fixed; // Add variable Opex here if available

    // 4. Calculate Annual Economic Benefits
    const annual_avoided_fuel_benefits = annual_re_generation_mwh * avoided_fossil_fuel_cost_per_mwh;
    const annual_avoided_co2_tons = annual_re_generation_mwh * displaced_co2_per_mwh;
    const annual_carbon_credit_benefits = annual_avoided_co2_tons * avoided_co2_price_per_ton;
    const total_annual_benefits = annual_avoided_fuel_benefits + annual_carbon_credit_benefits;

    // Initialize NPV and LCOE components
    let net_present_value = -total_initial_capex;
    let total_lifetime_opex_npv = 0;
    let total_lifetime_benefits_npv = 0;
    let sum_discounted_energy = 0;
    let cumulative_cash_flow = -total_initial_capex;
    let payback_period_years = Infinity;
    let found_payback = false;

    // 5. Perform NPV Analysis and Payback Period Calculation
    for (let year = 1; year <= project_lifespan_years; year++) {
        const discount_factor = 1 / Math.pow(1 + discount_rate, year);

        const discounted_annual_opex = total_annual_opex * discount_factor;
        total_lifetime_opex_npv += discounted_annual_opex;

        const discounted_annual_benefits = total_annual_benefits * discount_factor;
        total_lifetime_benefits_npv += discounted_annual_benefits;

        const net_cash_flow_year = total_annual_benefits - total_annual_opex;
        net_present_value += net_cash_flow_year * discount_factor;

        sum_discounted_energy += annual_re_generation_mwh * discount_factor;

        // Simple Payback Period calculation
        if (!found_payback) {
            cumulative_cash_flow += net_cash_flow_year;
            if (cumulative_cash_flow >= 0) {
                payback_period_years = year - 1 + (- (cumulative_cash_flow - net_cash_flow_year)) / net_cash_flow_year;
                found_payback = true;
            }
        }
    }

    // 6. Calculate Levelized Cost of Energy (LCOE)
    // LCOE = (Initial CAPEX + NPV of Opex) / (NPV of Energy Generated)
    let levelized_cost_of_energy;
    if (sum_discounted_energy > 0) {
        levelized_cost_of_energy = (total_initial_capex + total_lifetime_opex_npv) / sum_discounted_energy;
    } else {
        levelized_cost_of_energy = Infinity; // Should not happen with positive generation
    }

    // Edge case for payback: if net cash flow is never positive or only positive in the last year but not enough to offset initial CAPEX, payback is effectively never or project lifespan
    if (!found_payback && cumulative_cash_flow < 0) {
        payback_period_years = Infinity;
    } else if (!found_payback && cumulative_cash_flow >= 0) { // If it turns positive exactly at the last year boundary
        payback_period_years = project_lifespan_years;
    }

    return {
        total_initial_capex: total_initial_capex,
        total_lifetime_opex_npv: total_lifetime_opex_npv,
        total_lifetime_benefits_npv: total_lifetime_benefits_npv,
        net_present_value: net_present_value,
        levelized_cost_of_energy: levelized_cost_of_energy,
        payback_period_years: payback_period_years
    };
}
