function calculate() {
    const initialInvestment = inputs.initialInvestment;
    const projectLifespan = inputs.projectLifespan;
    const annualOpexSavings = inputs.annualOpexSavings;
    const annualOutageCostReduction = inputs.annualOutageCostReduction;
    const annualRenewableIntegrationBenefit = inputs.annualRenewableIntegrationBenefit;
    const annualGridModernizationBenefit = inputs.annualGridModernizationBenefit;
    const annualMaintenanceCostIncrease = inputs.annualMaintenanceCostIncrease;
    const discountRate = inputs.discountRate / 100; // Convert percentage to decimal

    // --- Calculate Total Annual Net Benefit --- 
    // This represents the annual cash flow from the investment.
    const totalAnnualNetBenefit = annualOpexSavings +
                                 annualOutageCostReduction +
                                 annualRenewableIntegrationBenefit +
                                 annualGridModernizationBenefit -
                                 annualMaintenanceCostIncrease;

    // --- Calculate Simple Payback Period ---
    // Time required for the cumulative cash inflows to equal the initial investment.
    let paybackPeriod;
    if (totalAnnualNetBenefit <= 0) {
        paybackPeriod = initialInvestment > 0 ? "Never" : 0; // If no net benefit, or negative, and initial investment exists, it never pays back. If no investment, it's 0.
    } else {
        paybackPeriod = initialInvestment / totalAnnualNetBenefit;
    }

    // --- Calculate Total Undiscounted Project Benefits ---
    // Sum of all annual net benefits over the project's lifespan, without considering time value of money.
    const totalProjectBenefitsUndiscounted = totalAnnualNetBenefit * projectLifespan;

    // --- Calculate Return on Investment (ROI) ---
    // Measures the profitability of the investment relative to its cost.
    let roi;
    if (initialInvestment === 0) {
        roi = totalProjectBenefitsUndiscounted > 0 ? Infinity : 0; // Infinite ROI if positive benefits with no investment.
    } else {
        roi = ((totalProjectBenefitsUndiscounted - initialInvestment) / initialInvestment) * 100;
    }

    // --- Calculate Net Present Value (NPV) ---
    // The difference between the present value of cash inflows and the present value of cash outflows.
    // A positive NPV indicates the project is expected to be profitable in present-day dollars.
    let npv = -initialInvestment; // Start with the initial investment as a negative cash flow.
    if (discountRate === -1) { // Edge case: discount rate of -100% means 1+r = 0, leading to division by zero.
        // In reality, a -100% discount rate is extremely rare and problematic for standard NPV. 
        // Here, we'll treat it as if no discounting occurs for simplicity in this specific edge case.
        npv = -initialInvestment + (totalAnnualNetBenefit * projectLifespan);
    } else {
        for (let i = 1; i <= projectLifespan; i++) {
            npv += totalAnnualNetBenefit / Math.pow(1 + discountRate, i);
        }
    }

    return {
        roi: roi,
        npv: npv,
        paybackPeriod: typeof paybackPeriod === 'number' ? Math.min(paybackPeriod, projectLifespan) : paybackPeriod // Cap numeric payback period at project lifespan for practical context
    };
}
