function calculate(inputs) {
  const { 
    currentAnnualEnergyBill, 
    currentElectricityPrice, 
    homeSizeSqFt, 
    estimatedEfficiencySavingsPercentage, 
    solarPVSystemSizekW, 
    solarPVSystemCostPerWatt, 
    otherRetrofitCost, 
    governmentIncentivesPercentage, 
    annualEnergyPriceInflation, 
    carbonIntensityGrid 
  } = inputs;

  // Constants & Sanity Checks
  const KWH_PER_KW_PER_YEAR = 1300; // Average solar generation in kWh per kW per year
  const MAX_INCENTIVE_PERCENTAGE = 100; // Cap incentives at 100%
  const MIN_ELECTRICITY_PRICE = 0.01; // Prevent division by zero or unrealistic prices

  if (currentElectricityPrice <= MIN_ELECTRICITY_PRICE) {
    return {
      annualEnergySavingsKWh: 0,
      annualCostSavings: 0,
      carbonReductionAnnual: 0,
      totalProjectCost: 0,
      netProjectCost: 0,
      simplePaybackPeriodYears: 'N/A',
      roiYear10: 0,
      currentAnnualConsumptionKWh: 0
    };
  }

  // 1. Calculate current annual energy consumption
  const currentAnnualConsumptionKWh = currentAnnualEnergyBill / currentElectricityPrice;

  // 2. Calculate energy savings from efficiency improvements
  const efficiencySavingsKWh = currentAnnualConsumptionKWh * (estimatedEfficiencySavingsPercentage / 100);

  // 3. Calculate energy generation from Solar PV
  const solarGenerationKWh = solarPVSystemSizekW > 0 ? solarPVSystemSizekW * KWH_PER_KW_PER_YEAR : 0;

  // 4. Total estimated annual energy savings
  const annualEnergySavingsKWh = efficiencySavingsKWh + solarGenerationKWh;

  // 5. Calculate annual monetary savings
  const annualCostSavings = annualEnergySavingsKWh * currentElectricityPrice;

  // 6. Calculate total project cost
  const solarPVCost = solarPVSystemSizekW * solarPVSystemCostPerWatt * 1000; // kW to Watts
  const totalProjectCost = solarPVCost + otherRetrofitCost;

  // 7. Calculate net project cost after incentives
  const effectiveIncentivesPercentage = Math.min(governmentIncentivesPercentage, MAX_INCENTIVE_PERCENTAGE) / 100;
  const netProjectCost = totalProjectCost * (1 - effectiveIncentivesPercentage);

  // 8. Calculate simple payback period
  let simplePaybackPeriodYears = 'N/A';
  if (annualCostSavings > 0 && netProjectCost >= 0) {
    simplePaybackPeriodYears = netProjectCost / annualCostSavings;
    if (simplePaybackPeriodYears < 0) simplePaybackPeriodYears = 'N/A'; // Handle cases where savings might be negative relative to cost
  } else if (netProjectCost < 0 && annualCostSavings >= 0) {
    simplePaybackPeriodYears = 0; // Negative cost (e.g., 100% incentives + profit), instant payback
  }

  // 9. Calculate CO2 reduction
  const carbonReductionAnnual = annualEnergySavingsKWh * carbonIntensityGrid;

  // 10. Calculate ROI after 10 years (considering energy price inflation)
  let cumulativeSavings10Years = 0;
  const inflationFactor = 1 + (annualEnergyPriceInflation / 100);
  let escalatedElectricityPrice = currentElectricityPrice;
  for (let i = 0; i < 10; i++) {
    cumulativeSavings10Years += annualEnergySavingsKWh * escalatedElectricityPrice;
    escalatedElectricityPrice *= inflationFactor;
  }

  let roiYear10 = 0;
  if (netProjectCost > 0) {
    roiYear10 = ((cumulativeSavings10Years - netProjectCost) / netProjectCost) * 100;
  } else if (netProjectCost === 0) {
    roiYear10 = (cumulativeSavings10Years > 0) ? Infinity : 0; // Infinite ROI if cost is 0 and savings exist
  } else { // Negative netProjectCost (e.g., incentives > total cost)
    roiYear10 = (cumulativeSavings10Years / Math.abs(netProjectCost)) * 100; // Treat as revenue on investment, not cost recovery
  }

  return {
    currentAnnualConsumptionKWh: currentAnnualConsumptionKWh,
    annualEnergySavingsKWh: annualEnergySavingsKWh,
    annualCostSavings: annualCostSavings,
    carbonReductionAnnual: carbonReductionAnnual,
    totalProjectCost: totalProjectCost,
    netProjectCost: netProjectCost,
    simplePaybackPeriodYears: typeof simplePaybackPeriodYears === 'number' ? Math.max(0, simplePaybackPeriodYears) : simplePaybackPeriodYears,
    roiYear10: roiYear10
  };
}
