function calculate({ projectType, projectCapacityMW, siteComplexityRating, gridConnectionDistanceKM, materialLeadTimeMonths, laborAvailabilityIndex, regulatoryApprovalMonths, environmentalRiskRating }) {
    // Input validation and default values for safety
    projectType = Math.max(1, Math.min(4, projectType || 1));
    projectCapacityMW = Math.max(0.1, projectCapacityMW || 1); // Minimum 0.1MW
    siteComplexityRating = Math.max(1, Math.min(5, siteComplexityRating || 3));
    gridConnectionDistanceKM = Math.max(0, gridConnectionDistanceKM || 5);
    materialLeadTimeMonths = Math.max(1, materialLeadTimeMonths || 6);
    laborAvailabilityIndex = Math.max(1, Math.min(5, laborAvailabilityIndex || 3));
    regulatoryApprovalMonths = Math.max(1, regulatoryApprovalMonths || 12);
    environmentalRiskRating = Math.max(1, Math.min(5, environmentalRiskRating || 3));

    let basePhysicalConstructionMonths = 0;
    let capacityFactorPerMW = 0;
    let typeComplexityMultiplier = 1; // General multiplier for inherent complexity

    // Step 1: Determine base physical construction duration based on project type and capacity
    switch (projectType) {
        case 1: // Offshore Wind
            basePhysicalConstructionMonths = 36; // Base duration for typical offshore project setup activities (site prep, foundation)
            capacityFactorPerMW = 0.08; // Months per MW for turbine installation, cabling, etc.
            typeComplexityMultiplier = 1.6; // Offshore wind is inherently more complex and slower
            break;
        case 2: // Onshore Wind
            basePhysicalConstructionMonths = 18;
            capacityFactorPerMW = 0.05;
            typeComplexityMultiplier = 1.0;
            break;
        case 3: // Utility-Scale Solar
            basePhysicalConstructionMonths = 12;
            capacityFactorPerMW = 0.03;
            typeComplexityMultiplier = 0.8; // Generally faster to deploy
            break;
        case 4: // Hydroelectric (Small/Medium Scale)
            basePhysicalConstructionMonths = 30;
            capacityFactorPerMW = 0.07;
            typeComplexityMultiplier = 1.3; // Can be complex due to civil works like dam construction
            break;
        default: // Default to Onshore Wind
            basePhysicalConstructionMonths = 18;
            capacityFactorPerMW = 0.05;
            typeComplexityMultiplier = 1.0;
    }

    // Initial capacity-based physical construction duration
    let durationFromCapacity = basePhysicalConstructionMonths + (projectCapacityMW * capacityFactorPerMW);
    durationFromCapacity *= typeComplexityMultiplier;

    // Step 2: Apply adjustments based on specific project factors to physical construction duration

    // Site Complexity: (1=Low, 5=High). Higher complexity increases duration.
    // Factor: 1 + (rating - 1) * 0.15 => ranges from 1.0 (for 1) to 1.6 (for 5)
    let siteFactor = 1 + (siteComplexityRating - 1) * 0.15;

    // Grid Connection Distance: Longer distances mean more infrastructure and time.
    // Factor: 1 + (distance / 50) * 0.1 => 1.0 (for 0km) to ~1.16 (for 80km, a realistic max for many projects to impact here)
    let gridFactor = 1 + (gridConnectionDistanceKM / 50) * 0.08;

    // Labor Availability: (1=Scarce, 5=Abundant). Scarce labor slows down projects.
    // Factor: 1 + (3 - index) * 0.1 => ranges from 1.2 (for 1) to 0.8 (for 5), neutral at 3
    let laborFactor = 1 + (3 - laborAvailabilityIndex) * 0.1;

    // Environmental & Social Impact Risk: Higher risk often means more mitigation measures, consultations, and potential delays during construction.
    // Factor: 1 + (rating - 1) * 0.07 => ranges from 1.0 (for 1) to 1.28 (for 5)
    let environmentalFactor = 1 + (environmentalRiskRating - 1) * 0.07;

    // Combine factors for estimated physical construction duration
    let estimatedConstructionDurationMonths = durationFromCapacity * siteFactor * gridFactor * laborFactor * environmentalFactor;

    // Material Lead Time: Critical path item. Directly adds to duration if longer than a typical pre-construction procurement phase.
    // Assuming typical material sourcing can be 6-9 months and partially overlap with early construction prep.
    // If actual lead time exceeds this buffer, it becomes a direct delay.
    let standardProcurementOverlap = Math.min(9, estimatedConstructionDurationMonths * 0.25); // Max 9 months overlap, or 25% of construction time
    let materialDelayMonths = Math.max(0, materialLeadTimeMonths - standardProcurementOverlap);

    estimatedConstructionDurationMonths += materialDelayMonths;

    // Cap estimatedConstructionDurationMonths minimum
    estimatedConstructionDurationMonths = Math.max(6, estimatedConstructionDurationMonths); // Minimum 6 months physical construction

    // Step 3: Calculate contingency buffer
    // Contingency based on combined risk factors (complexity, environmental, labor scarcity) and overall duration.
    let averageRiskScore = (siteComplexityRating + environmentalRiskRating + (6 - laborAvailabilityIndex)) / 3; // Normalize 1-5 risks. Labor inverse: 1 (scarce) is high risk, 5 (abundant) is low.
    let contingencyPercentage = 0.15 + (averageRiskScore - 1) * 0.05; // Base 15%, increases with risk up to 35% for max averageRiskScore (5)
    let contingencyBufferMonths = estimatedConstructionDurationMonths * contingencyPercentage;

    // Cap contingency buffer
    contingencyBufferMonths = Math.max(3, Math.min(estimatedConstructionDurationMonths * 0.5, contingencyBufferMonths)); // Min 3 months, Max 50% of construction

    // Step 4: Compute Total Project Timeline (Regulatory + Physical Construction + Contingency)
    // Regulatory approvals represent a significant pre-construction phase that must be substantially complete before major physical works.
    // Thus, it is added sequentially to the physical construction phase, acknowledging some overlap in early stages but representing a critical path dependency.
    let totalProjectTimelineMonths = regulatoryApprovalMonths + estimatedConstructionDurationMonths + contingencyBufferMonths;

    return {
        estimatedConstructionDurationMonths: estimatedConstructionDurationMonths,
        contingencyBufferMonths: contingencyBufferMonths,
        totalProjectTimelineMonths: totalProjectTimelineMonths
    };
}
