function calculate({ totalLandAreaHa, solarEfficiencyMWpHa, windEfficiencyMWpHa, geothermalEfficiencyMWpHa, solarEcoCommImpactScore, windEcoCommImpactScore, geothermalEcoCommImpactScore, outputVsImpactPreference }) {

  // Input validation and clamping
  totalLandAreaHa = Math.max(0, totalLandAreaHa);
  solarEfficiencyMWpHa = Math.max(0, solarEfficiencyMWpHa);
  windEfficiencyMWpHa = Math.max(0, windEfficiencyMWpHa);
  geothermalEfficiencyMWpHa = Math.max(0, geothermalEfficiencyMWpHa);

  solarEcoCommImpactScore = Math.max(0, solarEcoCommImpactScore);
  windEcoCommImpactScore = Math.max(0, windEcoCommImpactScore);
  geothermalEcoCommImpactScore = Math.max(0, geothermalEcoCommImpactScore);

  outputVsImpactPreference = Math.min(1, Math.max(0, outputVsImpactPreference));

  const weightOutput = outputVsImpactPreference;
  const weightImpact = 1 - outputVsImpactPreference;

  // Calculate a weighted net score per hectare for each technology
  // Score = (Efficiency * weightOutput) - (Efficiency * ImpactScore * weightImpact)
  const solarScorePerHa = (solarEfficiencyMWpHa * weightOutput) - (solarEfficiencyMWpHa * solarEcoCommImpactScore * weightImpact);
  const windScorePerHa = (windEfficiencyMWpHa * weightOutput) - (windEfficiencyMWpHa * windEcoCommImpactScore * weightImpact);
  const geothermalScorePerHa = (geothermalEfficiencyMWpHa * weightOutput) - (geothermalEfficiencyMWpHa * geothermalEcoCommImpactScore * weightImpact);

  const technologies = [
    { type: 'solar', score: solarScorePerHa, efficiency: solarEfficiencyMWpHa, impactFactor: solarEcoCommImpactScore },
    { type: 'wind', score: windScorePerHa, efficiency: windEfficiencyMWpHa, impactFactor: windEcoCommImpactScore },
    { type: 'geothermal', score: geothermalScorePerHa, efficiency: geothermalEfficiencyMWpHa, impactFactor: geothermalEcoCommImpactScore }
  ];

  // Filter out technologies with non-positive scores, as they are not desirable under current preferences
  const viableTechnologies = technologies.filter(tech => tech.score > 0);

  let solarAllocatedAreaHa = 0;
  let windAllocatedAreaHa = 0;
  let geothermalAllocatedAreaHa = 0;
  let totalAllocatedLand = 0;

  if (totalLandAreaHa <= 0) {
    return {
      solarAllocatedAreaHa: 0,
      windAllocatedAreaHa: 0,
      geothermalAllocatedAreaHa: 0,
      totalPowerOutputMW: 0,
      totalCombinedImpactScore: 0,
      landUtilizationPercentage: 0
    };
  }

  if (viableTechnologies.length === 0) {
    // If no technology is viable, allocate zero land to all
    return {
      solarAllocatedAreaHa: 0,
      windAllocatedAreaHa: 0,
      geothermalAllocatedAreaHa: 0,
      totalPowerOutputMW: 0,
      totalCombinedImpactScore: 0,
      landUtilizationPercentage: 0
    };
  }

  const sumOfPositiveScores = viableTechnologies.reduce((sum, tech) => sum + tech.score, 0);

  // Proportionally distribute the total land area among viable technologies
  viableTechnologies.forEach(tech => {
    const allocatedArea = (tech.score / sumOfPositiveScores) * totalLandAreaHa;
    totalAllocatedLand += allocatedArea;
    if (tech.type === 'solar') {
      solarAllocatedAreaHa = allocatedArea;
    } else if (tech.type === 'wind') {
      windAllocatedAreaHa = allocatedArea;
    } else if (tech.type === 'geothermal') {
      geothermalAllocatedAreaHa = allocatedArea;
    }
  });

  // Calculate total power output
  const totalPowerOutputMW = 
    (solarAllocatedAreaHa * solarEfficiencyMWpHa) +
    (windAllocatedAreaHa * windEfficiencyMWpHa) +
    (geothermalAllocatedAreaHa * geothermalEfficiencyMWpHa);

  // Calculate total combined ecological and community impact score
  const totalCombinedImpactScore = 
    (solarAllocatedAreaHa * solarEfficiencyMWpHa * solarEcoCommImpactScore) +
    (windAllocatedAreaHa * windEfficiencyMWpHa * windEcoCommImpactScore) +
    (geothermalAllocatedAreaHa * geothermalEfficiencyMWpHa * geothermalEcoCommImpactScore);

  // Calculate land utilization percentage
  const landUtilizationPercentage = (totalAllocatedLand / totalLandAreaHa) * 100;

  return {
    solarAllocatedAreaHa: solarAllocatedAreaHa,
    windAllocatedAreaHa: windAllocatedAreaHa,
    geothermalAllocatedAreaHa: geothermalAllocatedAreaHa,
    totalPowerOutputMW: totalPowerOutputMW,
    totalCombinedImpactScore: totalCombinedImpactScore,
    landUtilizationPercentage: landUtilizationPercentage
  };
}
