const capacityFactorDecimal = projectCapacityFactorPercent / 100;
const operatingCostRateDecimal = projectOperatingCostRatePercent / 100;
const discountRateDecimal = discountRatePercent / 100;
const hoursPerYear = 8760;

// Calculate total lost energy generation
const totalLostEnergyMWh = projectCapacityMW * capacityFactorDecimal * suspensionDurationYears * hoursPerYear;
const lostEnergyGWh = totalLostEnergyMWh / 1000;

// Calculate total lost revenue
const lostRevenueUSD = totalLostEnergyMWh * averageElectricityPricePerMWh;

// Calculate annual potential revenue and net profit for opportunity cost
const annualPotentialRevenue = projectCapacityMW * capacityFactorDecimal * hoursPerYear * averageElectricityPricePerMWh;
const annualOperationalCost = annualPotentialRevenue * operatingCostRateDecimal;
const annualNetProfit = annualPotentialRevenue - annualOperationalCost;

// Calculate Opportunity Cost as Net Present Value (NPV) of lost annual net profits
let opportunityCostNPVUSD = 0;
if (suspensionDurationYears > 0) {
  for (let t = 1; t <= suspensionDurationYears; t++) {
    opportunityCostNPVUSD += annualNetProfit / Math.pow(1 + discountRateDecimal, t);
  }
}

return {
  lostEnergyGWh: lostEnergyGWh,
  lostRevenueUSD: lostRevenueUSD,
  opportunityCostNPVUSD: opportunityCostNPVUSD
};
