formula:
const { initial_capital_cost, annual_opex, annual_energy_production, avg_energy_price, project_lifespan, itc_percentage, ptc_rate, ptc_duration_years } = inputs;

// 1. Net Initial Investment
const net_initial_cost = initial_capital_cost * (1 - itc_percentage / 100);

// 2. Annual Revenues
const annual_energy_revenue = annual_energy_production * avg_energy_price;
const annual_ptc_revenue = annual_energy_production * ptc_rate;

// 3. Annual Operating Profit (during PTC period and after)
const annual_gross_revenue_with_ptc = annual_energy_revenue + annual_ptc_revenue;
const annual_operating_profit_with_ptc = annual_gross_revenue_with_ptc - annual_opex;

const annual_gross_revenue_no_ptc = annual_energy_revenue;
const annual_operating_profit_no_ptc = annual_gross_revenue_no_ptc - annual_opex;

// Cap profits at 0 if negative, as a project won't operate at a loss forever without shutting down
const effective_annual_operating_profit_with_ptc = Math.max(0, annual_operating_profit_with_ptc);
const effective_annual_operating_profit_no_ptc = Math.max(0, annual_operating_profit_no_ptc);

// 4. Calculate Total Project Profit over lifespan for Simple ROI
let total_profit_over_lifespan = 0;
const actual_ptc_duration = Math.min(ptc_duration_years, project_lifespan);
const years_after_ptc = project_lifespan - actual_ptc_duration;

total_profit_over_lifespan = (effective_annual_operating_profit_with_ptc * actual_ptc_duration) +
                             (effective_annual_operating_profit_no_ptc * years_after_ptc);

// Ensure net_initial_cost is not zero to avoid division by zero for ROI
const simple_roi = net_initial_cost > 0 ? (total_profit_over_lifespan / net_initial_cost) * 100 : 0;

// 5. Calculate Payback Period
let payback_period = Infinity;

if (net_initial_cost <= 0) {
    payback_period = 0; // If initial cost is zero or negative, instant payback
} else if (effective_annual_operating_profit_with_ptc <= 0 && effective_annual_operating_profit_no_ptc <= 0) {
    payback_period = Infinity; // Project never pays back if all profits are non-positive
} else {
    // Payback during PTC period
    if (effective_annual_operating_profit_with_ptc > 0) {
        const years_to_payback_with_ptc = net_initial_cost / effective_annual_operating_profit_with_ptc;
        if (years_to_payback_with_ptc <= actual_ptc_duration) {
            payback_period = years_to_payback_with_ptc;
        } else {
            // Payback extends beyond PTC period
            let remaining_investment = net_initial_cost - (effective_annual_operating_profit_with_ptc * actual_ptc_duration);
            payback_period = actual_ptc_duration;

            if (effective_annual_operating_profit_no_ptc > 0) {
                payback_period += remaining_investment / effective_annual_operating_profit_no_ptc;
            } else {
                payback_period = Infinity; // Can't pay back after PTC if profit is 0 or negative
            }
        }
    } else { // If no profit with PTC (or PTC duration is 0, handled by actual_ptc_duration=0 effectively)
        if (effective_annual_operating_profit_no_ptc > 0) {
            payback_period = net_initial_cost / effective_annual_operating_profit_no_ptc;
        } else {
            payback_period = Infinity; // Cannot pay back at all
        }
    }
}

// Cap payback period at project lifespan if it exceeds it, or if it's practically never
payback_period = Math.min(payback_period, project_lifespan);
// Ensure payback period is not negative
payback_period = Math.max(0, payback_period);

return {
  net_initial_cost: net_initial_cost,
  annual_gross_revenue: annual_gross_revenue_with_ptc,
  annual_operating_profit: annual_operating_profit_with_ptc,
  simple_roi: simple_roi,
  payback_period: payback_period
};
