function calculate({ room_length, room_width, material_cost_per_sqm, waste_percentage, buffer_percentage, material_unit_coverage }) {
  const total_area_sqm = room_length * room_width;

  const waste_factor = 1 + (waste_percentage / 100);
  const buffer_factor = 1 + (buffer_percentage / 100);

  // Area considering waste and buffer for planning
  const material_area_needed = total_area_sqm * waste_factor * buffer_factor;

  // Number of units (e.g., boxes of tiles) needed, always rounded up
  const required_material_units = Math.ceil(material_area_needed / material_unit_coverage);

  // Actual area covered by the purchased units (since you buy in full units/boxes)
  const actual_purchased_area = required_material_units * material_unit_coverage;

  // Total cost based on actual purchased units/area
  const total_material_cost = actual_purchased_area * material_cost_per_sqm;

  // The estimated cost of material that will be wasted or used as buffer
  const estimated_waste_area = actual_purchased_area - total_area_sqm;
  const estimated_waste_cost = estimated_waste_area * material_cost_per_sqm;

  return {
    total_area_sqm,
    material_area_needed,
    required_material_units,
    total_material_cost,
    estimated_waste_cost
  };
}
