const purchasePrice = inputs.purchasePrice;
const downPaymentPct = inputs.downPaymentPct;
const interestRate = inputs.interestRate;
const loanTermYears = inputs.loanTermYears;
const monthlyRent = inputs.monthlyRent;
const monthlyOperatingExpenses = inputs.monthlyOperatingExpenses;
const vacancyRate = inputs.vacancyRate;
const annualAppreciationRate = inputs.annualAppreciationRate;

// Derived values
const downPaymentAmount = purchasePrice * downPaymentPct;
const loanAmount = purchasePrice - downPaymentAmount;

// Calculate Monthly Mortgage Payment (P&I only)
let monthlyMortgagePayment = 0;
const monthlyInterestRate = interestRate / 12;
const numberOfPayments = loanTermYears * 12;

if (loanAmount > 0 && monthlyInterestRate > 0) {
    monthlyMortgagePayment = loanAmount * (monthlyInterestRate * Math.pow(1 + monthlyInterestRate, numberOfPayments)) / (Math.pow(1 + monthlyInterestRate, numberOfPayments) - 1);
} else if (loanAmount > 0 && monthlyInterestRate === 0 && numberOfPayments > 0) {
    monthlyMortgagePayment = loanAmount / numberOfPayments;
}

// Annual Income & Expenses
const annualGrossRent = monthlyRent * 12;
const annualVacancyLoss = annualGrossRent * vacancyRate;
const annualEffectiveGrossIncome = annualGrossRent - annualVacancyLoss;
const annualOperatingExpenses = monthlyOperatingExpenses * 12;
const annualDebtService = monthlyMortgagePayment * 12;

// Net Operating Income (NOI)
const noi = annualEffectiveGrossIncome - annualOperatingExpenses;

// Annual Cash Flow
const annualCashFlow = noi - annualDebtService;

// Calculate Outputs

// Cash-on-Cash Return
let cashOnCashROI = 0;
if (downPaymentAmount > 0) {
    cashOnCashROI = (annualCashFlow / downPaymentAmount) * 100;
}

// Capitalization Rate (Cap Rate)
let capRate = 0;
if (purchasePrice > 0) {
    capRate = (noi / purchasePrice) * 100;
}

// Total Annual Profit/Loss
const annualNetProfit = annualCashFlow;

// Annualized Total Return (considering appreciation and principal paydown)
const annualAppreciationGain = purchasePrice * annualAppreciationRate;

// Calculate principal paid in the first year
let principalPaidFirstYear = 0;
if (loanAmount > 0 && monthlyMortgagePayment > 0 && numberOfPayments > 0) {
    let remainingBalance = loanAmount;
    for (let i = 0; i < 12 && i < numberOfPayments; i++) {
        const interestPayment = remainingBalance * monthlyInterestRate;
        const principalPayment = monthlyMortgagePayment - interestPayment;

        const actualPrincipalPayment = Math.min(principalPayment, remainingBalance);

        principalPaidFirstYear += actualPrincipalPayment;
        remainingBalance -= actualPrincipalPayment;

        if (remainingBalance <= 0) break;
    }
} else if (loanAmount > 0 && monthlyInterestRate === 0 && numberOfPayments > 0) {
    principalPaidFirstYear = Math.min(annualDebtService, loanAmount); // All principal if no interest, capped at loan amount
}

const totalAnnualReturnNumerator = annualCashFlow + principalPaidFirstYear + annualAppreciationGain;
let totalAnnualReturn = 0;
if (downPaymentAmount > 0) {
    totalAnnualReturn = (totalAnnualReturnNumerator / downPaymentAmount) * 100;
}

return {
    cashOnCashROI: cashOnCashROI,
    capRate: capRate,
    annualNetProfit: annualNetProfit,
    totalAnnualReturn: totalAnnualReturn
};
