const purchasePrice = inputs.purchasePrice;
const downPaymentPct = inputs.downPaymentPct;
const closingCostsPct = inputs.closingCostsPct;
const renovationCosts = inputs.renovationCosts || 0;
const monthlyRent = inputs.monthlyRent;
const vacancyRatePct = inputs.vacancyRatePct;
const monthlyOperatingExpenses = inputs.monthlyOperatingExpenses;
const interestRate = inputs.interestRate;
const loanTermYears = inputs.loanTermYears;

// Initial Investment Calculations
const downPaymentAmount = purchasePrice * (downPaymentPct / 100);
const closingCostsAmount = purchasePrice * (closingCostsPct / 100);
const initialCashInvested = downPaymentAmount + closingCostsAmount + renovationCosts;

// Loan Calculations
const loanAmount = purchasePrice - downPaymentAmount;
let monthlyMortgagePayment = 0;
if (loanAmount > 0 && loanTermYears > 0) {
    if (interestRate > 0) {
        const monthlyInterestRate = (interestRate / 100) / 12;
        const numberOfPayments = loanTermYears * 12;
        monthlyMortgagePayment = loanAmount * (monthlyInterestRate * Math.pow(1 + monthlyInterestRate, numberOfPayments)) / (Math.pow(1 + monthlyInterestRate, numberOfPayments) - 1);
    } else { // Handle 0 interest rate (simple principal division)
        monthlyMortgagePayment = loanAmount / (loanTermYears * 12);
    }
}

// Income Calculations
const annualGrossRent = monthlyRent * 12;
const annualVacancyLoss = annualGrossRent * (vacancyRatePct / 100);
const annualEffectiveRentalIncome = annualGrossRent - annualVacancyLoss;

// Expense Calculations
const annualOperatingExpenses = monthlyOperatingExpenses * 12;
const annualMortgagePayment = monthlyMortgagePayment * 12;

// Annual Cash Flow
const annualCashFlow = annualEffectiveRentalIncome - annualOperatingExpenses - annualMortgagePayment;

// Cap Rate (Capitalization Rate)
// Cap Rate = (Annual Net Operating Income) / Property Purchase Price
// Annual Net Operating Income (NOI) = Annual Effective Rental Income - Annual Operating Expenses (does NOT include mortgage)
const annualNetOperatingIncome = annualEffectiveRentalIncome - annualOperatingExpenses;
const capRate = (purchasePrice > 0) ? (annualNetOperatingIncome / purchasePrice) * 100 : 0;

// Cash-on-Cash Return
// Cash-on-Cash Return = (Annual Before-Tax Cash Flow) / Total Cash Invested
const cashOnCashReturn = (initialCashInvested > 0) ? (annualCashFlow / initialCashInvested) * 100 : 0;

return {
    annualCashFlow: annualCashFlow,
    capRate: capRate,
    cashOnCashReturn: cashOnCashReturn
};
