const avgDailyDemand = inputs.avg_daily_demand;
const avgLeadTime = inputs.lead_time;
const serviceLevel = inputs.service_level;
const stdDevDemand = inputs.std_dev_demand;
const stdDevLeadTime = inputs.std_dev_lead_time;

// Basic Z-score mapping for common service levels
let zScore;
if (serviceLevel === 80) { zScore = 0.84; } 
else if (serviceLevel === 85) { zScore = 1.04; }
else if (serviceLevel === 90) { zScore = 1.28; }
else if (serviceLevel === 95) { zScore = 1.645; }
else if (serviceLevel === 98) { zScore = 2.05; }
else if (serviceLevel === 99) { zScore = 2.33; }
else if (serviceLevel >= 99) { zScore = 2.33; } // Cap at 99% for simplicity
else if (serviceLevel <= 80) { zScore = 0.84; } // Cap at 80% for simplicity
else { zScore = 1.645; } // Default to 95% if not matched or out of common range

// Ensure non-negative standard deviations for calculation
const safeStdDevDemand = Math.max(0, stdDevDemand);
const safeStdDevLeadTime = Math.max(0, stdDevLeadTime);

// Standard Deviation of Demand During Lead Time (combined variability)
// Formula: sqrt((Avg Lead Time * Std Dev of Daily Demand)^2 + (Avg Daily Demand * Std Dev of Lead Time)^2)
const stdDevDemandDuringLeadTime = Math.sqrt(
    (avgLeadTime * Math.pow(safeStdDevDemand, 2)) + 
    (Math.pow(avgDailyDemand, 2) * Math.pow(safeStdDevLeadTime, 2))
);

const safetyStock = zScore * stdDevDemandDuringLeadTime;
const reorderPoint = (avgDailyDemand * avgLeadTime) + safetyStock;

return {
    safety_stock: Math.max(0, safetyStock), // Safety stock should not be negative
    reorder_point: Math.max(0, reorderPoint) // Reorder point should not be negative
};
