function calculate({ riskFreeRate, beta, marketRiskPremium, companySpecificRiskPremium }) {
  // Convert percentages to decimals for calculation
  const rfrDecimal = riskFreeRate / 100;
  const mrpDecimal = marketRiskPremium / 100;
  const csrpDecimal = companySpecificRiskPremium ? companySpecificRiskPremium / 100 : 0;

  // Ensure beta is not excessively low, minimum of 0.1 to reflect some market sensitivity if input is very low
  const effectiveBeta = Math.max(beta, 0.1);

  // Calculate Required Rate of Return using a CAPM-like model + Company Specific Risk
  let rrr = rfrDecimal + (effectiveBeta * mrpDecimal) + csrpDecimal;

  // For 'Minimum Acceptable Return', apply a potential cap to company specific risk
  // and a floor to the total return, demonstrating multiple operations and conditionals.
  let adjustedCsrpDecimal = csrpDecimal;
  if (csrpDecimal > 0.05) { // If company specific risk premium is > 5%, cap it at 5% for the adjusted return
    adjustedCsrpDecimal = 0.05;
  }

  // Calculate an adjusted RRR with the potentially capped specific risk component
  let adjustedRrr = rfrDecimal + (effectiveBeta * mrpDecimal) + adjustedCsrpDecimal;

  // Ensure the minimum acceptable return is at least Risk-Free Rate + 2% as a floor
  const floorReturn = rfrDecimal + 0.02; 
  adjustedRrr = Math.max(adjustedRrr, floorReturn);

  // Convert results back to percentage and return
  return {
    requiredRateOfReturn: rrr * 100,
    minimumAcceptableReturn: adjustedRrr * 100
  };
}
