function (inputs) {
    const { dailyAvgEssentialEnergy, peakEssentialLoad, avgOutageDuration, avgOutageFrequency, gridElectricityCost, batteryCostPerKWh, generatorCostPerKW, fuelCostPerKWhGen, systemLifespan, discountRate } = inputs;

    // Input Validation and Defaults
    const parsedInputs = {
        dailyAvgEssentialEnergy: Math.max(0, dailyAvgEssentialEnergy || 0),
        peakEssentialLoad: Math.max(0.1, peakEssentialLoad || 0.1), // Ensure at least 0.1kW for calculations
        avgOutageDuration: Math.max(0, avgOutageDuration || 0),
        avgOutageFrequency: Math.max(0, avgOutageFrequency || 0),
        gridElectricityCost: Math.max(0, gridElectricityCost || 0.1),
        batteryCostPerKWh: Math.max(0, batteryCostPerKWh || 750),
        generatorCostPerKW: Math.max(0, generatorCostPerKW || 500),
        fuelCostPerKWhGen: Math.max(0, fuelCostPerKWhGen || 0.2),
        systemLifespan: Math.max(1, systemLifespan || 15),
        discountRate: Math.max(0, Math.min(100, discountRate || 5))
    };

    const discountRateDecimal = parsedInputs.discountRate / 100;

    // 1. System Sizing
    // Battery sized for 1.5 days of essential energy autonomy
    const optimalBatteryCapacity = parsedInputs.dailyAvgEssentialEnergy * 1.5;
    // Generator sized for peak essential load with a 20% buffer for starting surges, capped at 25kW for residential
    const optimalGeneratorPower = Math.min(25, parsedInputs.peakEssentialLoad * 1.2);

    // Edge case: if no essential energy, no battery needed. If no peak load, no generator needed (or very small for trickle). Minimal values set to avoid zero sizing. 
    const actualOptimalBatteryCapacity = optimalBatteryCapacity > 0.5 ? optimalBatteryCapacity : 0; // Don't recommend tiny batteries
    const actualOptimalGeneratorPower = optimalGeneratorPower > 0.5 ? optimalGeneratorPower : 0; // Don't recommend tiny generators

    // 2. Initial Investment
    const batterySystemCost = actualOptimalBatteryCapacity * parsedInputs.batteryCostPerKWh;
    const generatorSystemCost = actualOptimalGeneratorPower * parsedInputs.generatorCostPerKW;
    const totalInitialInvestment = batterySystemCost + generatorSystemCost;

    // 3. Annual Savings & Costs
    const totalAnnualOutageHours = parsedInputs.avgOutageDuration * parsedInputs.avgOutageFrequency;

    let annualOutageSavings = 0;
    let annualFuelCost = 0;
    let annualMaintenanceCost = 0;

    if (totalAnnualOutageHours > 0) {
        // Calculate estimated kWh needed from backup during outages
        // Assuming average daily consumption spread over outage duration. A rough estimate.
        const estimatedAnnualKWhDuringOutages = (parsedInputs.dailyAvgEssentialEnergy / 24) * totalAnnualOutageHours;

        // Monetary value of avoiding grid electricity purchase/losses during outages
        annualOutageSavings = estimatedAnnualKWhDuringOutages * parsedInputs.gridElectricityCost;

        // Fuel cost for generator. Assuming generator runs for the duration of outages requiring power.
        // If battery is present, it reduces generator runtime. Simplification: assuming generator covers the energy that battery cannot, or runs to supplement/recharge.
        // For this calculator, we'll assume the generator is the primary energy source during outages for the calculated load, or for recharging. 
        // A more complex model would allocate load between battery and generator. For simplicity, we calculate fuel cost for the total estimated energy needed during outages.
        annualFuelCost = estimatedAnnualKWhDuringOutages * parsedInputs.fuelCostPerKWhGen;

        // Annual maintenance: approx 1% of initial investment
        annualMaintenanceCost = totalInitialInvestment * 0.01;
    }

    const netAnnualBenefit = annualOutageSavings - annualFuelCost - annualMaintenanceCost;

    // 4. Financial Metrics
    let simplePaybackPeriod = Infinity;
    if (netAnnualBenefit > 0 && totalInitialInvestment > 0) {
        simplePaybackPeriod = totalInitialInvestment / netAnnualBenefit;
    }
    simplePaybackPeriod = Math.min(parsedInputs.systemLifespan, simplePaybackPeriod);

    let netPresentValue = -totalInitialInvestment;
    let totalBenefitOverLifespan = 0;

    for (let t = 1; t <= parsedInputs.systemLifespan; t++) {
        const discountedBenefit = netAnnualBenefit / Math.pow(1 + discountRateDecimal, t);
        netPresentValue += discountedBenefit;
        totalBenefitOverLifespan += discountedBenefit; // Sum of discounted benefits
    }

    let roiPercentage = 0;
    if (totalInitialInvestment > 0) {
        // ROI = (Total Benefit Over Lifespan - Initial Investment) / Initial Investment * 100
        // Using NPV which already includes initial investment as a negative cash flow.
        // So, if NPV > -Initial, means there's a positive return.
        roiPercentage = (netPresentValue / totalInitialInvestment) * 100; // This gives the ROI relative to the initial investment.
        // If total initial investment is 0, this will still be 0 as netPresentValue will be 0 too.
    } else if (netPresentValue > 0) { // If initial investment is zero, but there's a benefit
        roiPercentage = Infinity;
    }

    return {
        optimalBatteryCapacity: actualOptimalBatteryCapacity,
        optimalGeneratorPower: actualOptimalGeneratorPower,
        totalInitialInvestment: totalInitialInvestment,
        annualOutageSavings: annualOutageSavings,
        annualFuelCost: annualFuelCost,
        netAnnualBenefit: netAnnualBenefit,
        simplePaybackPeriod: simplePaybackPeriod === Infinity ? 0 : simplePaybackPeriod, // Display 0 if infinite/no benefit
        netPresentValue: netPresentValue,
        roiPercentage: roiPercentage
    };
}
