function calculateBackupPower(inputs) {
    const {
        totalEssentialWattage,
        motorLoadPercentage,
        desiredRuntimeHours,
        systemTypePreference,
        generatorFuelType,
        batteryDailyUseHours,
        batteryReserveDays,
        maxBudget
    } = inputs;

    let recommendedSystemType = 'N/A';
    let recommendedPowerOutputKw = 0;
    let recommendedCapacityKwh = 0;
    let estimatedInstallCost = 0;
    let estimatedDailyFuelConsumption = 'N/A';
    let estimatedRuntimeAchieved = 0;
    let notes = [];

    // Input validation and defaults
    if (totalEssentialWattage === null || totalEssentialWattage <= 0) {
        return { error: 'Total essential wattage must be a positive number.' };
    }
    if (motorLoadPercentage === null || motorLoadPercentage < 0 || motorLoadPercentage > 100) {
        return { error: 'Motor load percentage must be between 0 and 100.' };
    }
    if (desiredRuntimeHours === null || desiredRuntimeHours <= 0) {
        return { error: 'Desired runtime must be a positive number.' };
    }

    // --- Step 1: Calculate Peak Wattage Requirement ---
    const continuousWatts = totalEssentialWattage;
    const motorWatts = continuousWatts * (motorLoadPercentage / 100);
    const nonMotorWatts = continuousWatts - motorWatts;
    const surgeFactor = 3; // Common starting surge for motors
    const peakSurgeWatts = (motorWatts * surgeFactor) + nonMotorWatts;
    let requiredOutputWatts = Math.max(continuousWatts, peakSurgeWatts);
    requiredOutputWatts *= 1.2; // 20% safety buffer for future expansion/unexpected loads
    const requiredOutputKw = requiredOutputWatts / 1000;

    // Standard generator sizes (kW, continuous output)
    const standardGenSizes = [7, 10, 12, 14, 17, 20, 22, 24, 26, 30, 35, 40, 45, 50, 60];
    const findNearestStandardGenSize = (targetKw) => {
        for (const size of standardGenSizes) {
            if (size >= targetKw) return size;
        }
        return standardGenSizes[standardGenSizes.length - 1]; // Return largest if target exceeds all
    };

    // Standard battery capacities (kWh)
    const standardBatteryKwh = [5, 7.5, 10, 12.5, 15, 20, 25, 30, 40, 50];
    const findNearestStandardBatteryKwh = (targetKwh) => {
        for (const size of standardBatteryKwh) {
            if (size >= targetKwh) return size;
        }
        return standardBatteryKwh[standardBatteryKwh.length - 1]; // Return largest if target exceeds all
    };

    let generatorOption = null;
    let batteryOption = null;

    // --- Step 2: Generator System Calculation (if applicable) ---
    if (systemTypePreference === 'generator' || systemTypePreference === 'both') {
        let genCostPerKw = 0;
        let fuelConsumptionRatePerKw = 0; // Fuel unit per hour per kW (average at ~50% load)
        let fuelUnit = 'unit/hour';

        switch (generatorFuelType) {
            case 'naturalGas':
                genCostPerKw = 400; // $/kW for standby unit only
                fuelConsumptionRatePerKw = 20; // ~20 CFH per kW (e.g., 200 CFH for 10kW at half load)
                fuelUnit = 'cubic feet';
                break;
            case 'propane':
                genCostPerKw = 450; 
                fuelConsumptionRatePerKw = 0.15; // ~0.15 GPH per kW (e.g., 1.5 GPH for 10kW at half load)
                fuelUnit = 'gallons';
                break;
            case 'diesel':
                genCostPerKw = 550; 
                fuelConsumptionRatePerKw = 0.08; // ~0.08 GPH per kW (e.g., 0.8 GPH for 10kW at half load)
                fuelUnit = 'gallons';
                break;
            case 'gasoline': 
            default:
                genCostPerKw = 250; 
                fuelConsumptionRatePerKw = 0.1; // ~0.1 GPH per kW (e.g., 1 GPH for 10kW at half load)
                fuelUnit = 'gallons';
                notes.push('Gasoline generators are typically portable and may not be suitable for extended whole-home backup. Consider other fuel types for permanent solutions.');
                break;
        }

        const selectedGenKw = findNearestStandardGenSize(requiredOutputKw);
        const genSystemCost = (selectedGenKw * genCostPerKw) + 7000; // Base installation cost approx $7000
        const dailyFuelConsumption = (selectedGenKw * fuelConsumptionRatePerKw * desiredRuntimeHours);

        generatorOption = {
            type: `Standby Generator (${generatorFuelType ? generatorFuelType.replace(/([A-Z])/g, ' $1').toLowerCase() : 'Unspecified'})`,
            powerOutputKw: selectedGenKw,
            capacityKwh: 0,
            cost: genSystemCost,
            fuelConsumption: `${dailyFuelConsumption.toFixed(1)} ${fuelUnit}/day (${fuelUnit}/hour at ${selectedGenKw.toFixed(1)} kW output for ${desiredRuntimeHours} hours)`,
            runtime: desiredRuntimeHours
        };

        if (systemTypePreference === 'generator') {
            recommendedSystemType = generatorOption.type;
            recommendedPowerOutputKw = generatorOption.powerOutputKw;
            estimatedInstallCost = generatorOption.cost;
            estimatedDailyFuelConsumption = generatorOption.fuelConsumption;
            estimatedRuntimeAchieved = generatorOption.runtime;
        }
    }

    // --- Step 3: Battery System Calculation (if applicable) ---
    if (systemTypePreference === 'battery' || systemTypePreference === 'both') {
        let effectiveBatteryDailyUseHours = batteryDailyUseHours || 8; 
        if (!batteryDailyUseHours) notes.push('Battery daily usage hours not provided. Assuming 8 hours for calculation.');

        let effectiveBatteryReserveDays = batteryReserveDays || 1;
        if (!batteryReserveDays) notes.push('Battery reserve days not provided. Assuming 1 day for calculation.');

        const dailyEnergyKwh = (totalEssentialWattage * effectiveBatteryDailyUseHours) / 1000;
        let totalRequiredKwh = dailyEnergyKwh * effectiveBatteryReserveDays;

        const inverterEfficiency = 0.90; // 90% inverter efficiency
        totalRequiredKwh /= inverterEfficiency;

        const batteryDoD = 0.85; // 85% usable DoD for LiFePO4 batteries
        totalRequiredKwh /= batteryDoD;

        const selectedBatteryKwh = findNearestStandardBatteryKwh(totalRequiredKwh);
        // Inverter size needs to match peak power output, not necessarily continuous.
        const inverterKw = findNearestStandardGenSize(requiredOutputKw); 

        const batteryCostPerKwh = 1000; // $/kWh for LiFePO4 batteries
        const inverterCostPerKw = 500; // $/kW for hybrid inverter
        const batterySystemCost = (selectedBatteryKwh * batteryCostPerKwh) + (inverterKw * inverterCostPerKw) + 6000; // Base installation cost approx $6000

        batteryOption = {
            type: 'Battery Backup System',
            powerOutputKw: inverterKw, // Inverter output
            capacityKwh: selectedBatteryKwh,
            cost: batterySystemCost,
            fuelConsumption: `Requires ${dailyEnergyKwh.toFixed(1)} kWh/day charging to achieve ${effectiveBatteryReserveDays} days autonomy.`,
            runtime: desiredRuntimeHours 
        };

        if (systemTypePreference === 'battery') {
            recommendedSystemType = batteryOption.type;
            recommendedPowerOutputKw = batteryOption.powerOutputKw;
            recommendedCapacityKwh = batteryOption.capacityKwh;
            estimatedInstallCost = batteryOption.cost;
            estimatedDailyFuelConsumption = batteryOption.fuelConsumption;
            estimatedRuntimeAchieved = batteryOption.runtime;
        }
    }

    // --- Step 4: Compare and Select Best Option (if 'both') ---
    if (systemTypePreference === 'both') {
        let bestOption = null;
        const currentBudget = maxBudget === null ? Infinity : maxBudget;

        if (generatorOption && generatorOption.cost <= currentBudget) {
            bestOption = generatorOption;
        } else if (generatorOption) {
             notes.push(`Generator option (${generatorOption.cost.toLocaleString('en-US', { style: 'currency', currency: 'USD' })}) exceeds your budget.`);
        }

        if (batteryOption && batteryOption.cost <= currentBudget) {
            if (!bestOption || batteryOption.cost < bestOption.cost) {
                bestOption = batteryOption;
            }
        } else if (batteryOption) {
            notes.push(`Battery option (${batteryOption.cost.toLocaleString('en-US', { style: 'currency', currency: 'USD' })}) exceeds your budget.`);
        }

        if (bestOption) {
            recommendedSystemType = bestOption.type;
            recommendedPowerOutputKw = bestOption.powerOutputKw;
            recommendedCapacityKwh = bestOption.capacityKwh;
            estimatedInstallCost = bestOption.cost;
            estimatedDailyFuelConsumption = bestOption.fuelConsumption;
            estimatedRuntimeAchieved = bestOption.runtime;
        } else if (generatorOption && batteryOption) {
            // Both exceed budget, just choose the cheaper of the two to present as an option
            if (generatorOption.cost < batteryOption.cost) {
                bestOption = generatorOption;
                notes.push('Both options exceed budget. Presenting the less expensive generator option.');
            } else {
                bestOption = batteryOption;
                notes.push('Both options exceed budget. Presenting the less expensive battery option.');
            }
            recommendedSystemType = bestOption.type;
            recommendedPowerOutputKw = bestOption.powerOutputKw;
            recommendedCapacityKwh = bestOption.capacityKwh;
            estimatedInstallCost = bestOption.cost;
            estimatedDailyFuelConsumption = bestOption.fuelConsumption;
            estimatedRuntimeAchieved = bestOption.runtime;
        } else if (generatorOption) {
            notes.push('Battery option could not be calculated (missing inputs). Generator option (exceeds budget) presented.');
            recommendedSystemType = generatorOption.type;
            recommendedPowerOutputKw = generatorOption.powerOutputKw;
            estimatedInstallCost = generatorOption.cost;
            estimatedDailyFuelConsumption = generatorOption.fuelConsumption;
            estimatedRuntimeAchieved = generatorOption.runtime;
        } else if (batteryOption) {
            notes.push('Generator option could not be calculated (missing inputs). Battery option (exceeds budget) presented.');
            recommendedSystemType = batteryOption.type;
            recommendedPowerOutputKw = batteryOption.powerOutputKw;
            recommendedCapacityKwh = batteryOption.capacityKwh;
            estimatedInstallCost = batteryOption.cost;
            estimatedDailyFuelConsumption = batteryOption.fuelConsumption;
            estimatedRuntimeAchieved = batteryOption.runtime;
        } else {
            notes.push('No suitable system could be calculated based on provided preferences and inputs.');
            recommendedSystemType = 'N/A';
            estimatedInstallCost = 0;
        }
    }

    // --- Step 5: Budget Constraint Handling (Final Check) ---
    if (maxBudget !== null && estimatedInstallCost > maxBudget && recommendedSystemType !== 'N/A') {
        notes.unshift('The recommended system exceeds your maximum budget. Consider reducing essential loads, desired runtime, increasing budget, or exploring smaller, portable options.');
    }

    return {
        recommendedSystemType,
        recommendedPowerOutputKw: parseFloat(recommendedPowerOutputKw.toFixed(1)),
        recommendedCapacityKwh: parseFloat(recommendedCapacityKwh.toFixed(1)),
        estimatedInstallCost: Math.round(estimatedInstallCost),
        estimatedDailyFuelConsumption,
        estimatedRuntimeAchieved: parseFloat(estimatedRuntimeAchieved.toFixed(1)),
        notes: notes.join(' ')
    };
}
