function calculateEssentialNeeds(inputs) {
  const { blackoutDurationHours, numOccupants, refrigeratorType, freezerPresent, medicalDevicesEssential, heatingCoolingPriority, heatingCoolingType, wellPumpPresent, internetRouterModemEssential, lightingEssential } = inputs;

  // Input validation and defaults
  const duration = Math.max(1, Math.min(720, parseFloat(blackoutDurationHours) || 24));
  const occupants = Math.max(1, Math.min(10, parseInt(numOccupants) || 1));

  // Appliance constants (Running Watts, Starting Watts, Daily Hours of Use)
  const applianceData = {
    refrigerator: {
      none: { running: 0, starting: 0, dailyHours: 0 },
      standard: { running: 100, starting: 500, dailyHours: 8 },
      large: { running: 150, starting: 750, dailyHours: 8 },
      sideBySide: { running: 200, starting: 1000, dailyHours: 8 }
    },
    freezer: { running: 75, starting: 400, dailyHours: 6 },
    medicalDevices: { running: 250, starting: 250, dailyHours: 24 }, // Assumed average for critical devices like CPAP + small concentrator
    gasFurnaceBlower: { running: 500, starting: 1500, dailyHours: 8 }, // Assumes moderate winter use
    electricFurnace: { running: 10000, starting: 10000, dailyHours: 8 }, // High load, often requires large generators
    centralAC: { running: 3500, starting: 10000, dailyHours: 10 }, // High load
    windowAC: { running: 1000, starting: 2500, dailyHours: 10 },
    wellPump: { running: 1000, starting: 3000, dailyHours: 2.5 }, // Assumes average family use
    internetRouterModem: { running: 30, starting: 30, dailyHours: 24 },
    lighting: { running: 100, starting: 100, dailyHours: 6 }, // A few LED bulbs
    generalElectronics: { running: 50, starting: 50, dailyHours: 8 } // Per occupant for charging phones/laptops
  };

  let currentRunningWatts = 0; // Sum of continuous running loads that might be on simultaneously
  let peakStartingWatts = 0;   // Largest single motor starting load
  let totalDailyKWh = 0;

  // 1. General Electronics (for each occupant)
  currentRunningWatts += applianceData.generalElectronics.running * occupants;
  totalDailyKWh += (applianceData.generalElectronics.running * occupants * applianceData.generalElectronics.dailyHours) / 1000;

  // 2. Refrigerator
  if (refrigeratorType !== 'none') {
    const fridgeData = applianceData.refrigerator[refrigeratorType];
    currentRunningWatts += fridgeData.running; // Assuming it could be running
    if (fridgeData.starting > peakStartingWatts) peakStartingWatts = fridgeData.starting;
    totalDailyKWh += (fridgeData.running * fridgeData.dailyHours) / 1000;
  }

  // 3. Freezer
  if (freezerPresent) {
    currentRunningWatts += applianceData.freezer.running;
    if (applianceData.freezer.starting > peakStartingWatts) peakStartingWatts = applianceData.freezer.starting;
    totalDailyKWh += (applianceData.freezer.running * applianceData.freezer.dailyHours) / 1000;
  }

  // 4. Medical Devices
  if (medicalDevicesEssential) {
    currentRunningWatts += applianceData.medicalDevices.running;
    totalDailyKWh += (applianceData.medicalDevices.running * applianceData.medicalDevices.dailyHours) / 1000;
    // Medical devices often don't have large starting surges, treat as continuous load for simplicity in this context.
  }

  // 5. Heating/Cooling
  if (heatingCoolingPriority && heatingCoolingType !== 'none') {
    const hvacData = applianceData[heatingCoolingType];
    if (hvacData) {
      currentRunningWatts += hvacData.running;
      if (hvacData.starting > peakStartingWatts) peakStartingWatts = hvacData.starting;
      totalDailyKWh += (hvacData.running * hvacData.dailyHours) / 1000;
    }
  }

  // 6. Well Pump
  if (wellPumpPresent) {
    currentRunningWatts += applianceData.wellPump.running;
    if (applianceData.wellPump.starting > peakStartingWatts) peakStartingWatts = applianceData.wellPump.starting;
    totalDailyKWh += (applianceData.wellPump.running * applianceData.wellPump.dailyHours) / 1000;
  }

  // 7. Internet/Router/Modem
  if (internetRouterModemEssential) {
    currentRunningWatts += applianceData.internetRouterModem.running;
    totalDailyKWh += (applianceData.internetRouterModem.running * applianceData.internetRouterModem.dailyHours) / 1000;
  }

  // 8. Essential Lighting
  if (lightingEssential) {
    currentRunningWatts += applianceData.lighting.running;
    totalDailyKWh += (applianceData.lighting.running * applianceData.lighting.dailyHours) / 1000;
  }

  // Calculate total essential running and starting watts
  // The total starting watts calculation assumes only one major motor starts at a time.
  // If multiple motors could start simultaneously (less common for residential essential loads), this would be higher.
  const totalEssentialRunningWatts = currentRunningWatts;
  const totalEssentialStartingWatts = currentRunningWatts + peakStartingWatts; // Sum of all running + largest single surge

  // Calculate total energy required over the blackout duration
  const totalEnergyRequiredKWh = (totalDailyKWh / 24) * duration * 1.15; // 15% buffer for efficiency and variable usage

  // --- Recommendations ---

  // Generator Sizing: Generators are typically rated by running watts (kW).
  // Ensure generator continuous output >= totalEssentialRunningWatts
  // And generator surge output >= totalEssentialStartingWatts
  let recommendedGeneratorSizeKW = Math.ceil(Math.max(totalEssentialRunningWatts, totalEssentialStartingWatts / 1.5) / 1000);
  // Round up to common generator sizes
  if (recommendedGeneratorSizeKW <= 2) recommendedGeneratorSizeKW = 2.0; // Smallest portable
  else if (recommendedGeneratorSizeKW <= 4) recommendedGeneratorSizeKW = 3.5;
  else if (recommendedGeneratorSizeKW <= 6) recommendedGeneratorSizeKW = 5.0;
  else if (recommendedGeneratorSizeKW <= 8) recommendedGeneratorSizeKW = 7.5;
  else if (recommendedGeneratorSizeKW <= 10) recommendedGeneratorSizeKW = 9.0;
  else if (recommendedGeneratorSizeKW <= 12) recommendedGeneratorSizeKW = 10.0;
  else if (recommendedGeneratorSizeKW <= 15) recommendedGeneratorSizeKW = 12.5;
  else recommendedGeneratorSizeKW = Math.ceil(recommendedGeneratorSizeKW / 5) * 5; // Round to nearest 5kW for larger sizes


  // Battery Sizing: Batteries are rated by kWh capacity. Inverter must handle peak watts.
  // Battery capacity should cover totalEnergyRequiredKWh, accounting for DoD and inverter efficiency
  let recommendedBatteryCapacityKWh = Math.max(1, Math.ceil(totalEnergyRequiredKWh * 1.2 / 5)) * 5; // 20% buffer, round up to nearest 5kWh block
  if (recommendedBatteryCapacityKWh < 10 && totalEnergyRequiredKWh > 1) recommendedBatteryCapacityKWh = 10; // Minimum practical size
  if (recommendedBatteryCapacityKWh > 100) recommendedBatteryCapacityKWh = 100; // Cap for extreme cases, encourages reconsideration

  // Determine solution type based on load and duration
  let solutionType = "";
  if (totalEssentialRunningWatts > 5000 && duration > 24) {
      solutionType = "Generator System";
  } else if (totalEssentialRunningWatts > 2000 && duration > 72) {
      solutionType = "Generator System";
  } else if (totalEssentialRunningWatts < 1000 && duration <= 24 && totalEnergyRequiredKWh < 10) {
      solutionType = "Portable Power Station / Small Battery Bank";
  } else {
      solutionType = "Battery Storage System or Mid-size Generator";
  }

  // Special consideration for extremely high loads like electric furnace or central AC
  if (heatingCoolingType === 'electricFurnace' || heatingCoolingType === 'centralAC') {
      solutionType = "Whole-Home Generator System or Advanced Battery/Solar Hybrid";
      if (recommendedGeneratorSizeKW < 20) recommendedGeneratorSizeKW = 20; // Min for these loads
      if (recommendedBatteryCapacityKWh < 40) recommendedBatteryCapacityKWh = 40;
  }

  // Check for unrealistic starting loads for battery systems
  // Most residential battery inverters are 5-12kW continuous, 10-24kW surge.
  if (totalEssentialStartingWatts > 20000 && solutionType.includes("Battery")) {
    solutionType = "Generator System is likely more practical due to very high surge requirements."
  }

  return {
    totalEssentialRunningWatts: totalEssentialRunningWatts,
    totalEssentialStartingWatts: totalEssentialStartingWatts,
    totalEnergyRequiredKWh: totalEnergyRequiredKWh,
    recommendedGeneratorSizeKW: recommendedGeneratorSizeKW,
    recommendedBatteryCapacityKWh: recommendedBatteryCapacityKWh,
    solutionType: solutionType
  };
}
