const { exteriorWallArea, windowDoorArea, roomVolume, designIndoorTemp, designOutdoorTemp, wallUValue, windowDoorUValue, airChangesPerHour } = inputs; 

 const deltaT = Math.max(0, designIndoorTemp - designOutdoorTemp); 

 const safeExteriorWallArea = Math.max(0, exteriorWallArea); 
 const safeWindowDoorArea = Math.max(0, windowDoorArea); 
 const safeRoomVolume = Math.max(0, roomVolume); 
 const safeAirChangesPerHour = Math.max(0, airChangesPerHour); 

 // Conduction through opaque walls: Q_wall = U_wall * A_wall * deltaT 
 const heatLossWalls = wallUValue * safeExteriorWallArea * deltaT; 

 // Conduction through windows and doors: Q_window_door = U_window_door * A_window_door * deltaT 
 const heatLossWindowsDoors = windowDoorUValue * safeWindowDoorArea * deltaT; 

 // Heat loss due to air infiltration: Q_infiltration = 0.018 * Volume * ACH * deltaT 
 // (0.018 BTU/ft³·°F is a common factor for air density * specific heat) 
 const heatLossInfiltration = 0.018 * safeRoomVolume * safeAirChangesPerHour * deltaT; 

 const totalHeatLoss = heatLossWalls + heatLossWindowsDoors + heatLossInfiltration; 

 return { 
  totalHeatLoss: totalHeatLoss, 
  heatLossWalls: heatLossWalls, 
  heatLossWindowsDoors: heatLossWindowsDoors, 
  heatLossInfiltration: heatLossInfiltration 
 };
