const initial_system_cost_usd = inputs.initial_system_cost_usd;
const solar_capacity_kW = inputs.solar_capacity_kW;
const annual_solar_production_kwh_per_kw = inputs.annual_solar_production_kwh_per_kw;
const battery_storage_capacity_kwh = inputs.battery_storage_capacity_kwh;
const average_daily_electricity_consumption_kwh = inputs.average_daily_electricity_consumption_kwh;
const current_grid_electricity_rate_usd_kwh = inputs.current_grid_electricity_rate_usd_kwh;
const annual_electricity_rate_increase_percent = inputs.annual_electricity_rate_increase_percent;
const system_lifetime_years = inputs.system_lifetime_years;
const annual_maintenance_cost_percent_of_initial = inputs.annual_maintenance_cost_percent_of_initial;
const discount_rate_percent = inputs.discount_rate_percent;

// --- Constants / Assumptions ---
const base_solar_self_consumption_rate = 0.40; // % of solar directly consumed without battery
const battery_impact_on_self_consumption = 0.40; // Additional % of solar effectively used due to battery storage, max 100%
const max_effective_offset_of_consumption = 0.95; // System typically offsets up to 95% of home consumption, grid still needed for peak/backup
const battery_degradation_replacement_interval_years = 10; // Battery might need replacement
const battery_replacement_cost_percent_of_initial = 0.30; // Cost of battery replacement relative to initial *total* system cost (simplification)
const days_in_year = 365;

// Edge case: if initial_system_cost_usd is 0 or negative, return early to prevent division by zero or illogical results.
if (initial_system_cost_usd <= 0) {
    return {
        payback_period_years: 0,
        net_present_value_usd: 0,
        total_savings_over_lifetime_usd: 0,
        roi_percentage_over_lifetime: 0
    };
}

// --- Calculations ---

// 1. Annual Electricity Consumption & Generation
const annual_consumption_kwh = average_daily_electricity_consumption_kwh * days_in_year;
const annual_solar_generation_kwh = solar_capacity_kW * annual_solar_production_kwh_per_kw;

// 2. Effective Annual Offset from Grid
let effective_solar_utilization_rate = base_solar_self_consumption_rate;
if (battery_storage_capacity_kwh > 0) {
    effective_solar_utilization_rate = Math.min(base_solar_self_consumption_rate + battery_impact_on_self_consumption, 1.0);
}
const potential_electricity_offset_kwh = annual_solar_generation_kwh * effective_solar_utilization_rate;
const actual_electricity_offset_kwh = Math.min(potential_electricity_offset_kwh, annual_consumption_kwh * max_effective_offset_of_consumption);

// 3. Financial Projections over System Lifetime
let cumulative_undiscounted_net_benefits = 0; // Sum of annual_net_benefit for ROI and total savings
let cumulative_cash_flow_for_payback = -initial_system_cost_usd; // Tracks cash flow for payback period
let payback_period_years = null; // Will store the year when cumulative_cash_flow_for_payback becomes non-negative

let npv = -initial_system_cost_usd; // Initial investment for NPV
const discount_factor = 1 + (discount_rate_percent / 100); // For discounting future cash flows

let current_grid_rate = current_grid_electricity_rate_usd_kwh;

for (let year = 1; year <= system_lifetime_years; year++) {
    // Adjust grid rate for assumed annual increase
    current_grid_rate *= (1 + (annual_electricity_rate_increase_percent / 100));

    // Annual savings from avoided grid purchases
    const annual_avoided_cost_usd = actual_electricity_offset_kwh * current_grid_rate;

    // Annual Operations & Maintenance (O&M) cost
    const annual_om_cost_usd = initial_system_cost_usd * (annual_maintenance_cost_percent_of_initial / 100);

    // Battery replacement cost (if applicable and within system lifetime)
    let battery_replacement_cost_year = 0;
    if (battery_storage_capacity_kwh > 0 && battery_degradation_replacement_interval_years > 0 && year % battery_degradation_replacement_interval_years === 0 && year < system_lifetime_years) {
        // This is a simplification, assuming battery cost is a percentage of total initial system cost.
        // A more complex model would require a separate battery initial cost input.
        battery_replacement_cost_year = initial_system_cost_usd * battery_replacement_cost_percent_of_initial;
    }

    const annual_net_benefit = annual_avoided_cost_usd - annual_om_cost_usd - battery_replacement_cost_year;

    // Update cumulative benefits and cash flow for payback calculation
    cumulative_undiscounted_net_benefits += annual_net_benefit;
    cumulative_cash_flow_for_payback += annual_net_benefit;

    // Check for payback period
    if (payback_period_years === null && cumulative_cash_flow_for_payback >= 0) {
        payback_period_years = year;
    }

    // Update Net Present Value (NPV)
    npv += annual_net_benefit / Math.pow(discount_factor, year);
}

// 4. Final Outputs
const total_savings_over_lifetime_usd = cumulative_undiscounted_net_benefits;

// ROI Percentage: (Total Net Benefits / Initial Investment) * 100
let roi_percentage_over_lifetime = (total_savings_over_lifetime_usd / initial_system_cost_usd) * 100;

// Handle edge cases for display of payback period
if (payback_period_years === null && total_savings_over_lifetime_usd < 0) {
    payback_period_years = null; // Indicates payback is never achieved due to negative overall benefits
} else if (payback_period_years === null && total_savings_over_lifetime_usd >= 0 && system_lifetime_years > 0) {
    // If total savings are positive but payback was not reached within lifetime, it implies it would be just beyond.
    // We set it to null for clarity, or can be system_lifetime_years + X for indication.
    payback_period_years = null; 
}

return {
    payback_period_years: payback_period_years,
    net_present_value_usd: npv,
    total_savings_over_lifetime_usd: total_savings_over_lifetime_usd,
    roi_percentage_over_lifetime: roi_percentage_over_lifetime,
};
