formula:
const { roofArea, annualRainfall, runoffCoefficient, systemEfficiency, storageCapacity, dailyWaterNeed } = inputs;

// Conversion factor: 1 sq ft * 1 inch of rain = 0.623 gallons
// (1 sq ft * 1/12 ft) * 7.48 gallons/cu ft = 0.623 gallons (approximately)
const GALLONS_PER_SQFT_INCH = 0.623;

// Assumed maximum number of times the storage tank can be effectively refilled and utilized in a year.
// This is a simplification to account for distributed rainfall events and intermittent usage throughout the wet season.
// A higher number suggests frequent rain events and/or quick consumption, allowing for more refill cycles.
const MAX_TANK_REFILLS_PER_YEAR = 15;

// 1. Calculate the potential volume of rainwater that can be collected.
// This is the theoretical maximum without considering specific storage limitations or usage patterns.
const potentialAnnualHarvest = roofArea * annualRainfall * GALLONS_PER_SQFT_INCH *
                               runoffCoefficient * (systemEfficiency / 100);

// 2. Calculate the effective annual harvest, factoring in storage capacity.
// This accounts for the reality that a tank can only hold so much at any given time,
// but can be refilled multiple times throughout the year depending on rainfall distribution and water usage.
const maxStorageContributionAnnually = storageCapacity * MAX_TANK_REFILLS_PER_YEAR;
const effectiveAnnualHarvest = Math.min(potentialAnnualHarvest, maxStorageContributionAnnually);

// 3. Estimate the number of days of non-potable water supply.
// This is conditional, only calculated if a daily water need is provided and is a positive value.
const daysWaterSupply = (dailyWaterNeed != null && dailyWaterNeed > 0)
                        ? (effectiveAnnualHarvest / dailyWaterNeed)
                        : 0; // Return 0 if dailyWaterNeed is not provided or invalid

return {
    potentialAnnualHarvest: potentialAnnualHarvest,
    effectiveAnnualHarvest: effectiveAnnualHarvest,
    daysWaterSupply: daysWaterSupply
};
