function calculate() {
    const systemCost = parseFloat(inputs.systemCost);
    const annualEnergyProduction = parseFloat(inputs.annualEnergyProduction);
    const currentElectricityRate = parseFloat(inputs.currentElectricityRate);
    const annualElectricityRateIncrease = parseFloat(inputs.annualElectricityRateIncrease);
    const federalTaxCreditPercent = parseFloat(inputs.federalTaxCreditPercent);
    const stateLocalRebates = parseFloat(inputs.stateLocalRebates);
    const annualMaintenanceCost = parseFloat(inputs.annualMaintenanceCost);
    const systemLifespan = parseInt(inputs.systemLifespan);
    const systemDegradationRate = parseFloat(inputs.systemDegradationRate);

    // Input validation and defaults
    if (isNaN(systemCost) || systemCost < 0) return { error: 'Invalid System Cost' };
    if (isNaN(annualEnergyProduction) || annualEnergyProduction < 0) return { error: 'Invalid Annual Energy Production' };
    if (isNaN(currentElectricityRate) || currentElectricityRate < 0) return { error: 'Invalid Electricity Rate' };
    if (isNaN(annualElectricityRateIncrease) || annualElectricityRateIncrease < -50 || annualElectricityRateIncrease > 50) return { error: 'Invalid Electricity Rate Increase' }; // Reasonable bounds
    if (isNaN(federalTaxCreditPercent) || federalTaxCreditPercent < 0 || federalTaxCreditPercent > 100) return { error: 'Invalid Federal Tax Credit' };
    if (isNaN(stateLocalRebates) || stateLocalRebates < 0) return { error: 'Invalid State/Local Rebates' };
    if (isNaN(annualMaintenanceCost) || annualMaintenanceCost < 0) return { error: 'Invalid Annual Maintenance Cost' };
    if (isNaN(systemLifespan) || systemLifespan < 1 || systemLifespan > 100) return { error: 'Invalid System Lifespan' }; // Reasonable bounds
    if (isNaN(systemDegradationRate) || systemDegradationRate < 0 || systemDegradationRate > 10) return { error: 'Invalid Degradation Rate' }; // Reasonable bounds

    // 1. Calculate Net Initial Cost
    let netInitialCost = systemCost * (1 - federalTaxCreditPercent / 100) - stateLocalRebates;
    netInitialCost = Math.max(0, netInitialCost); // Initial cost cannot be negative

    let cumulativeCashFlow = -netInitialCost;
    let currentElectricityPricePerKWH = currentElectricityRate;
    let totalSavingsOverLifespan = 0;
    let paybackPeriod = null;
    let effectiveAnnualProduction = annualEnergyProduction;

    // Handle immediate payback if net initial cost is 0 or negative and there are savings
    if (netInitialCost <= 0) {
        // Calculate savings for the first year to ensure a positive flow
        let initialEnergySavings = annualEnergyProduction * currentElectricityRate;
        let initialMaintenanceCost = annualMaintenanceCost;
        let firstYearNetSavings = initialEnergySavings - initialMaintenanceCost;
        if (firstYearNetSavings > 0) {
            paybackPeriod = 0; // Immediate payback if no initial investment and positive cash flow
            totalSavingsOverLifespan = firstYearNetSavings * systemLifespan; // Simplified for this edge case
        } else {
            paybackPeriod = 'No Payback'; // Even if no cost, if it loses money, no payback
            totalSavingsOverLifespan = firstYearNetSavings * systemLifespan; // Will be negative
        }

        // Calculate ROI for netInitialCost <= 0
        // If no cost and positive savings, ROI is effectively infinite, but for reporting, use a large number or specific string
        let roiPercentage = (netInitialCost <= 0 && totalSavingsOverLifespan > 0) ? Infinity : (netInitialCost <= 0 && totalSavingsOverLifespan <= 0) ? 0 : 0;

        return {
            netInitialCost: netInitialCost,
            paybackPeriodYears: paybackPeriod,
            totalSavingsOverLifespan: totalSavingsOverLifespan,
            roiPercentage: roiPercentage
        };
    }

    for (let year = 1; year <= systemLifespan; year++) {
        // Apply degradation to production
        effectiveAnnualProduction = annualEnergyProduction * Math.pow(1 - (systemDegradationRate / 100), year - 1);
        if (effectiveAnnualProduction < 0) effectiveAnnualProduction = 0; // Production cannot go negative

        // Energy Savings Calculation
        let energySavingsThisYear = effectiveAnnualProduction * currentElectricityPricePerKWH;

        // Annual Maintenance Cost
        let maintenanceCostThisYear = annualMaintenanceCost;

        // Net Annual Cash Flow
        let annualCashFlow = energySavingsThisYear - maintenanceCostThisYear;
        cumulativeCashFlow += annualCashFlow;
        totalSavingsOverLifespan += annualCashFlow; // This accumulates the net cash flow from operations

        // Update electricity price for next year
        currentElectricityPricePerKWH *= (1 + annualElectricityRateIncrease / 100);

        // Check for payback period
        if (paybackPeriod === null && cumulativeCashFlow >= 0) {
            // Linear interpolation for more accurate payback period
            let previousCumulativeCashFlow = cumulativeCashFlow - annualCashFlow; // Cumulative before this year's positive flow
            if (annualCashFlow > 0) {
                 paybackPeriod = year - 1 + (Math.abs(previousCumulativeCashFlow) / annualCashFlow);
            } else {
                 paybackPeriod = year; // If annual cash flow is zero or negative, but cumulative turned positive, mark end of year
            }
        }
    }

    // Calculate ROI
    let roiPercentage = (netInitialCost > 0) ? (totalSavingsOverLifespan / netInitialCost) * 100 : (totalSavingsOverLifespan > 0 ? Infinity : 0);
    if (!isFinite(roiPercentage) && totalSavingsOverLifespan > 0) roiPercentage = 999999; // Represent Infinity as a large number for display if needed

    // Handle cases where payback period is not reached
    if (paybackPeriod === null) {
        paybackPeriod = (totalSavingsOverLifespan >= 0) ? 'No Payback within Lifespan' : 'No Payback';
    }

    // Cap total savings if it goes negative (lost more than initial investment + future losses)
    // This ensures consistency with ROI where total_loss / initial_investment can't exceed -100%
    if (totalSavingsOverLifespan < -netInitialCost && netInitialCost > 0) {
        // totalSavingsOverLifespan = -netInitialCost; // Not strictly needed, ROI handles it
    }

    // Cap ROI for negative values to -100% if the system results in a net loss greater than initial investment
    if (roiPercentage < -100) roiPercentage = -100; // Cannot lose more than 100% of your investment relative to initial investment

    return {
        netInitialCost: netInitialCost,
        paybackPeriodYears: typeof paybackPeriod === 'number' ? paybackPeriod : paybackPeriod,
        totalSavingsOverLifespan: totalSavingsOverLifespan,
        roiPercentage: roiPercentage
    };
}
