function calculate() {
  const snowDepthInches = parseFloat(inputs.snowDepthInches);
  const snowDensityLbsPerCuFt = parseFloat(inputs.snowDensityLbsPerCuFt);
  const roofPitchRise = parseFloat(inputs.roofPitchRise);
  const roofPitchRun = parseFloat(inputs.roofPitchRun);
  const buildingCodeLimitPSF = parseFloat(inputs.buildingCodeLimitPSF);

  if (isNaN(snowDepthInches) || isNaN(snowDensityLbsPerCuFt) || isNaN(roofPitchRise) || isNaN(roofPitchRun) || isNaN(buildingCodeLimitPSF) || snowDepthInches < 0 || snowDensityLbsPerCuFt <= 0 || roofPitchRise < 0 || roofPitchRun <= 0 || buildingCodeLimitPSF <= 0) {
    return {
      calculatedSnowLoadPSF: 'N/A',
      roofPitchAngleDegrees: 'N/A',
      riskLevel: 'Invalid input'
    };
  }

  // Convert snow depth from inches to feet
  const snowDepthFeet = snowDepthInches / 12;

  // Calculate raw ground snow load (before roof pitch adjustment)
  const rawGroundSnowLoadPSF = snowDepthFeet * snowDensityLbsPerCuFt;

  // Calculate roof pitch angle in degrees
  const roofPitchAngleRadians = Math.atan(roofPitchRise / roofPitchRun);
  const roofPitchAngleDegrees = roofPitchAngleRadians * (180 / Math.PI);

  // Calculate roof pitch factor (Cp) based on ASCE 7-05 / IBC general approach
  // Cp = 1.0 for angles <= 30 degrees
  // Cp = 0.0 for angles >= 70 degrees
  // Linear interpolation for angles between 30 and 70 degrees
  let roofPitchFactorCp;
  if (roofPitchAngleDegrees <= 30) {
    roofPitchFactorCp = 1.0;
  } else if (roofPitchAngleDegrees >= 70) {
    roofPitchFactorCp = 0.0;
  } else {
    roofPitchFactorCp = 1 - (roofPitchAngleDegrees - 30) / 40;
  }

  // Ensure Cp is within valid range [0, 1]
  roofPitchFactorCp = Math.max(0, Math.min(1, roofPitchFactorCp));

  // Calculate the adjusted snow load on the roof surface
  const calculatedSnowLoadPSF = rawGroundSnowLoadPSF * roofPitchFactorCp;

  // Determine risk level
  let riskLevel;
  if (calculatedSnowLoadPSF > buildingCodeLimitPSF * 1.2) {
    riskLevel = 'Critical - Significantly exceeds code limits! Immediate action advised.';
  } else if (calculatedSnowLoadPSF > buildingCodeLimitPSF) {
    riskLevel = 'High - Exceeds local building code limits. Structural risk is elevated.';
  } else if (calculatedSnowLoadPSF > buildingCodeLimitPSF * 0.8) {
    riskLevel = 'Moderate - Approaching local building code limits. Monitor closely.';
  } else {
    riskLevel = 'Low - Well within local building code limits.';
  }

  return {
    calculatedSnowLoadPSF: calculatedSnowLoadPSF,
    roofPitchAngleDegrees: roofPitchAngleDegrees,
    riskLevel: riskLevel
  };
}
