const essentialDailyLoad = inputs.essentialDailyLoad;
const desiredAutonomyDays = inputs.desiredAutonomyDays;
const peakSunHours = inputs.peakSunHours;
const systemEfficiency = inputs.systemEfficiency / 100;
const batteryDoD = inputs.batteryDoD / 100;
const solarPanelDerating = inputs.solarPanelDerating / 100;
const solarPanelWattage = inputs.solarPanelWattage;

// Input validation and sensible defaults/caps to prevent errors and ensure realistic outputs
const safePeakSunHours = Math.max(0.5, peakSunHours); // Minimum peak sun hours
const safeSystemEfficiency = Math.max(0.6, Math.min(0.95, systemEfficiency)); // Cap between 60-95%
const safeBatteryDoD = Math.max(0.5, Math.min(0.95, batteryDoD)); // Cap between 50-95% for longevity
const safeSolarPanelDerating = Math.max(0.6, Math.min(0.9, solarPanelDerating)); // Cap between 60-90%
const safeEssentialDailyLoad = Math.max(500, essentialDailyLoad); // Minimum essential load for practical sizing
const safeSolarPanelWattage = Math.max(100, solarPanelWattage); // Minimum panel wattage

// 1. Calculate Optimal Battery Storage Capacity (kWh)
// Energy required from battery during outage (Wh)
const requiredBatteryEnergy_Wh = safeEssentialDailyLoad * desiredAutonomyDays;
// Gross battery capacity needed, considering Depth of Discharge (Wh)
const grossBatteryCapacity_Wh = requiredBatteryEnergy_Wh / safeBatteryDoD;
// Convert to kWh
const optimalBatteryCapacity_kWh = grossBatteryCapacity_Wh / 1000;

// 2. Calculate Optimal Solar Panel Array Size (kW)
// The array must be sized to meet the essential daily load on an average sunny day,
// accounting for system losses and panel derating. This ensures the battery can be recharged
// and daily loads met during an extended outage.
const dailyEnergyProductionRequired_Wh = safeEssentialDailyLoad; // This is the net energy required from solar

// The formula calculates the AC power output (kW) needed from the array to meet the daily energy requirement.
// We divide the daily energy required by (peak sun hours * derating * system efficiency)
// to get the nominal DC power (Wp) of the array, then convert to kW.
const optimalSolarArraySize_kW = (dailyEnergyProductionRequired_Wh / (safePeakSunHours * safeSolarPanelDerating * safeSystemEfficiency)) / 1000;

// 3. Calculate Number of Solar Panels
// Convert total array kW to total Watts, then divide by individual panel wattage.
const numSolarPanels = Math.ceil((optimalSolarArraySize_kW * 1000) / safeSolarPanelWattage);

return {
    optimalSolarArraySize: optimalSolarArraySize_kW,
    optimalBatteryCapacity: optimalBatteryCapacity_kWh,
    numSolarPanels: numSolarPanels
};
