const systemCost = inputs.system_cost;
const systemSize = inputs.system_size_kw;
const annualProductionPerKw = inputs.annual_production_kwh_per_kw;
let currentElectricityPrice = inputs.current_electricity_price;
const electricityInflationRate = inputs.electricity_inflation_rate / 100;
const federalTaxCreditPercent = inputs.federal_tax_credit_percent / 100;
const stateLocalIncentive = inputs.state_local_incentive_amount;
const feedInTariffRate = inputs.annual_feed_in_tariff_kwh_price;
const avgAnnualConsumption = inputs.average_annual_electricity_consumption_kwh;
const degradationRate = inputs.system_degradation_rate / 100;

// --- Step 1: Calculate Initial Net Cost --- Start
let netInitialCost = systemCost;
const federalTaxCreditAmount = Math.min(systemCost * federalTaxCreditPercent, systemCost);
netInitialCost -= federalTaxCreditAmount;
netInitialCost -= stateLocalIncentive;

// Handle scenarios where incentives fully cover or exceed the system cost
if (netInitialCost <= 0) {
    const totalAnnualProductionYear1 = systemSize * annualProductionPerKw;
    const effectiveElectricityPriceYear1 = currentElectricityPrice;
    const selfConsumptionKwhYear1 = Math.min(totalAnnualProductionYear1, avgAnnualConsumption);
    const excessProductionKwhYear1 = Math.max(0, totalAnnualProductionYear1 - avgAnnualConsumption);
    const savingsFromSelfConsumptionYear1 = selfConsumptionKwhYear1 * effectiveElectricityPriceYear1;
    const feedInTariffIncomeYear1 = excessProductionKwhYear1 * feedInTariffRate;
    const annualBenefitYear1 = savingsFromSelfConsumptionYear1 + feedInTariffIncomeYear1;

    let cumulativeBenefitsFor20YearsIfZeroCost = 0;
    let currentAnnualProductionForROI = totalAnnualProductionYear1;
    for (let i = 1; i <= 20; i++) {
        const effectiveElectricityPrice = currentElectricityPrice * Math.pow(1 + electricityInflationRate, i - 1);
        const selfConsumptionKwh = Math.min(currentAnnualProductionForROI, avgAnnualConsumption);
        const excessProductionKwh = Math.max(0, currentAnnualProductionForROI - avgAnnualConsumption);
        const savingsFromSelfConsumption = selfConsumptionKwh * effectiveElectricityPrice;
        const feedInTariffIncome = excessProductionKwh * feedInTariffRate;
        cumulativeBenefitsFor20YearsIfZeroCost += (savingsFromSelfConsumption + feedInTariffIncome);
        currentAnnualProductionForROI *= (1 - degradationRate);
    }

    return {
        payback_period_years: 0,
        total_savings_at_payback: systemCost - netInitialCost,
        annual_net_savings_year1: annualBenefitYear1,
        roi_after_20_years: 99999.0
    };
}
// --- Step 1: Calculate Initial Net Cost --- End

// --- Step 2: Calculate Annual Production for Year 1 --- Start
const totalAnnualProductionYear1 = systemSize * annualProductionPerKw;
// --- Step 2: Calculate Annual Production for Year 1 --- End

// --- Step 3: Iterate year by year to find payback --- Start
let cumulativeSavings = 0; 
let paybackPeriodYears = 0;
const maxIterations = 50;
let currentAnnualProduction = totalAnnualProductionYear1;
let annualNetSavingsYear1 = 0; 
let totalSavingsAtPayback = 0; 

for (let year = 1; year <= maxIterations; year++) {
    const effectiveElectricityPrice = currentElectricityPrice * Math.pow(1 + electricityInflationRate, year - 1);
    const selfConsumptionKwh = Math.min(currentAnnualProduction, avgAnnualConsumption);
    const excessProductionKwh = Math.max(0, currentAnnualProduction - avgAnnualConsumption);
    const savingsFromSelfConsumption = selfConsumptionKwh * effectiveElectricityPrice;
    const feedInTariffIncome = excessProductionKwh * feedInTariffRate;
    const annualBenefit = savingsFromSelfConsumption + feedInTariffIncome;

    cumulativeSavings += annualBenefit;

    if (year === 1) {
        annualNetSavingsYear1 = annualBenefit;
    }

    if (cumulativeSavings >= netInitialCost && paybackPeriodYears === 0) {
        paybackPeriodYears = year;
        totalSavingsAtPayback = cumulativeSavings;
    }

    currentAnnualProduction *= (1 - degradationRate);
}

if (paybackPeriodYears === 0) {
    paybackPeriodYears = maxIterations;
    totalSavingsAtPayback = 0;
}
// --- Step 3: Iterate year by year to find payback --- End

// --- Step 4: Calculate ROI after 20 years --- Start
let cumulativeBenefits20Years = 0;
currentAnnualProduction = totalAnnualProductionYear1;
for (let i = 1; i <= 20; i++) {
    const effectiveElectricityPrice = currentElectricityPrice * Math.pow(1 + electricityInflationRate, i - 1);
    const selfConsumptionKwh = Math.min(currentAnnualProduction, avgAnnualConsumption);
    const excessProductionKwh = Math.max(0, currentAnnualProduction - avgAnnualConsumption);
    const savingsFromSelfConsumption = selfConsumptionKwh * effectiveElectricityPrice;
    const feedInTariffIncome = excessProductionKwh * feedInTariffRate;
    cumulativeBenefits20Years += (savingsFromSelfConsumption + feedInTariffIncome);
    currentAnnualProduction *= (1 - degradationRate);
}

let roiAfter20Years = 0;
if (netInitialCost > 0) {
    roiAfter20Years = ((cumulativeBenefits20Years - netInitialCost) / netInitialCost) * 100;
} else {
    roiAfter20Years = 99999.0;
}
// --- Step 4: Calculate ROI after 20 years --- End

return {
    payback_period_years: parseFloat(paybackPeriodYears.toFixed(1)),
    total_savings_at_payback: parseFloat(totalSavingsAtPayback.toFixed(0)),
    annual_net_savings_year1: parseFloat(annualNetSavingsYear1.toFixed(0)),
    roi_after_20_years: parseFloat(roiAfter20Years.toFixed(1))
};
