function calculate() {
  const initialSystemCost = inputs.initialSystemCost;
  const annualElectricityConsumption = inputs.annualElectricityConsumption;
  const currentElectricityRate = inputs.currentElectricityRate;
  const solarProductionOffsetPercentage = inputs.solarProductionOffsetPercentage;
  const federalTaxCreditPercentage = inputs.federalTaxCreditPercentage;
  const annualElectricityRateIncrease = inputs.annualElectricityRateIncrease;
  const otherIncentivesAmount = inputs.otherIncentivesAmount || 0;

  // 1. Calculate the effective initial cost after incentives
  const applicableFederalCreditPercentage = Math.min(federalTaxCreditPercentage, 30); // Cap ITC at 30% for realism if input exceeds
  const federalCreditAmount = initialSystemCost * (applicableFederalCreditPercentage / 100);
  const netInitialCost = Math.max(0, initialSystemCost - federalCreditAmount - otherIncentivesAmount);

  // 2. Calculate annual electricity offset by solar panels
  const annualSolarOffsetKwh = annualElectricityConsumption * (solarProductionOffsetPercentage / 100);

  // Initialize variables for iteration
  let currentElectricityPrice = currentElectricityRate;
  let cumulativeSavings = 0;
  let paybackPeriod = NaN;
  let remainingCostForPayback = netInitialCost;
  let totalSavingsOver25Years = 0;

  // Assuming a 25-year lifespan for solar panels for ROI calculation
  const lifespanYears = 25;

  // 3. Iterate through years to calculate savings, payback period, and total 25-year savings
  for (let year = 1; year <= lifespanYears; year++) {
    const yearlySavings = annualSolarOffsetKwh * currentElectricityPrice;

    totalSavingsOver25Years += yearlySavings;
    cumulativeSavings += yearlySavings;
    remainingCostForPayback -= yearlySavings; // Track remaining cost towards payback

    // Check for payback period
    if (isNaN(paybackPeriod) && remainingCostForPayback <= 0) {
      // Linear interpolation for a more precise payback period
      paybackPeriod = year - (remainingCostForPayback / yearlySavings);
    }

    // Factor in annual electricity rate increase for the next year
    currentElectricityPrice *= (1 + annualElectricityRateIncrease / 100);
  }

  // 4. Calculate ROI (Return on Investment) over 25 years
  let roi25Year = 0;
  if (netInitialCost > 0) {
    roi25Year = ((totalSavingsOver25Years - netInitialCost) / netInitialCost) * 100;
  } else if (totalSavingsOver25Years > 0) {
    roi25Year = Infinity; // Infinite ROI if net cost is 0 but there are savings
  }

  return {
    paybackPeriod: paybackPeriod,
    total25YearSavings: totalSavingsOver25Years,
    roi25Year: roi25Year
  };
}
