const damageSavings = inputs.annualDamageBefore * (inputs.damageReductionPercent / 100);
const insuranceSavings = inputs.annualPremiumBefore * (inputs.premiumReductionPercent / 100);
const totalAnnualBenefit = damageSavings + insuranceSavings;
const netAnnualSavings = totalAnnualBenefit - inputs.upgradeMaintenanceCost;

let paybackPeriod;
if (netAnnualSavings > 0) {
    paybackPeriod = inputs.upgradeCost / netAnnualSavings;
    if (paybackPeriod > 1000) { // Cap at a very high number to represent 'effectively never' for practical display
        paybackPeriod = 9999;
    }
} else if (netAnnualSavings < 0) {
    paybackPeriod = 9999; // Represents 'Never (Annual costs exceed benefits)'
} else { // netAnnualSavings === 0
    paybackPeriod = 9999; // Represents 'No Net Savings'
}
paybackPeriod = Math.max(0, paybackPeriod); // Ensure paybackPeriod is not negative for display

let roi;
if (inputs.upgradeCost > 0) {
    const totalBenefitOverTime = netAnnualSavings * inputs.timeHorizon;
    roi = ((totalBenefitOverTime - inputs.upgradeCost) / inputs.upgradeCost) * 100;
} else if (inputs.upgradeCost === 0) {
    // If cost is 0, any positive savings yields infinite ROI; otherwise, 0 ROI.
    roi = (netAnnualSavings > 0) ? 999999999 : 0; 
} else { // Negative upgrade cost (e.g., substantial rebate exceeding cost)
    // This is an unusual scenario but handled to prevent division by zero or unexpected values
    const totalBenefitOverTime = netAnnualSavings * inputs.timeHorizon;
    roi = ((totalBenefitOverTime - inputs.upgradeCost) / Math.abs(inputs.upgradeCost)) * 100; // Use absolute cost for denominator
    if (roi < 0 && netAnnualSavings > 0) roi = 999999999; // If rebate makes cost negative, and there are savings, ROI is effectively infinite
}

return {
    annualSavings: netAnnualSavings,
    paybackPeriod: paybackPeriod,
    roi: roi
};
