function calculate(inputs) {
    const {
        animalType, propertySizeSqFt, damageHistory, desiredEffectiveness,
        currentAnnualDamageCost, diyCapability, budgetLimit, strategyPreference
    } = inputs;

    let initialCost = 0;
    let annualMaintCost = 0;
    let effectivenessFactor = desiredEffectiveness / 100;
    let strategyDescription = "";

    // Base Costs (simplified for example, real would be more granular and consider specific deterrent types)
    // Costs are per sq ft of affected area or linear ft for fencing (deer)
    const baseCostsPerUnit = {
        'Bear': { 
            'Exclusion': { initial: 30, annualMaintFactor: 0.05, description: 'Heavy-Duty Structural Exclusion & Reinforcement' }, 
            'Electric Fence': { initial: 20, annualMaintFactor: 0.1, description: 'Perimeter Electric Fencing & Deterrents' } 
        },
        'Raccoon': { 
            'Exclusion': { initial: 15, annualMaintFactor: 0.07, description: 'Comprehensive Entry Point Sealing & Trapping' }, 
            'Advanced Exclusion': { initial: 25, annualMaintFactor: 0.08, description: 'Advanced Exclusion with One-Way Doors & Monitoring' } 
        },
        'Rodent (Mice/Rats)': { 
            'Exclusion': { initial: 7, annualMaintFactor: 0.05, description: 'Basic Rodent Proofing & Bait Stations' }, 
            'Advanced Exclusion': { initial: 12, annualMaintFactor: 0.07, description: 'Comprehensive Rodent Exclusion & Integrated Pest Management' } 
        },
        'Deer': { 
            'Fencing': { initial: 3.5, annualMaintFactor: 0.05, unit: 'linear_ft', description: 'Tall Perimeter Fencing (8ft+)' }, 
            'Repellent Program': { initial: 1.5, annualMaintFactor: 0.2, unit: 'sq_ft', description: 'Regular Repellent Application Program' } 
        },
        'Bird': { 
            'Netting': { initial: 2.5, annualMaintFactor: 0.08, description: 'Targeted Bird Netting & Spikes' }, 
            'Sonic': { initial: 1.0, annualMaintFactor: 0.15, description: 'Sonic Deterrents & Visual Scare Devices' } 
        },
        'Skunk': { 
            'Exclusion': { initial: 12, annualMaintFactor: 0.07, description: 'Under-Structure Exclusion & Den Removal' }, 
            'Trapping': { initial: 8, annualMaintFactor: 0.05, description: 'Live Trapping & Relocation Services' } 
        },
        'Opossum': { 
            'Exclusion': { initial: 12, annualMaintFactor: 0.07, description: 'Under-Structure Exclusion & Habitat Modification' }, 
            'Trapping': { initial: 8, annualMaintFactor: 0.05, description: 'Live Trapping & Relocation Services' } 
        },
        'Coyote': { 
            'Fencing': { initial: 4.0, annualMaintFactor: 0.06, unit: 'linear_ft', description: 'Coyote-Roll/Dig-Proof Perimeter Fencing' }, 
            'Hazing': { initial: 0.7, annualMaintFactor: 0.15, description: 'Hazing & Landscape Modification Program' } 
        },
        'Other': { 
            'General Exclusion': { initial: 10, annualMaintFactor: 0.08, description: 'General Structural Exclusion Measures' }, 
            'General Repellent': { initial: 1, annualMaintFactor: 0.15, description: 'General Wildlife Repellent Application' } 
        }
    };

    // Damage history multipliers for initial cost (higher damage -> more repair/robust solution needed)
    const damageMultiplier = {
        'None (Proactive)': 1.0,
        'Minor (Occasional nuisance, no structural damage)': 1.15,
        'Moderate (Minor property damage, frequent nuisance)': 1.4,
        'Severe (Significant structural or landscaping damage)': 1.8,
        'Chronic (Repeated, severe damage requiring ongoing repairs)': 2.5
    }[damageHistory] || 1.0;

    // DIY capability multiplier for installation labor (reduces initial cost component)
    const diyMultiplier = {
        'None (Full professional service)': 1.0, // Full professional install
        'Moderate (Some tasks, basic repairs)': 0.8, // Some labor saved
        'High (Significant self-sufficiency)': 0.6 // Significant labor saved
    }[diyCapability] || 1.0;

    // Determine the most appropriate strategy based on animal type, desired effectiveness, and preference
    let selectedStrategyKey = '';
    let strategyInfo = null;

    const animalStrategies = baseCostsPerUnit[animalType] || baseCostsPerUnit['Other'];

    // Prioritize 'Exclusion' if highly desired or high effectiveness, otherwise consider 'Repellent' or other secondary
    if (strategyPreference === 'Exclusion' || effectivenessFactor >= 0.85) {
        // Try to find the most 'exclusion'-like strategy
        if (animalType === 'Bear') selectedStrategyKey = 'Electric Fence'; // More effective for bears typically
        else if (animalType === 'Raccoon' || animalType === 'Rodent (Mice/Rats)') selectedStrategyKey = 'Advanced Exclusion';
        else if (animalType === 'Deer') selectedStrategyKey = 'Fencing';
        else if (animalType === 'Bird') selectedStrategyKey = 'Netting';
        else if (animalType === 'Skunk' || animalType === 'Opossum') selectedStrategyKey = 'Exclusion';
        else if (animalType === 'Coyote') selectedStrategyKey = 'Fencing';
        else selectedStrategyKey = 'General Exclusion';

        // Fallback if specific key not found for an animal, e.g. if 'Advanced Exclusion' doesn't exist for 'Skunk'
        if (!animalStrategies[selectedStrategyKey]) {
             selectedStrategyKey = Object.keys(animalStrategies).find(key => key.includes('Exclusion') || key.includes('Fence') || key.includes('Netting')) || Object.keys(animalStrategies)[0];
        }
    } else if (strategyPreference === 'Repellent') {
        if (animalType === 'Deer') selectedStrategyKey = 'Repellent Program';
        else if (animalType === 'Bird') selectedStrategyKey = 'Sonic';
        else if (animalType === 'Coyote') selectedStrategyKey = 'Hazing';
        else selectedStrategyKey = 'General Repellent';

        if (!animalStrategies[selectedStrategyKey]) {
            selectedStrategyKey = Object.keys(animalStrategies).find(key => key.includes('Repellent') || key.includes('Sonic') || key.includes('Hazing')) || Object.keys(animalStrategies)[0];
        }
    } else if (strategyPreference === 'Habitat Modification') {
        // Habitat modification often implied within other strategies or is lower cost
        selectedStrategyKey = Object.keys(animalStrategies).find(key => key.includes('Hazing') || key.includes('Repellent Program') || key.includes('Exclusion')) || Object.keys(animalStrategies)[0];
    } else { // Integrated or default fallback
        // Choose a generally robust one, leaning towards exclusion
        if (animalType === 'Bear') selectedStrategyKey = 'Electric Fence';
        else if (animalType === 'Raccoon' || animalType === 'Rodent (Mice/Rats)') selectedStrategyKey = 'Advanced Exclusion';
        else if (animalType === 'Deer') selectedStrategyKey = 'Fencing';
        else if (animalType === 'Bird') selectedStrategyKey = 'Netting';
        else if (animalType === 'Skunk' || animalType === 'Opossum') selectedStrategyKey = 'Exclusion';
        else if (animalType === 'Coyote') selectedStrategyKey = 'Fencing';
        else selectedStrategyKey = 'General Exclusion';

        if (!animalStrategies[selectedStrategyKey]) {
             selectedStrategyKey = Object.keys(animalStrategies)[0];
        }
    }

    strategyInfo = animalStrategies[selectedStrategyKey];

    // Calculate initial and annual maintenance costs
    let effectivePropertyUnit = propertySizeSqFt;
    if (strategyInfo.unit === 'linear_ft') {
        // For fencing, approximate perimeter as 4 * sqrt(area) if area is dominant factor
        // For real-world, a linear foot input would be better.
        effectivePropertyUnit = Math.max(100, Math.sqrt(propertySizeSqFt) * 4);
    }

    initialCost = strategyInfo.initial * effectivePropertyUnit;
    annualMaintCost = initialCost * strategyInfo.annualMaintFactor;

    // Adjust for damage history and DIY
    initialCost *= damageMultiplier;
    initialCost *= diyMultiplier;

    // Adjust for desired effectiveness (higher effectiveness implies higher quality materials/installation/scope)
    const effectivenessCostMultiplier = (effectivenessFactor < 0.7) ? 0.9 : (effectivenessFactor < 0.85) ? 1.0 : 1.2;
    initialCost *= effectivenessCostMultiplier;
    annualMaintCost *= effectivenessCostMultiplier; // Maintenance also scales with quality

    let finalInitialInvestment = initialCost;
    let finalAnnualMaintenanceCost = annualMaintCost;
    let effectiveEffectivenessFactor = effectivenessFactor; // actual achieved effectiveness
    let budgetAdjustmentNote = "";

    if (budgetLimit && initialCost > budgetLimit) {
        finalInitialInvestment = budgetLimit;
        // Scale down effectiveness proportionally, with a floor, and adjust maintenance
        effectiveEffectivenessFactor = Math.max(0.5, effectivenessFactor * (budgetLimit / initialCost));
        finalAnnualMaintenanceCost = annualMaintCost * (budgetLimit / initialCost);
        budgetAdjustmentNote = ` (Adjusted to meet budget limit of $${budgetLimit}. Achieved effectiveness estimated at ${Math.round(effectiveEffectivenessFactor * 100)}%.)`;
    }

    // Calculate potential annual savings
    // Savings = (Current Annual Damage Cost) * (Effective Effectiveness Factor) + buffer for avoided escalation/peace of mind
    const estimatedAnnualSavings = currentAnnualDamageCost * effectiveEffectivenessFactor * 1.1; 

    // Calculate Payback Period
    const paybackPeriodYears = (estimatedAnnualSavings > 0 && finalInitialInvestment > 0) ? finalInitialInvestment / estimatedAnnualSavings : (finalInitialInvestment === 0 ? 0 : Infinity);

    // Calculate 5-Year ROI
    const totalCostOver5Years = finalInitialInvestment + (finalAnnualMaintenanceCost * 5);
    const totalSavingsOver5Years = estimatedAnnualSavings * 5;
    const fiveYearROI = (totalCostOver5Years > 0) ? ((totalSavingsOver5Years - totalCostOver5Years) / totalCostOver5Years) * 100 : 0;

    return {
        recommendedInitialInvestment: parseFloat(finalInitialInvestment.toFixed(2)),
        annualMaintenanceCost: parseFloat(finalAnnualMaintenanceCost.toFixed(2)),
        estimatedAnnualSavings: parseFloat(estimatedAnnualSavings.toFixed(2)),
        paybackPeriodYears: parseFloat(paybackPeriodYears.toFixed(2)),
        fiveYearROI: parseFloat(fiveYearROI.toFixed(2)),
        recommendedStrategyDetails: `Recommended strategy: ${strategyInfo.description}. Based on your inputs, this approach offers an estimated ${Math.round(effectiveEffectivenessFactor * 100)}% reduction in wildlife conflict. ${budgetAdjustmentNote}`
    };
}
