const H = inputs.wall_height_h;
const B = inputs.wall_base_width_b;
const gamma_soil = inputs.soil_unit_weight_gamma;
const phi_deg = inputs.soil_friction_angle_phi;
const delta_deg = inputs.wall_friction_angle_delta;
const q = inputs.surcharge_load_q;
const mu = inputs.coefficient_of_friction_mu;
const gamma_wall = inputs.wall_unit_weight_gamma_wall;

// Input validation (basic checks for realistic values)
if (H <= 0 || B <= 0 || gamma_soil <= 0 || phi_deg < 0 || phi_deg >= 90 || delta_deg < 0 || delta_deg > phi_deg || mu <= 0 || gamma_wall <= 0) {
    return {
        active_earth_pressure_force_pa: NaN,
        total_sliding_force: NaN,
        total_resisting_force: NaN,
        factor_of_safety_sliding: NaN
    };
}

// Convert angles to radians
const phi_rad = phi_deg * Math.PI / 180;
const delta_rad = delta_deg * Math.PI / 180;

// 1. Calculate Rankine Active Earth Pressure Coefficient (Ka)
// Assuming vertical wall face and horizontal backfill for Rankine theory simplification
const Ka = Math.pow(Math.tan(Math.PI / 4 - phi_rad / 2), 2);

// 2. Calculate Total Active Earth Pressure Force (Pa) per unit length of wall
// Pa is the resultant force magnitude. We use Rankine Ka and apply delta for inclination.
// The pressure distribution is triangular from soil weight and rectangular from surcharge.
const Pa_soil_component = 0.5 * gamma_soil * Math.pow(H, 2) * Ka;
const Pa_surcharge_component = q * H * Ka;
const active_earth_pressure_force_pa = Pa_soil_component + Pa_surcharge_component;

// 3. Resolve Pa into horizontal and vertical components, considering wall-soil friction angle (delta)
// For a vertical wall, delta is the angle of Pa with the horizontal.
const Pah = active_earth_pressure_force_pa * Math.cos(delta_rad);
const Pav = active_earth_pressure_force_pa * Math.sin(delta_rad);

// 4. Calculate Wall Weight (W_wall) per unit length (assuming rectangular wall for simplicity)
const W_wall = gamma_wall * B * H;

// 5. Calculate Total Vertical Force (Rv) acting on the base
const total_vertical_force_rv = W_wall + Pav;

// 6. Calculate Total Horizontal Sliding Force (Rh)
const total_sliding_force = Pah;

// 7. Calculate Resisting Force (Fr) due to friction at the base
const total_resisting_force = mu * total_vertical_force_rv;

// 8. Calculate Factor of Safety Against Sliding (FOS_sliding)
let factor_of_safety_sliding = total_sliding_force === 0 ? Infinity : total_resisting_force / total_sliding_force;

// Cap FOS at a reasonable high value if it becomes excessively large (e.g., 999 for practical purposes)
if (factor_of_safety_sliding > 999) {
    factor_of_safety_sliding = 999;
}

return {
    active_earth_pressure_force_pa: active_earth_pressure_force_pa,
    total_sliding_force: total_sliding_force,
    total_resisting_force: total_resisting_force,
    factor_of_safety_sliding: factor_of_safety_sliding
};
