const retirementDurationYears = 25; // Fixed assumption for calculation
const safeWithdrawalRate = 0.04; // 4% real withdrawal rate

const yearsUntilRetirement = Math.max(0, retirementAge - currentAge);

// Convert percentage to decimal
const realReturnDecimal = expectedRealAnnualReturn / 100;

// Calculate desired income in 'today's dollars' (real terms)
const desiredFirstYearRetirementIncome = currentAnnualExpenses * (desiredIncomePercentage / 100);

// Other income is also in 'today's dollars' (real terms) as per input description
const adjustedOtherRetirementIncome = otherAnnualRetirementIncome;

// Calculate the future value of current savings (in 'today's dollars' / real terms)
const futureValueCurrentSavings = currentRetirementSavings * Math.pow(1 + realReturnDecimal, yearsUntilRetirement);

// Calculate the future value of annual savings contributions (in 'today's dollars' / real terms)
let futureValueAnnualSavings = 0;
if (yearsUntilRetirement > 0 && realReturnDecimal !== 0) {
  futureValueAnnualSavings = annualSavingsContribution * ((Math.pow(1 + realReturnDecimal, yearsUntilRetirement) - 1) / realReturnDecimal);
} else if (yearsUntilRetirement > 0 && realReturnDecimal === 0) {
  futureValueAnnualSavings = annualSavingsContribution * yearsUntilRetirement;
} else { // yearsUntilRetirement === 0
  futureValueAnnualSavings = 0; // No more contributions if already retired
}

const totalSavingsAtRetirement = futureValueCurrentSavings + futureValueAnnualSavings;

// Calculate annual income available from projected savings
const annualIncomeFromSavings = totalSavingsAtRetirement * safeWithdrawalRate;

// Calculate the net annual income needed from savings (after other income)
const netAnnualIncomeNeeded = Math.max(0, desiredFirstYearRetirementIncome - adjustedOtherRetirementIncome);

// Calculate the annual income shortfall in the first year of retirement
const annualShortfallFirstYear = Math.max(0, netAnnualIncomeNeeded - annualIncomeFromSavings);

// Calculate the lump sum shortfall needed at retirement to cover the annual shortfall
const lumpSumShortfall = (annualShortfallFirstYear > 0 && safeWithdrawalRate > 0) ? (annualShortfallFirstYear / safeWithdrawalRate) : 0;

// Calculate additional annual savings needed to cover the lump sum shortfall
let additionalAnnualSavingsNeeded = 0;
if (lumpSumShortfall > 0) {
  if (yearsUntilRetirement > 0) {
    if (realReturnDecimal !== 0) {
      // Future Value of an Annuity formula: FV = P * [((1 + r)^n - 1) / r]
      // We need P, so P = FV / [((1 + r)^n - 1) / r]
      const fvFactor = (Math.pow(1 + realReturnDecimal, yearsUntilRetirement) - 1) / realReturnDecimal;
      additionalAnnualSavingsNeeded = lumpSumShortfall / fvFactor;
    } else { // realReturnDecimal === 0
      additionalAnnualSavingsNeeded = lumpSumShortfall / yearsUntilRetirement;
    }
  } else { // yearsUntilRetirement === 0
    additionalAnnualSavingsNeeded = lumpSumShortfall; // Need to save it all now
  }
}

return {
  annualShortfallFirstYear: annualShortfallFirstYear,
  lumpSumShortfall: lumpSumShortfall,
  additionalAnnualSavingsNeeded: additionalAnnualSavingsNeeded
};
