function randNormal(mean, stdDev) {
    let u = 0, v = 0;
    while (u === 0) u = Math.random();
    while (v === 0) v = Math.random();
    const z = Math.sqrt(-2.0 * Math.log(u)) * Math.cos(2.0 * Math.PI * v);
    return z * stdDev + mean;
}

const {
    initialPortfolio,
    initialWithdrawal,
    inflationRate,
    avgMarketReturn,
    stdDevMarketReturn,
    simulationYears,
    numSimulations
} = inputs;

const annualInflationFactor = 1 + (inflationRate / 100);

let successfulSimulations = 0;
let endingPortfolioValues = [];
let yearsLastedArray = [];

for (let s = 0; s < numSimulations; s++) {
    let currentPortfolio = initialPortfolio;
    let currentWithdrawal = initialWithdrawal;
    let yearsLasted = 0;
    let portfolioDepleted = false;

    for (let year = 1; year <= simulationYears; year++) {
        let annualReturnPercentage = randNormal(avgMarketReturn, stdDevMarketReturn);
        let annualReturnFactor = 1 + (annualReturnPercentage / 100);

        currentPortfolio *= annualReturnFactor;

        currentPortfolio -= currentWithdrawal;

        if (currentPortfolio <= 0) {
            portfolioDepleted = true;
            yearsLasted = year;
            break;
        }

        currentWithdrawal *= annualInflationFactor;

        yearsLasted = year;
    }

    if (!portfolioDepleted) {
        successfulSimulations++;
        endingPortfolioValues.push(currentPortfolio);
        yearsLastedArray.push(simulationYears);
    } else {
        endingPortfolioValues.push(0);
        yearsLastedArray.push(yearsLasted);
    }
}

const successRate = (successfulSimulations / numSimulations) * 100;

endingPortfolioValues.sort((a, b) => a - b);
const mid = Math.floor(endingPortfolioValues.length / 2);
const medianEndingPortfolioValue = endingPortfolioValues.length % 2 === 0
    ? (endingPortfolioValues[mid - 1] + endingPortfolioValues[mid]) / 2
    : endingPortfolioValues[mid];

const totalYearsLasted = yearsLastedArray.reduce((sum, val) => sum + val, 0);
const avgYearsPortfolioLasted = totalYearsLasted / numSimulations;

return {
    successRate: successRate,
    medianEndingPortfolioValue: Math.max(0, medianEndingPortfolioValue),
    avgYearsPortfolioLasted: avgYearsPortfolioLasted
};
