function calculate({ currentAge, retirementAge, currentSavings, monthlySavings, annualReturnRate, inflationRate, desiredAnnualIncome }) {
  const yearsToRetirement = retirementAge - currentAge;
  if (yearsToRetirement < 0) {
    return {
      projectedNestEgg: 0,
      desiredIncomeAtRetirement: 0,
      annualIncomeFromNestEgg: 0,
      incomeShortfallSurplus: 0,
      yearsIncomeWillLast: 0
    };
  }

  const nominalReturnRate = annualReturnRate / 100;
  const inflationRateDecimal = inflationRate / 100;
  const monthlyNominalRate = nominalReturnRate / 12;
  const totalMonths = yearsToRetirement * 12;
  const withdrawalRate = 0.04; // The 4% rule

  // 1. Future Value of Current Savings
  const futureValueOfCurrentSavings = currentSavings * Math.pow(1 + nominalReturnRate, yearsToRetirement);

  // 2. Future Value of Future Monthly Contributions (Annuity Formula)
  let futureValueOfMonthlySavings = 0;
  if (monthlyNominalRate > 0) {
    futureValueOfMonthlySavings = monthlySavings * (Math.pow(1 + monthlyNominalRate, totalMonths) - 1) / monthlyNominalRate;
  } else {
    // If rate is 0, it's just total contributions
    futureValueOfMonthlySavings = monthlySavings * totalMonths;
  }

  // Total Projected Nest Egg
  const projectedNestEgg = futureValueOfCurrentSavings + futureValueOfMonthlySavings;

  // 3. Desired Annual Income adjusted for inflation
  const desiredIncomeAtRetirement = desiredAnnualIncome * Math.pow(1 + inflationRateDecimal, yearsToRetirement);

  // 4. Annual income generated by the projected nest egg (using 4% rule)
  const annualIncomeFromNestEgg = projectedNestEgg * withdrawalRate;

  // 5. Shortfall or Surplus
  const incomeShortfallSurplus = annualIncomeFromNestEgg - desiredIncomeAtRetirement;

  // 6. Years Income Will Last (if depleted, assuming desired withdrawal without 4% rule constraint)
  let yearsIncomeWillLast;
  if (annualIncomeFromNestEgg >= desiredIncomeAtRetirement && desiredAnnualIncome > 0) {
      yearsIncomeWillLast = Infinity; // Indicates it should last indefinitely under the 4% rule
  } else if (desiredIncomeAtRetirement <= 0) {
      yearsIncomeWillLast = Infinity;
  } else {
      // Calculate depletion years if the nest egg is insufficient for the 4% rule
      // This is a simplified depletion model assuming constant withdrawals and real growth
      let balance = projectedNestEgg;
      let years = 0;
      const realReturnRate = (1 + nominalReturnRate) / (1 + inflationRateDecimal) - 1;
      const effectiveAnnualWithdrawal = desiredIncomeAtRetirement;

      if (effectiveAnnualWithdrawal <= 0) {
          years = Infinity;
      } else if (projectedNestEgg <= 0) {
          years = 0;
      } else {
          while (balance > effectiveAnnualWithdrawal && years < 100) {
              balance = balance * (1 + realReturnRate) - effectiveAnnualWithdrawal;
              years++;
          }
          // If balance is exactly 0 or just below after loop, it implies it lasted 'years'
          // If balance is still positive but less than withdrawal, it lasts 'years' + fraction
          if (balance > 0 && balance < effectiveAnnualWithdrawal) {
              years += balance / effectiveAnnualWithdrawal;
          }
      }
      yearsIncomeWillLast = years;
  }

  return {
    projectedNestEgg: Math.max(0, projectedNestEgg),
    desiredIncomeAtRetirement: Math.max(0, desiredIncomeAtRetirement),
    annualIncomeFromNestEgg: Math.max(0, annualIncomeFromNestEgg),
    incomeShortfallSurplus: incomeShortfallSurplus,
    yearsIncomeWillLast: yearsIncomeWillLast
  };
}
