function calculate({ inputs }) {
    let initial_portfolio_value = inputs.initial_portfolio_value;
    let retirement_years = inputs.retirement_years;
    let expected_annual_return_decimal = inputs.expected_annual_return / 100;
    let inflation_rate_decimal = inputs.inflation_rate / 100;
    let desired_income_first_year = inputs.desired_income_first_year;

    let initial_safe_withdrawal_amount;
    let safe_withdrawal_rate;
    let years_to_last_with_income = 0;
    let final_portfolio_value_at_target_longevity = 0;

    // Calculate initial_safe_withdrawal_amount based on a simplified annuity formula
    // This calculates the maximum initial withdrawal amount (inflation-adjusted annually)
    // that the portfolio can sustain for the given retirement duration.
    if (expected_annual_return_decimal === inflation_rate_decimal) {
        // Special case where real return is 0
        initial_safe_withdrawal_amount = initial_portfolio_value / retirement_years;
    } else {
        // General case using the formula for calculating maximum sustainable withdrawal
        // Formula derived from Present Value of an Annuity Due with growth:
        // P = W * [1 - ((1+I)/(1+R))^N] / (R-I) if R != I
        // So W = P * (R-I) / [1 - ((1+I)/(1+R))^N]
        let numerator = initial_portfolio_value * (expected_annual_return_decimal - inflation_rate_decimal);
        let denominator_term = Math.pow((1 + inflation_rate_decimal) / (1 + expected_annual_return_decimal), retirement_years);
        let denominator = 1 - denominator_term;

        if (denominator === 0 || numerator === 0) {
            initial_safe_withdrawal_amount = 0;
        } else {
            initial_safe_withdrawal_amount = numerator / denominator;
        }

        // If inflation is much higher than return, initial_safe_withdrawal_amount can be negative or unrealistic.
        // If denominator becomes negative (meaning (1+I)/(1+R) is large and exponent makes it very large, > 1),
        // it suggests the portfolio can't sustain positive real withdrawals for that long.
        if (initial_safe_withdrawal_amount < 0 || !isFinite(initial_safe_withdrawal_amount)) {
            initial_safe_withdrawal_amount = 0; 
        }
    }

    // Cap safe withdrawal amount to a realistic range (e.g., max 10% of portfolio value), and ensure it's not negative.
    initial_safe_withdrawal_amount = Math.max(0, Math.min(initial_safe_withdrawal_amount, initial_portfolio_value * 0.10));

    // Calculate the safe withdrawal rate as a percentage
    safe_withdrawal_rate = (initial_portfolio_value > 0) ? (initial_safe_withdrawal_amount / initial_portfolio_value) * 100 : 0;

    // Simulate portfolio longevity for desired_income_first_year
    let current_portfolio_for_simulation = initial_portfolio_value;
    let current_withdrawal_for_simulation = desired_income_first_year;
    let annual_growth_factor = (1 + expected_annual_return_decimal);
    let annual_inflation_factor = (1 + inflation_rate_decimal);
    let max_simulation_years = retirement_years * 2; // Cap simulation to avoid infinite loops for very long lasting portfolios

    for (let year = 1; year <= max_simulation_years; year++) {
        // Portfolio grows, then withdrawal is taken
        current_portfolio_for_simulation = current_portfolio_for_simulation * annual_growth_factor;

        // Check if portfolio can cover the withdrawal
        if (current_portfolio_for_simulation <= current_withdrawal_for_simulation) {
            years_to_last_with_income = year;
            final_portfolio_value_at_target_longevity = 0;
            current_portfolio_for_simulation = 0; // Portfolio depleted
            break;
        }

        current_portfolio_for_simulation -= current_withdrawal_for_simulation;

        // Withdrawal adjusts for inflation for the next year
        current_withdrawal_for_simulation = current_withdrawal_for_simulation * annual_inflation_factor;

        // If we've reached the target retirement years, capture the portfolio value
        if (year === retirement_years) {
            final_portfolio_value_at_target_longevity = current_portfolio_for_simulation;
        }

        years_to_last_with_income = year;
    }

    // If simulation ran to max_simulation_years and portfolio is still positive, it lasted at least that long
    if (current_portfolio_for_simulation > 0 && years_to_last_with_income === max_simulation_years) {
        years_to_last_with_income = max_simulation_years; // Indicate it lasted beyond the target
    }
    // Ensure final portfolio value is not negative if it ran out exactly at target years but was slightly negative due to calculation order
    if (final_portfolio_value_at_target_longevity < 0 && years_to_last_with_income <= retirement_years) {
        final_portfolio_value_at_target_longevity = 0;
    }

    return {
        initial_safe_withdrawal_amount: initial_safe_withdrawal_amount,
        safe_withdrawal_rate: safe_withdrawal_rate,
        years_to_last_with_income: years_to_last_with_income,
        final_portfolio_value_at_target_longevity: final_portfolio_value_at_target_longevity
    };
}
