function calculate() {
  const currentAge = inputs.currentAge;
  const retirementAge = inputs.retirementAge;
  const currentSavings = inputs.currentSavings;
  const monthlyContribution = inputs.monthlyContribution;
  const annualReturnRate = inputs.annualReturnRate / 100;
  const inflationRate = inputs.inflationRate / 100;
  const retirementIncomeNeeded = inputs.retirementIncomeNeeded;

  const yearsToRetirement = Math.max(0, retirementAge - currentAge);

  let projectedRetirementSavings = 0;
  let targetNestEgg = 0;
  let savingsGapOrSurplus = 0;

  const safeWithdrawalRate = 0.04; // Common 4% rule

  if (yearsToRetirement === 0) {
    projectedRetirementSavings = currentSavings;
    targetNestEgg = retirementIncomeNeeded / safeWithdrawalRate; // Assuming current income is future income
  } else {
    // Future Value of Current Savings (Lump Sum)
    const fvCurrentSavings = currentSavings * Math.pow(1 + annualReturnRate, yearsToRetirement);

    // Future Value of Monthly Contributions (Annuity)
    const monthlyReturnRate = Math.pow(1 + annualReturnRate, 1 / 12) - 1; // Effective monthly rate
    const numberOfMonths = yearsToRetirement * 12;

    let fvContributions;
    if (monthlyReturnRate === 0) {
      fvContributions = monthlyContribution * numberOfMonths;
    } else {
      fvContributions = monthlyContribution * ((Math.pow(1 + monthlyReturnRate, numberOfMonths) - 1) / monthlyReturnRate);
    }

    projectedRetirementSavings = fvCurrentSavings + fvContributions;

    // Inflation-adjust desired retirement income
    const inflationAdjustedRetirementIncome = retirementIncomeNeeded * Math.pow(1 + inflationRate, yearsToRetirement);

    // Calculate target nest egg using the 4% rule
    targetNestEgg = inflationAdjustedRetirementIncome / safeWithdrawalRate;
  }

  savingsGapOrSurplus = projectedRetirementSavings - targetNestEgg;

  return {
    projectedRetirementSavings: projectedRetirementSavings,
    targetNestEgg: targetNestEgg,
    savingsGapOrSurplus: savingsGapOrSurplus
  };
}
