function calculate({ currentBuildingValue, estimatedRetrofitCost, remainingBuildingLifespan, postRetrofitValueIncrease, preRetrofitDamageProbability, preRetrofitDamageSeverity, postRetrofitDamageProbabilityReduction, postRetrofitDamageSeverityReduction, discountRate }) {

  // Input validation and clamping
  currentBuildingValue = Math.max(0, currentBuildingValue || 0);
  estimatedRetrofitCost = Math.max(0, estimatedRetrofitCost || 0);
  remainingBuildingLifespan = Math.max(1, Math.round(remainingBuildingLifespan || 1));
  postRetrofitValueIncrease = Math.max(0, postRetrofitValueIncrease || 0);
  preRetrofitDamageProbability = Math.max(0, Math.min(100, preRetrofitDamageProbability || 0));
  preRetrofitDamageSeverity = Math.max(0, Math.min(100, preRetrofitDamageSeverity || 0));
  postRetrofitDamageProbabilityReduction = Math.max(0, Math.min(100, postRetrofitDamageProbabilityReduction || 0));
  postRetrofitDamageSeverityReduction = Math.max(0, Math.min(100, postRetrofitDamageSeverityReduction || 0));
  discountRate = Math.max(0, discountRate || 0);

  const dr = discountRate / 100; // Convert to decimal

  // 1. Calculate Expected Annual Damage Pre-Retrofit
  const expectedAnnualDamagePreRetrofit = currentBuildingValue * (preRetrofitDamageProbability / 100) * (preRetrofitDamageSeverity / 100);

  // 2. Calculate Expected Annual Damage Post-Retrofit
  const newDamageProbability = preRetrofitDamageProbability * (1 - postRetrofitDamageProbabilityReduction / 100);
  const newDamageSeverity = preRetrofitDamageSeverity * (1 - postRetrofitDamageSeverityReduction / 100);

  const expectedAnnualDamagePostRetrofit = currentBuildingValue * (newDamageProbability / 100) * (newDamageSeverity / 100);

  // 3. Calculate Net Annual Benefit from Damage Avoidance
  const netAnnualBenefitFromRetrofit = expectedAnnualDamagePreRetrofit - expectedAnnualDamagePostRetrofit;

  // 4. Calculate Net Present Value (NPV) of Benefits
  let npvOfDamageAvoidanceBenefits = 0;
  for (let i = 1; i <= remainingBuildingLifespan; i++) {
    npvOfDamageAvoidanceBenefits += netAnnualBenefitFromRetrofit / Math.pow(1 + dr, i);
  }

  // Add potential property value increase as a one-time benefit (NPV'd at end of lifespan)
  const npvOfValueIncrease = currentBuildingValue * (postRetrofitValueIncrease / 100) / Math.pow(1 + dr, remainingBuildingLifespan);

  const totalNetPresentBenefit = (npvOfDamageAvoidanceBenefits + npvOfValueIncrease) - estimatedRetrofitCost;

  // 5. Calculate Retrofit ROI
  let retrofitROI = 0;
  if (estimatedRetrofitCost > 0) {
    retrofitROI = (totalNetPresentBenefit / estimatedRetrofitCost) * 100;
  }

  // 6. Calculate Payback Period
  let paybackPeriod = 'Never';
  if (netAnnualBenefitFromRetrofit > 0) {
    let cumulativeBenefit = 0;
    for (let year = 1; year <= remainingBuildingLifespan; year++) {
      cumulativeBenefit += netAnnualBenefitFromRetrofit / Math.pow(1 + dr, year); // Discounted annual benefit
      if (cumulativeBenefit >= estimatedRetrofitCost) {
        paybackPeriod = year;
        break;
      }
    }
    if (paybackPeriod === 'Never' && cumulativeBenefit < estimatedRetrofitCost) {
      paybackPeriod = `>${remainingBuildingLifespan}`;
    }
  } else if (netAnnualBenefitFromRetrofit <= 0 && estimatedRetrofitCost > 0) {
    paybackPeriod = 'Never (No net annual benefit)';
  } else if (estimatedRetrofitCost === 0) {
      paybackPeriod = 0; // If no cost, immediate payback
  }

  return {
    totalRetrofitCost: estimatedRetrofitCost,
    expectedAnnualDamagePreRetrofit: expectedAnnualDamagePreRetrofit,
    expectedAnnualDamagePostRetrofit: expectedAnnualDamagePostRetrofit,
    netAnnualBenefitFromRetrofit: netAnnualBenefitFromRetrofit,
    totalNetPresentBenefit: totalNetPresentBenefit,
    retrofitROI: retrofitROI,
    paybackPeriod: typeof paybackPeriod === 'number' ? parseFloat(paybackPeriod.toFixed(1)) : paybackPeriod
  };
}
