function calculate({ roofPitch, roofingDeadLoad, groundSnowLoad, exposureFactor, thermalFactor, importanceFactor, baseStructuralStrength }) {
    let Cs;

    // Slope Factor (Cs) - Simplified for 'cold roof' condition based on ASCE 7
    if (roofPitch <= 30) {
        Cs = 1.0;
    } else if (roofPitch > 30 && roofPitch <= 70) {
        Cs = 1 - (roofPitch - 30) / 40;
    } else {
        Cs = 0; // For roofs steeper than 70 degrees, snow rarely accumulates significantly
    }

    // Design Flat Roof Snow Load (Pf)
    const Pf = 0.7 * exposureFactor * thermalFactor * importanceFactor * groundSnowLoad;

    // Calculated Design Roof Snow Load (Ps) - load on the roof surface
    const calculatedRoofSnowLoad = Cs * Pf;

    // Estimated Max Safe Snow Load Capacity
    // This is a simplified estimation. baseStructuralStrength is assumed to be the total live load capacity (including snow),
    // so we subtract the permanent dead load to find the remaining capacity for snow.
    const effectiveRoofCapacity = Math.max(0, baseStructuralStrength - roofingDeadLoad);

    let safetyStatus;
    if (effectiveRoofCapacity >= calculatedRoofSnowLoad * 1.2) {
        safetyStatus = 'Safe (20% buffer)';
    } else if (effectiveRoofCapacity >= calculatedRoofSnowLoad) {
        safetyStatus = 'Marginal (Meets minimum, but little buffer)';
    } else {
        safetyStatus = 'High Risk (Capacity likely below design load)';
    }

    return {
        calculatedRoofSnowLoad: calculatedRoofSnowLoad,
        effectiveRoofCapacity: effectiveRoofCapacity,
        safetyStatus: safetyStatus
    };
}
