const { groundSnowLoad, exposureCategory, thermalCondition, roofSlopeAngle, unobstructedRoofLength } = inputs;

// Input validation and mapping
if (groundSnowLoad <= 0 || roofSlopeAngle < 0 || roofSlopeAngle > 90 || unobstructedRoofLength <= 0) {
  throw new Error('Please enter valid positive values for ground snow load, roof slope, and roof length.');
}

let Ce;
switch (exposureCategory.toLowerCase().trim()) {
    case 'exposed': Ce = 0.9; break;
    case 'partially exposed': Ce = 1.0; break;
    case 'sheltered': Ce = 1.2; break;
    default: throw new Error('Invalid Exposure Category. Please use 'Exposed', 'Partially Exposed', or 'Sheltered'.');
}

let Ct;
switch (thermalCondition.toLowerCase().trim()) {
    case 'warm (unheated)': Ct = 1.0; break;
    case 'ventilated (unheated)': Ct = 1.1; break;
    case 'cold (unheated/continuous)': Ct = 1.2; break;
    default: throw new Error('Invalid Thermal Condition. Please use 'Warm (unheated)', 'Ventilated (unheated)', or 'Cold (unheated/continuous)'.');
}

const Cr = 0.7; // Rain-snow surcharge factor (ASCE 7-16 Eq. 7.3-1)
const Is = 1.0; // Importance Factor (assumed 1.0 for simplicity in this conceptual tool)

// 1. Flat Roof Snow Load (Pf) - ASCE 7-16 Eq. 7.3-1
const Pf = Cr * Ce * Ct * Is * groundSnowLoad;

// 2. Roof Slope Factor (Cs) - Simplified ASCE 7-16 Fig. 7.4-1
let Cs;
if (roofSlopeAngle <= 0) { // Flat roof
    Cs = 1.0;
} else if (roofSlopeAngle >= 70) { // Very steep, snow slides off
    Cs = 0.0;
} else {
    if (Ct === 1.0) { // Warm Roofs (Ct=1.0)
        if (roofSlopeAngle <= 30) {
            Cs = 1.0;
        } else { // Linear interpolation between 30 and 70 degrees
            Cs = 1.0 - (roofSlopeAngle - 30) / 40;
        }
    } else { // Cold/Ventilated Roofs (Ct > 1.0)
        if (roofSlopeAngle <= 15) {
            Cs = 1.0;
        } else { // Linear interpolation between 15 and 70 degrees
            Cs = 1.0 - (roofSlopeAngle - 15) / 55;
        }
    }
}
// Ensure Cs is within [0, 1]
Cs = Math.max(0, Math.min(1.0, Cs));

// 3. Sloped Roof Snow Load (Ps)
const Ps = Cs * Pf;

// 4. Minimum Roof Snow Load (Ps,min) - ASCE 7-16 Section 7.3.4
let minimumRoofSnowLoad;
if (Ct === 1.0 && roofSlopeAngle <= 30) {
    minimumRoofSnowLoad = 20 * Is; // For roofs with slope <= 30 degrees, Ct=1.0
} else if (Ct > 1.0 && roofSlopeAngle <= 15) {
    minimumRoofSnowLoad = 20 * Is; // For roofs with slope <= 15 degrees, Ct>1.0
} else {
    minimumRoofSnowLoad = 10 * Is; // General case
}
// The actual design load is the greater of Ps or minimumRoofSnowLoad
const designRoofSnowLoad = Math.max(Ps, minimumRoofSnowLoad);

// 5. Conceptual Snow Drift Height (hd) - Highly simplified and NOT code-compliant
// This is a conceptual approximation demonstrating that drift increases with length and snow load.
// Actual drift calculations are complex (ASCE 7-16 Sections 7.7 & 7.8).
// The factor (0.01 * unobstructedRoofLength * Math.sqrt(groundSnowLoad)) is illustrative.
const conceptualSnowDriftHeight = Math.min(6, (0.01 * unobstructedRoofLength * Math.sqrt(groundSnowLoad)));

return {
    flatRoofSnowLoad: parseFloat(Pf.toFixed(2)),
    slopedRoofSnowLoad: parseFloat(Ps.toFixed(2)),
    designRoofSnowLoad: parseFloat(designRoofSnowLoad.toFixed(2)),
    conceptualSnowDriftHeight: parseFloat(conceptualSnowDriftHeight.toFixed(2))
};
