function calculate({ groundSnowLoad, roofSlope, roofEaveHeight, exposureCategory, occupancyCategory, thermalCondition, currentSnowDepth, currentIceThickness }) {

    const G = 0.7; // Snow load importance factor for flat roofs (ASCE 7-16 Figure 7.3-1)

    // 1. Exposure Factor (Ce) - ASCE 7-16 Table 7.3-1
    let Ce;
    switch (exposureCategory) {
        case 'B': Ce = 0.9; break; // Partially Exposed
        case 'C': Ce = 1.0; break; // Exposed
        case 'D': Ce = 1.2; break; // Fully Exposed
        default: Ce = 1.0; // Default to Exposed if category is invalid
    }

    // Additional consideration for roofEaveHeight (simplified, ASCE has more detailed criteria)
    if (roofEaveHeight < 15 && exposureCategory === 'D') { // Buildings less than 15ft tall in Category D are less exposed
        Ce = Math.max(0.9, Ce - 0.1); // Adjust slightly, not strictly ASCE but reasonable interpretation
    }

    // 2. Thermal Factor (Kt) - ASCE 7-16 Table 7.3-2
    let Kt;
    switch (thermalCondition) {
        case 'warm': Kt = 1.0; break; // Warm Roofs (R > 30, well-ventilated)
        case 'moderate': Kt = 1.0; break; // Moderately Warm (Typical residential, R 20-30)
        case 'cold': Kt = 1.1; break; // Cold Roofs (R < 20, unheated attics)
        case 'unheated': Kt = 1.2; break; // Unheated structures
        default: Kt = 1.1; // Default to cold roof
    }

    // 3. Importance Factor (I) - ASCE 7-16 Table 1.5-2 (for snow loads)
    let I;
    switch (occupancyCategory) {
        case 'I': I = 0.8; break;
        case 'II': I = 1.0; break;
        case 'III': I = 1.1; break;
        case 'IV': I = 1.2; break;
        default: I = 1.0; // Default to standard occupancy
    }

    // 4. Flat Roof Snow Load (Pf) - ASCE 7-16 Eq. 7.3-1
    const Pf = G * Ce * Kt * I * groundSnowLoad;

    // 5. Roof Slope Factor (Cs) - ASCE 7-16 Figure 7.4-1 (Cold Roofs, Unventilated Attic)
    // For cold roofs: cs = 1.0 for slopes <= 30 deg, then decreases linearly to 0 for slopes >= 70 deg
    let Cs;
    if (roofSlope <= 30) {
        Cs = 1.0;
    } else if (roofSlope >= 70) {
        Cs = 0;
    } else {
        Cs = 1.0 - (roofSlope - 30) / (70 - 30);
    }

    // 6. Design Roof Snow Load (Ps) - ASCE 7-16 Eq. 7.4-1
    const designRoofSnowLoad = Cs * Pf;

    // 7. Current Actual Load Calculation
    let currentRoofSnowLoad = 0;
    let currentRoofIceLoad = 0;

    const snowDensityPerInch = 1.25; // Average wet snow density: approx 1.25 psf per inch of snow (can vary 0.5 to 2.5+)
    const iceDensityPerInch = 5.2; // Density of ice: approx 5.2 psf per inch of ice (62.4 lbs/cu ft / 12 inches/ft)

    if (currentSnowDepth > 0) {
        currentRoofSnowLoad = currentSnowDepth * snowDensityPerInch;
    }
    if (currentIceThickness > 0) {
        currentRoofIceLoad = currentIceThickness * iceDensityPerInch;
    }

    const totalCurrentLoad = currentRoofSnowLoad + currentRoofIceLoad;

    // 8. Remaining Capacity and Risk Assessment
    let remainingCapacityPercentage;
    if (designRoofSnowLoad <= 0) {
        remainingCapacityPercentage = 0; // Prevent division by zero
    } else {
        remainingCapacityPercentage = Math.max(0, ((designRoofSnowLoad - totalCurrentLoad) / designRoofSnowLoad) * 100);
        remainingCapacityPercentage = Math.min(100, remainingCapacityPercentage); // Cap at 100%
    }

    let riskAssessment = 'Not applicable (No current load provided)';
    if (currentSnowDepth > 0 || currentIceThickness > 0) {
        if (remainingCapacityPercentage < 10) {
            riskAssessment = 'Critical - Immediate Action Required';
        } else if (remainingCapacityPercentage < 25) {
            riskAssessment = 'High - Urgent Assessment Needed';
        } else if (remainingCapacityPercentage < 50) {
            riskAssessment = 'Moderate - Monitor Closely';
        } else {
            riskAssessment = 'Low - Normal Conditions';
        }
    }

    return {
        designRoofSnowLoad,
        currentRoofSnowLoad,
        currentRoofIceLoad,
        totalCurrentLoad,
        remainingCapacityPercentage,
        riskAssessment
    };
}
