const TARGET_5K_KM = 5;
const TARGET_10K_KM = 10;
const TARGET_HALF_MARATHON_KM = 21.0975; 
const TARGET_MARATHON_KM = 42.195;    

const MILES_TO_KM = 1.60934;
const RIEGEL_EXPONENT = 1.06; 

let D1_km = recentRaceDistanceValue;
if (recentRaceDistanceUnit.toLowerCase() === 'miles') {
    D1_km *= MILES_TO_KM;
} else if (recentRaceDistanceUnit.toLowerCase() !== 'km') {
    // Default to km if unit is not recognized, or handle as an error
    // For this predictor, we'll assume km if not explicitly 'miles'
}

const T1_seconds = (recentRaceTimeHours * 3600) + (recentRaceTimeMinutes * 60) + recentRaceTimeSeconds;

if (D1_km <= 0 || T1_seconds <= 0) {
    return {
        predicted5kTimeSeconds: 0,
        predicted10kTimeSeconds: 0,
        predictedHalfMarathonTimeSeconds: 0,
        predictedMarathonTimeSeconds: 0
    };
}

const predictTime = (D2_km) => {
    // T2 = T1 * (D2 / D1)^E
    const T2_seconds = T1_seconds * Math.pow(D2_km / D1_km, RIEGEL_EXPONENT);
    return Math.round(T2_seconds);
};

const predicted5kTimeSeconds = predictTime(TARGET_5K_KM);
const predicted10kTimeSeconds = predictTime(TARGET_10K_KM);
const predictedHalfMarathonTimeSeconds = predictTime(TARGET_HALF_MARATHON_KM);
const predictedMarathonTimeSeconds = predictTime(TARGET_MARATHON_KM);

return {
    predicted5kTimeSeconds,
    predicted10kTimeSeconds,
    predictedHalfMarathonTimeSeconds,
    predictedMarathonTimeSeconds
};
