function formatTime(totalSeconds) {
    if (isNaN(totalSeconds) || totalSeconds < 0) return 'N/A';
    const h = Math.floor(totalSeconds / 3600);
    const m = Math.floor((totalSeconds % 3600) / 60);
    const s = Math.round(totalSeconds % 60);
    return [h, m, s].map(v => v < 10 ? '0' + v : v).join(':');
}

function formatPace(totalSeconds, distanceKm) {
    if (isNaN(totalSeconds) || totalSeconds <= 0 || isNaN(distanceKm) || distanceKm <= 0) return 'N/A';
    const paceSecondsPerKm = totalSeconds / distanceKm;
    const minutes = Math.floor(paceSecondsPerKm / 60);
    const seconds = Math.round(paceSecondsPerKm % 60);
    return `${minutes}:${seconds < 10 ? '0' + seconds : seconds}`;
}

const MILE_TO_KM = 1.60934;

// Input validation and parsing
const targetTotalSeconds = (targetHours * 3600) + (targetMinutes * 60) + targetSeconds;

if (raceDistance <= 0 || targetTotalSeconds <= 0 || negativeSplitPercentage < 0 || negativeSplitPercentage > 10 || splitSegments < 2 || splitSegments % 2 !== 0) {
    throw new Error('Please ensure race distance is positive, target time is valid, negative split percentage is between 0-10, and split segments is an even number >= 2.');
}

let raceDistanceKm = raceDistanceUnit === 'mi' ? raceDistance * MILE_TO_KM : raceDistance;

// 1. Calculate Overall Pace
const overallPaceSecondsPerKm = targetTotalSeconds / raceDistanceKm;
const overallPaceSecondsPerMile = overallPaceSecondsPerKm * MILE_TO_KM;

// 2. Apply Negative Split Logic
const nsFactor = negativeSplitPercentage / 100;
let firstHalfTimeSeconds, secondHalfTimeSeconds;

if (nsFactor === 0) {
    // Even split
    firstHalfTimeSeconds = targetTotalSeconds / 2;
    secondHalfTimeSeconds = targetTotalSeconds / 2;
} else {
    // T2 = (targetTotalSeconds * (1 - nsFactor)) / (2 - nsFactor);
    // T1 = targetTotalSeconds - T2;
    // Re-evaluating the standard negative split formula: second half is faster than first half by a factor.
    // A common interpretation for negative split %: second half is X% faster than the *average pace* for that distance.
    // Or, total time = T_first + T_second. T_second = T_first * (1 - nsFactor).
    // T_first + T_first * (1 - nsFactor) = targetTotalSeconds
    // T_first * (2 - nsFactor) = targetTotalSeconds
    // T_first = targetTotalSeconds / (2 - nsFactor);
    // T_second = targetTotalSeconds - T_first;

    // Let's use the definition where the second half is 'nsFactor' faster than the first half's average pace.
    // If nsFactor is 2%, second half is 2% faster than first half.
    // If T1 is first half time, T2 is second half time.
    // T1 + T2 = Total Time
    // T2 = T1 * (1 - nsFactor)
    // T1 + T1 * (1 - nsFactor) = Total Time
    // T1 * (1 + 1 - nsFactor) = Total Time
    // T1 * (2 - nsFactor) = Total Time
    // T1 = Total Time / (2 - nsFactor)
    // T2 = Total Time - T1

    // This definition means if nsFactor is 0.05 (5%), then T2 = T1 * 0.95. This means T2 is shorter (faster).
    // Example: Total 100s, ns 5%. T1 = 100 / 1.95 = 51.28s. T2 = 48.72s. T2 is 0.95 * T1. Correct.
    firstHalfTimeSeconds = targetTotalSeconds / (2 - nsFactor);
    secondHalfTimeSeconds = targetTotalSeconds - firstHalfTimeSeconds;
}

// Half distances are equal
const firstHalfDistanceKm = raceDistanceKm / 2;
const secondHalfDistanceKm = raceDistanceKm / 2;

// Calculate Half Paces
const firstHalfPaceSecondsPerKm = firstHalfTimeSeconds / firstHalfDistanceKm;
const secondHalfPaceSecondsPerKm = secondHalfTimeSeconds / secondHalfDistanceKm;

const firstHalfPaceSecondsPerMile = firstHalfPaceSecondsPerKm * MILE_TO_KM;
const secondHalfPaceSecondsPerMile = secondHalfPaceSecondsPerKm * MILE_TO_KM;

// 3. Calculate Segment Details
const segmentDistanceKm = raceDistanceKm / splitSegments;
const numSegmentsInFirstHalf = splitSegments / 2;
const numSegmentsInSecondHalf = splitSegments / 2;

const segmentDetails = [];
for (let i = 0; i < splitSegments; i++) {
    let segmentTimeSeconds;
    if (i < numSegmentsInFirstHalf) {
        // First half segments
        segmentTimeSeconds = firstHalfTimeSeconds / numSegmentsInFirstHalf;
    } else {
        // Second half segments
        segmentTimeSeconds = secondHalfTimeSeconds / numSegmentsInSecondHalf;
    }

    const segmentPacePerKm = formatPace(segmentTimeSeconds, segmentDistanceKm);
    const segmentPacePerMile = formatPace(segmentTimeSeconds, segmentDistanceKm / MILE_TO_KM);

    segmentDetails.push({
        segmentNumber: i + 1,
        distance: `${segmentDistanceKm.toFixed(3)} km (${(segmentDistanceKm / MILE_TO_KM).toFixed(3)} mi)`,
        time: formatTime(segmentTimeSeconds),
        pacePerKm: segmentPacePerKm,
        pacePerMile: segmentPacePerMile
    });
}

return {
    overallPacePerKm: formatPace(targetTotalSeconds, raceDistanceKm),
    overallPacePerMile: formatPace(targetTotalSeconds, raceDistanceKm / MILE_TO_KM),
    firstHalfDistance: firstHalfDistanceKm.toFixed(3),
    firstHalfTime: formatTime(firstHalfTimeSeconds),
    firstHalfPacePerKm: formatPace(firstHalfTimeSeconds, firstHalfDistanceKm),
    firstHalfPacePerMile: formatPace(firstHalfTimeSeconds, firstHalfDistanceKm / MILE_TO_KM),
    secondHalfDistance: secondHalfDistanceKm.toFixed(3),
    secondHalfTime: formatTime(secondHalfTimeSeconds),
    secondHalfPacePerKm: formatPace(secondHalfTimeSeconds, secondHalfDistanceKm),
    secondHalfPacePerMile: formatPace(secondHalfTimeSeconds, secondHalfDistanceKm / MILE_TO_KM),
    segmentDetails: segmentDetails
};
