function calculateSanctionsComplianceCosts({ initialLegalAssessmentCost, ongoingLegalComplianceCost, supplyChainRestructuringCost, technologyUpgradeCost, staffTrainingCost, marketAccessImpact, auditReportingFees, riskComplexityFactor }) {
    initialLegalAssessmentCost = parseFloat(initialLegalAssessmentCost) || 0;
    ongoingLegalComplianceCost = parseFloat(ongoingLegalComplianceCost) || 0;
    supplyChainRestructuringCost = parseFloat(supplyChainRestructuringCost) || 0;
    technologyUpgradeCost = parseFloat(technologyUpgradeCost) || 0;
    staffTrainingCost = parseFloat(staffTrainingCost) || 0;
    marketAccessImpact = parseFloat(marketAccessImpact) || 0;
    auditReportingFees = parseFloat(auditReportingFees) || 0;
    riskComplexityFactor = parseFloat(riskComplexityFactor) || 1.0;

    riskComplexityFactor = Math.max(1.0, Math.min(2.5, riskComplexityFactor));

    const directCostsSum =
        initialLegalAssessmentCost +
        ongoingLegalComplianceCost +
        supplyChainRestructuringCost +
        technologyUpgradeCost +
        staffTrainingCost +
        auditReportingFees +
        marketAccessImpact;

    const complexityProneCosts =
        ongoingLegalComplianceCost +
        supplyChainRestructuringCost +
        technologyUpgradeCost +
        staffTrainingCost +
        auditReportingFees;

    const riskAdjustedOverhead = complexityProneCosts * (riskComplexityFactor - 1);

    const totalEstimatedCost = directCostsSum + riskAdjustedOverhead;

    return {
        totalEstimatedCost: Math.max(0, totalEstimatedCost),
        directCostsSum: Math.max(0, directCostsSum),
        riskAdjustedOverhead: Math.max(0, riskAdjustedOverhead)
    };
}
