const initialUpgradeCost = inputs.initialUpgradeCost;
const upgradeLifespanYears = inputs.upgradeLifespanYears;
const annualMaintenanceCost = inputs.annualMaintenanceCost;
const baselineAnnualIncidentProbability = inputs.baselineAnnualIncidentProbability;
const averageCostPerIncident = inputs.averageCostPerIncident;
const upgradeEffectivenessReduction = inputs.upgradeEffectivenessReduction;
const annualInsuranceSavings = inputs.annualInsuranceSavings;
const annualOperationalSavings = inputs.annualOperationalSavings;

// --- Calculations ---

// 1. Total Cost of Ownership (TCO) for the Upgrades
const totalUpgradeMaintenanceCost = annualMaintenanceCost * upgradeLifespanYears;
const totalCostOfOwnership = initialUpgradeCost + totalUpgradeMaintenanceCost;

// 2. Baseline Expected Annual Damages (without upgrades)
const baselineExpectedAnnualDamages = baselineAnnualIncidentProbability * averageCostPerIncident;

// 3. Post-Upgrade Expected Annual Damages
const postUpgradeIncidentProbability = baselineAnnualIncidentProbability * (1 - upgradeEffectivenessReduction);
const postUpgradeExpectedAnnualDamages = postUpgradeIncidentProbability * averageCostPerIncident;

// 4. Annual Averted Damages (due to reduced risk)
const annualAvertedDamages = baselineExpectedAnnualDamages - postUpgradeExpectedAnnualDamages;

// 5. Total Averted Damages over Lifespan
const totalAvertedDamages = annualAvertedDamages * upgradeLifespanYears;

// 6. Total Long-Term Operational Savings (Insurance + Other Savings)
const totalAnnualOperationalSavings = annualInsuranceSavings + annualOperationalSavings;
const totalLongTermSavings = totalAnnualOperationalSavings * upgradeLifespanYears;

// 7. Total Benefits
const totalBenefits = totalAvertedDamages + totalLongTermSavings;

// 8. Net Benefit
const netBenefit = totalBenefits - totalCostOfOwnership;

// 9. Return on Investment (ROI)
let roiPercentage = 0;
if (totalCostOfOwnership > 0) {
    roiPercentage = (netBenefit / totalCostOfOwnership) * 100;
} else if (netBenefit > 0) { // If cost is zero, but benefits exist, ROI is infinite positive
    roiPercentage = Infinity;
} else {
    roiPercentage = 0; // If cost and benefit are both 0 or negative/0, ROI is 0
}

// 10. Payback Period
let paybackPeriodYears = Infinity;
const annualNetGain = annualAvertedDamages + totalAnnualOperationalSavings - annualMaintenanceCost;
if (annualNetGain > 0) {
    paybackPeriodYears = initialUpgradeCost / annualNetGain;
    // Cap payback period at upgrade lifespan if it exceeds it, or ensure it's not negative if initial cost is zero
    if (paybackPeriodYears < 0 || initialUpgradeCost === 0) paybackPeriodYears = 0;
    if (paybackPeriodYears > upgradeLifespanYears) paybackPeriodYears = upgradeLifespanYears; // Can't pay back beyond its useful life
} else if (initialUpgradeCost === 0 && annualNetGain >= 0) { // If no initial cost and positive/zero annual gain
    paybackPeriodYears = 0;
}

// --- Return Results ---
return {
    totalUpgradeCost: totalCostOfOwnership,
    totalBaselineAvertedDamages: baselineExpectedAnnualDamages * upgradeLifespanYears,
    totalAvertedDamagesPostUpgrade: totalAvertedDamages,
    totalLongTermSavings: totalLongTermSavings,
    netBenefit: netBenefit,
    roiPercentage: roiPercentage,
    paybackPeriodYears: paybackPeriodYears
};
