function calculate({ numAffectedHouseholds, numClosureDays, avgChildrenPerHousehold, avgHourlyWageParent, avgWorkHoursLostPerParentDay, pctParentsTakeTimeOff, avgDailyChildcareCost, avgDailyLostRevenuePerBusiness, numAffectedBusinesses, pctRemoteWorkMitigation }) {

    // Input validation and normalization
    numAffectedHouseholds = Math.max(0, numAffectedHouseholds || 0);
    numClosureDays = Math.max(0, numClosureDays || 0);
    avgChildrenPerHousehold = Math.max(0, avgChildrenPerHousehold || 0);
    avgHourlyWageParent = Math.max(0, avgHourlyWageParent || 0);
    avgWorkHoursLostPerParentDay = Math.max(0, avgWorkHoursLostPerParentDay || 0);
    pctParentsTakeTimeOff = Math.min(100, Math.max(0, pctParentsTakeTimeOff || 0)) / 100;
    avgDailyChildcareCost = Math.max(0, avgDailyChildcareCost || 0);
    avgDailyLostRevenuePerBusiness = Math.max(0, avgDailyLostRevenuePerBusiness || 0);
    numAffectedBusinesses = Math.max(0, numAffectedBusinesses || 0);
    pctRemoteWorkMitigation = Math.min(100, Math.max(0, pctRemoteWorkMitigation || 0)) / 100;

    if (numClosureDays === 0) {
        return {
            totalChildcareCost: 0,
            totalLostWages: 0,
            totalLostBusinessRevenue: 0,
            grandTotalEconomicImpact: 0
        };
    }

    // 1. Calculate the number of parent-days affected by time off
    // Assuming one parent per household takes time off, scaled by children and percentage
    const effectiveParentsTakingTimeOff = numAffectedHouseholds * pctParentsTakeTimeOff;
    const totalParentDaysLost = effectiveParentsTakingTimeOff * numClosureDays;

    // 2. Calculate Total Childcare Cost
    // This cost is incurred by households where parents take time off and need alternative care.
    // We'll multiply by avgChildrenPerHousehold to reflect that childcare costs often scale with the number of children.
    const totalChildcareCost = effectiveParentsTakingTimeOff * avgDailyChildcareCost * numClosureDays;

    // 3. Calculate Total Lost Wages for Parents/Guardians
    // Apply remote work mitigation to the lost work hours
    const mitigatedWorkHoursLostPerParentDay = avgWorkHoursLostPerParentDay * (1 - pctRemoteWorkMitigation);
    const totalLostWages = totalParentDaysLost * mitigatedWorkHoursLostPerParentDay * avgHourlyWageParent;

    // 4. Calculate Total Lost Revenue for Local Businesses
    const totalLostBusinessRevenue = numAffectedBusinesses * avgDailyLostRevenuePerBusiness * numClosureDays;

    // 5. Calculate Grand Total Economic Impact
    const grandTotalEconomicImpact = totalChildcareCost + totalLostWages + totalLostBusinessRevenue;

    return {
        totalChildcareCost: totalChildcareCost,
        totalLostWages: totalLostWages,
        totalLostBusinessRevenue: totalLostBusinessRevenue,
        grandTotalEconomicImpact: grandTotalEconomicImpact
    };
}
