function calculate({ access_control_score, drill_frequency_efficacy_score, communication_system_score, staff_training_score, emergency_plan_review_score, threat_assessment_score, mental_health_support_score, crisis_supplies_score }) {
  // Helper to ensure input is a number and within 0-10, defaulting to 0 for invalid inputs
  const validateScore = (score) => Math.max(0, Math.min(10, parseFloat(score) || 0));

  const ac = validateScore(access_control_score);
  const df = validateScore(drill_frequency_efficacy_score);
  const cs = validateScore(communication_system_score);
  const st = validateScore(staff_training_score);
  const ep = validateScore(emergency_plan_review_score);
  const ta = validateScore(threat_assessment_score);
  const mh = validateScore(mental_health_support_score);
  const sup = validateScore(crisis_supplies_score);

  // Define weights for each category (sum should ideally be 1 or normalized later)
  const weights = {
    ac: 0.15, // Physical Security
    df: 0.20, // Drills
    cs: 0.15, // Communication
    st: 0.15, // Staff Training
    ep: 0.10, // Plan Review
    ta: 0.10, // Threat Assessment
    mh: 0.08, // Mental Health
    sup: 0.07  // Supplies
  };

  // Calculate weighted sum (maximum possible score is 10 if all inputs are 10)
  const weightedSum = 
    ac * weights.ac +
    df * weights.df +
    cs * weights.cs +
    st * weights.st +
    ep * weights.ep +
    ta * weights.ta +
    mh * weights.mh +
    sup * weights.sup;

  // Normalize to a 0-100 scale. Since weights sum to 1, max weightedSum is 10.
  const preparedness_score = (weightedSum / 10) * 100;

  let risk_level;
  if (preparedness_score >= 85) {
    risk_level = 'Low Risk - Exemplary Preparedness';
  } else if (preparedness_score >= 70) {
    risk_level = 'Moderate Risk - Good Preparedness';
  } else if (preparedness_score >= 50) {
    risk_level = 'Elevated Risk - Requires Improvement';
  } else {
    risk_level = 'High Risk - Critical Gaps Identified';
  }

  return {
    preparedness_score: parseFloat(preparedness_score.toFixed(1)),
    risk_level: risk_level
  };
}
