const durationWeeks = inputs.durationWeeks;
const dailyInstructionHours = inputs.dailyInstructionHours;
const criticalSubjectFocus = inputs.criticalSubjectFocus / 100;
const studentVulnerabilityIndex = inputs.studentVulnerabilityIndex;
const gradeLevelMultiplierId = inputs.gradeLevelMultiplierId;
const remoteLearningEffectiveness = inputs.remoteLearningEffectiveness / 100;
const preDisruptionAttendanceRate = inputs.preDisruptionAttendanceRate / 100;
const postDisruptionInterventionHoursPerWeek = inputs.postDisruptionInterventionHoursPerWeek;

const workingDaysPerWeek = 5;
const maxVulnerabilityMultiplier = 1.8; // Max impact for highest vulnerability
const minVulnerabilityMultiplier = 1.0; // Min impact for lowest vulnerability
const criticalSubjectMultiplier = 1.5; // Critical subjects have 1.5x impact on loss
const generalSubjectMultiplier = 1.0; // General subjects have 1.0x impact
const performanceDipFactor = 0.5; // Heuristic: 0.5% performance dip per week of learning loss
const interventionRecoveryFactor = 0.6; // Each intervention hour recovers 60% of a lost hour.

const gradeLevelFactors = {
    'early_ed': 1.6,
    'elementary': 1.4,
    'middle_school': 1.2,
    'high_school': 1.0
};

let totalPotentialInstructionalHours = durationWeeks * dailyInstructionHours * workingDaysPerWeek;

// 1. Adjust for remote learning effectiveness
let unmitigatedInstructionalHours = totalPotentialInstructionalHours * (1 - remoteLearningEffectiveness);

// 2. Apply critical subject focus and general subject impact
const criticalHours = unmitigatedInstructionalHours * criticalSubjectFocus;
const generalHours = unmitigatedInstructionalHours * (1 - criticalSubjectFocus);
let weightedInstructionalHoursLost = 
    (criticalHours * criticalSubjectMultiplier) + 
    (generalHours * generalSubjectMultiplier);

// 3. Factor in student demographic vulnerability
const vulnerabilityMultiplier = minVulnerabilityMultiplier + 
    ((studentVulnerabilityIndex - 1) / 4) * (maxVulnerabilityMultiplier - minVulnerabilityMultiplier);
weightedInstructionalHoursLost *= vulnerabilityMultiplier;

// 4. Apply grade level impact
const currentGradeLevelFactor = gradeLevelFactors[gradeLevelMultiplierId] || gradeLevelFactors.high_school;
weightedInstructionalHoursLost *= currentGradeLevelFactor;

// 5. Adjust for pre-disruption attendance as a baseline: Higher attendance means more students consistently engaged, so disruption affects a larger, more engaged cohort.
weightedInstructionalHoursLost *= preDisruptionAttendanceRate;

// 6. Adjust for post-disruption intervention hours
const totalInterventionHours = postDisruptionInterventionHoursPerWeek * durationWeeks; // Assumes interventions for the duration of the disruption's 'recovery period'
const recoveredHours = totalInterventionHours * interventionRecoveryFactor;

let netInstructionalHoursLost = Math.max(0, weightedInstructionalHoursLost - recoveredHours);

// Cap max loss to a reasonable upper bound to prevent unrealistic extreme values
const maxCalculableLossHours = totalPotentialInstructionalHours * criticalSubjectMultiplier * maxVulnerabilityMultiplier * gradeLevelFactors.early_ed * 1.5; // Generous cap
netInstructionalHoursLost = Math.min(netInstructionalHoursLost, maxCalculableLossHours);

// --- Outputs Calculation ---

const averageWeeklyInstructionalHours = dailyInstructionHours * workingDaysPerWeek;
let estimatedLearningLossWeeks = 0;
if (averageWeeklyInstructionalHours > 0) {
    estimatedLearningLossWeeks = netInstructionalHoursLost / averageWeeklyInstructionalHours;
}

const academicPerformanceDipPercentage = estimatedLearningLossWeeks * performanceDipFactor;

estimatedLearningLossWeeks = Math.max(0, estimatedLearningLossWeeks);
academicPerformanceDipPercentage = Math.max(0, Math.min(100, academicPerformanceDipPercentage));

return {
    estimatedLearningLossWeeks: estimatedLearningLossWeeks,
    academicPerformanceDipPercentage: academicPerformanceDipPercentage,
    totalInstructionalHoursLost: netInstructionalHoursLost
};
