const BASE_SECURITY_RATIO = 500; // 1 security per 500 students (ideal starting point)
const MAX_SECURITY_FTE = 15;
const MIN_SECURITY_FTE_FOR_STUDENTS = 1; // Min FTE if students > 0

const BASE_MENTAL_HEALTH_RATIO = 250; // 1 mental health per 250 students (ideal starting point)
const VULNERABLE_MENTAL_HEALTH_FACTOR = 0.5; // Additional 0.5 FTE per 100 vulnerable students
const MAX_MENTAL_HEALTH_FTE = 20;
const MIN_MENTAL_HEALTH_FTE_FOR_STUDENTS = 0.5; // Min FTE if students > 0

const BASE_TRAINING_BUDGET_PER_STUDENT = 15; // $15 per student for training
const COMPLEXITY_TRAINING_FACTOR = 0.1; // 10% increase per complexity score point
const MIN_BASELINE_TRAINING_BUDGET = 2000; // Minimum baseline for any school
const MAX_TRAINING_BUDGET_PERCENT_TOTAL_SAFETY = 0.25; // Training budget cannot exceed 25% of annual safety budget

const SECURITY_COST_PER_FTE = 75000; // Avg cost per security FTE
const MENTAL_HEALTH_COST_PER_FTE = 90000; // Avg cost per mental health FTE

const totalStudents = inputs.totalStudents || 0;
const vulnerableStudentPercentage = inputs.vulnerableStudentPercentage || 0;
const buildingAgeYears = inputs.buildingAgeYears || 0;
const campusComplexityScore = Math.max(1, Math.min(5, inputs.campusComplexityScore || 1)); // Clamped 1-5
const existingSecurityPersonnel = inputs.existingSecurityPersonnel || 0;
const existingMentalHealthFTE = inputs.existingMentalHealthFTE || 0;
const crisisTrainingFrequencyAnnually = inputs.crisisTrainingFrequencyAnnually || 0;
const annualSafetyBudget = inputs.annualSafetyBudget || 0;

if (totalStudents === 0) {
    return {
        recommendedSecurityPersonnelFTE: 0,
        recommendedMentalHealthFTE: 0,
        recommendedCrisisTrainingBudget: MIN_BASELINE_TRAINING_BUDGET,
        resourceAllocationIndex: annualSafetyBudget >= MIN_BASELINE_TRAINING_BUDGET ? 100 : Math.round((annualSafetyBudget / MIN_BASELINE_TRAINING_BUDGET) * 100) || 0
    };
}

// --- Step 1: Calculate Ideal Baseline Needs ---

// Security Personnel
let idealSecurityPersonnelFTE = Math.ceil(totalStudents / BASE_SECURITY_RATIO);
const ageFactor = Math.min(buildingAgeYears / 20, 1.5); // Older buildings need more oversight, cap at 1.5x of base
const complexityFactor = (campusComplexityScore - 1) * 0.15; // Each complexity point adds 15% need
idealSecurityPersonnelFTE *= (1 + ageFactor * 0.2 + complexityFactor); // Age contributes 20% of its factor, complexity contributes full factor
idealSecurityPersonnelFTE = Math.max(MIN_SECURITY_FTE_FOR_STUDENTS, Math.min(MAX_SECURITY_FTE, idealSecurityPersonnelFTE));

// Mental Health Professionals
let idealMentalHealthFTE = Math.ceil(totalStudents / BASE_MENTAL_HEALTH_RATIO);
const vulnerableStudents = totalStudents * (vulnerableStudentPercentage / 100);
idealMentalHealthFTE += (vulnerableStudents * (VULNERABLE_MENTAL_HEALTH_FACTOR / 100)); // Additional for vulnerable students (e.g., 0.5 FTE per 100 vulnerable students)
idealMentalHealthFTE = Math.max(MIN_MENTAL_HEALTH_FTE_FOR_STUDENTS, Math.min(MAX_MENTAL_HEALTH_FTE, idealMentalHealthFTE));

// Crisis Training Budget
let idealCrisisTrainingBudget = totalStudents * BASE_TRAINING_BUDGET_PER_STUDENT;
idealCrisisTrainingBudget *= (1 + (campusComplexityScore - 1) * COMPLEXITY_TRAINING_FACTOR);
idealCrisisTrainingBudget = Math.max(MIN_BASELINE_TRAINING_BUDGET, idealCrisisTrainingBudget);

// --- Step 2: Account for Existing Resources (Gap Analysis) ---

let recommendedSecurityPersonnelFTE = Math.max(0, idealSecurityPersonnelFTE - existingSecurityPersonnel);
let recommendedMentalHealthFTE = Math.max(0, idealMentalHealthFTE - existingMentalHealthFTE);

// Adjust training budget based on frequency of drills
if (crisisTrainingFrequencyAnnually >= 4) { // 4+ drills/year is considered strong
    idealCrisisTrainingBudget *= 0.75; // 25% reduction in *additional* budget needed
} else if (crisisTrainingFrequencyAnnually <= 1) { // 1 or fewer drills/year is weak
    idealCrisisTrainingBudget *= 1.25; // 25% increase in *additional* budget needed
}
let recommendedCrisisTrainingBudget = idealCrisisTrainingBudget; // For now, assume this is what's needed

// --- Step 3: Prioritize and Adjust for Annual Safety Budget ---

const idealSecurityCost = recommendedSecurityPersonnelFTE * SECURITY_COST_PER_FTE;
const idealMentalHealthCost = recommendedMentalHealthFTE * MENTAL_HEALTH_COST_PER_FTE;
const totalIdealRecommendedCost = idealSecurityCost + idealMentalHealthCost + recommendedCrisisTrainingBudget;

let finalSecurityFTE = recommendedSecurityPersonnelFTE;
let finalMentalHealthFTE = recommendedMentalHealthFTE;
let finalTrainingBudget = recommendedCrisisTrainingBudget;
let resourceAllocationIndex = 100;

if (annualSafetyBudget < totalIdealRecommendedCost) {
    resourceAllocationIndex = Math.round((annualSafetyBudget / totalIdealRecommendedCost) * 100);

    let remainingBudget = annualSafetyBudget;
    let tempSecurityFTE = 0;
    let tempMentalHealthFTE = 0;
    let tempTrainingBudget = 0;

    // Prioritization: Mental Health (high impact on student well-being) > Security (immediate safety) > Training (can be scaled)

    // Try to fund mental health first
    if (remainingBudget >= idealMentalHealthCost) {
        tempMentalHealthFTE = recommendedMentalHealthFTE;
        remainingBudget -= idealMentalHealthCost;
    } else {
        tempMentalHealthFTE = Math.floor(remainingBudget / MENTAL_HEALTH_COST_PER_FTE); // Allocate as much as possible
        remainingBudget -= (tempMentalHealthFTE * MENTAL_HEALTH_COST_PER_FTE);
    }
    tempMentalHealthFTE = Math.max(0, tempMentalHealthFTE);

    // Then security
    if (remainingBudget > 0 && remainingBudget >= idealSecurityCost) {
        tempSecurityFTE = recommendedSecurityPersonnelFTE;
        remainingBudget -= idealSecurityCost;
    } else if (remainingBudget > 0) {
        tempSecurityFTE = Math.floor(remainingBudget / SECURITY_COST_PER_FTE); // Allocate as much as possible
        remainingBudget -= (tempSecurityFTE * SECURITY_COST_PER_FTE);
    }
    tempSecurityFTE = Math.max(0, tempSecurityFTE);

    // Then training
    if (remainingBudget > 0) {
        tempTrainingBudget = Math.min(recommendedCrisisTrainingBudget, remainingBudget);
    }
    tempTrainingBudget = Math.max(0, tempTrainingBudget);

    finalSecurityFTE = tempSecurityFTE;
    finalMentalHealthFTE = tempMentalHealthFTE;
    finalTrainingBudget = tempTrainingBudget;

} else {
    // Budget is sufficient, ensure training budget doesn't exceed a reasonable percentage
    finalTrainingBudget = Math.min(recommendedCrisisTrainingBudget, annualSafetyBudget * MAX_TRAINING_BUDGET_PERCENT_TOTAL_SAFETY);
    // Adjust final figures to reflect initial recommendations (since budget is sufficient)
    finalSecurityFTE = recommendedSecurityPersonnelFTE;
    finalMentalHealthFTE = recommendedMentalHealthFTE;
}

// Final clamping and precision
finalSecurityFTE = parseFloat(finalSecurityFTE.toFixed(1));
finalMentalHealthFTE = parseFloat(finalMentalHealthFTE.toFixed(1));
finalTrainingBudget = Math.round(finalTrainingBudget);
resourceAllocationIndex = Math.max(0, Math.min(100, resourceAllocationIndex));

return {
    recommendedSecurityPersonnelFTE: finalSecurityFTE,
    recommendedMentalHealthFTE: finalMentalHealthFTE,
    recommendedCrisisTrainingBudget: finalTrainingBudget,
    resourceAllocationIndex: resourceAllocationIndex
};
