function calculate({
    currentStudentEnrollment,
    annualBudgetPerStudent,
    staffingAllocationPercent,
    technologyAllocationPercent,
    programsAllocationPercent,
    averageTeacherSalary,
    techEfficiencyFactor,
    programEffectivenessFactor,
    baseStudentPerformanceIndex
}) {
    // Input validation and normalization
    currentStudentEnrollment = Math.max(1, currentStudentEnrollment || 0);
    annualBudgetPerStudent = Math.max(0, annualBudgetPerStudent || 0);
    staffingAllocationPercent = Math.max(0, Math.min(100, staffingAllocationPercent || 0));
    technologyAllocationPercent = Math.max(0, Math.min(100, technologyAllocationPercent || 0));
    programsAllocationPercent = Math.max(0, Math.min(100, programsAllocationPercent || 0));
    averageTeacherSalary = Math.max(0, averageTeacherSalary || 0);
    techEfficiencyFactor = Math.max(0, Math.min(1, techEfficiencyFactor || 0));
    programEffectivenessFactor = Math.max(0, Math.min(2, programEffectivenessFactor || 0));
    baseStudentPerformanceIndex = Math.max(0, Math.min(100, baseStudentPerformanceIndex || 0));

    let totalAllocatedPercent = staffingAllocationPercent + technologyAllocationPercent + programsAllocationPercent;
    let operationalOverheadAllocationPercent;

    if (totalAllocatedPercent > 100) {
        // If sum exceeds 100, normalize and set operational overhead to 0
        const scaleFactor = 100 / totalAllocatedPercent;
        staffingAllocationPercent *= scaleFactor;
        technologyAllocationPercent *= scaleFactor;
        programsAllocationPercent *= scaleFactor;
        operationalOverheadAllocationPercent = 0;
    } else {
        operationalOverheadAllocationPercent = 100 - totalAllocatedPercent;
    }

    const projectedTotalBudget = currentStudentEnrollment * annualBudgetPerStudent;

    const staffingCost = (projectedTotalBudget * staffingAllocationPercent) / 100;
    const technologyCost = (projectedTotalBudget * technologyAllocationPercent) / 100;
    const programsCost = (projectedTotalBudget * programsAllocationPercent) / 100;
    const operationalOverheadCost = (projectedTotalBudget * operationalOverheadAllocationPercent) / 100;

    // Calculate Student-Teacher Ratio
    let projectedTeacherCount = 0;
    if (averageTeacherSalary > 0) {
        projectedTeacherCount = staffingCost / averageTeacherSalary;
    }
    const projectedStudentTeacherRatio = projectedTeacherCount > 0 ? currentStudentEnrollment / projectedTeacherCount : Infinity; // Handle zero teachers

    // Calculate Projected Student Performance Index
    // This is a simplified model. Factors influence performance.
    // Staffing quality/quantity, tech integration, and program effectiveness.
    // Relative weights for impact: Staffing ~40%, Technology ~30%, Programs ~30%
    const staffingImpact = (staffingAllocationPercent / 100) * 0.4; 
    const technologyImpact = (technologyAllocationPercent / 100) * techEfficiencyFactor * 0.3; 
    const programsImpact = (programsAllocationPercent / 100) * programEffectivenessFactor * 0.3; 

    let projectedPerformanceIndex = baseStudentPerformanceIndex * (1 + staffingImpact + technologyImpact + programsImpact);
    // Cap performance index at 100 and ensure minimum 0
    projectedPerformanceIndex = Math.min(100, Math.max(0, projectedPerformanceIndex));

    // Calculate Projected Operational Efficiency Score
    // Higher operational overhead relative to total budget might reduce efficiency.
    // Higher tech efficiency might increase it.
    // Score (0-100)
    let projectedEfficiencyScore = (1 - (operationalOverheadAllocationPercent / 100)) * 100 * (1 + (techEfficiencyFactor * 0.2)); 
    projectedEfficiencyScore = Math.min(100, Math.max(0, projectedEfficiencyScore));

    return {
        projectedTotalBudget: projectedTotalBudget,
        staffingCost: staffingCost,
        technologyCost: technologyCost,
        programsCost: programsCost,
        operationalOverheadCost: operationalOverheadCost,
        projectedStudentTeacherRatio: projectedStudentTeacherRatio,
        projectedPerformanceIndex: projectedPerformanceIndex,
        projectedEfficiencyScore: projectedEfficiencyScore
    };
}
