function calculateRisk(inputs) {
  const { 
    perimeterSecurityRating, accessControlRating, surveillanceCoverage, physicalBarrierRating, 
    emergencyDrillFrequency, emergencyPlanReviewFrequency, staffEmergencyTrainingCoverage, 
    counselorStudentRatio, mhProgramAvailability, communityPartnershipScore 
  } = inputs;

  // Constants for normalization and weighting
  const maxRating = 5;
  const maxPercent = 100;
  const maxDrills = 12;
  const maxReviews = 4;
  const idealCounselorRatio = 100; // e.g., 1:100
  const worstCounselorRatio = 2000; // e.g., 1:2000

  // Helper function to clamp values between 0 and 100
  const clamp = (num, min, max) => Math.max(min, Math.min(num, max));

  // 1. Calculate Physical Security Risk (weighted equally for sub-components)
  const perimeterRisk = clamp((maxRating - perimeterSecurityRating) / maxRating * 25, 0, 25);
  const accessControlSubRisk = clamp((maxRating - accessControlRating) / maxRating * 25, 0, 25);
  const surveillanceSubRisk = clamp((maxPercent - surveillanceCoverage) / maxPercent * 25, 0, 25);
  const barrierSubRisk = clamp((maxRating - physicalBarrierRating) / maxRating * 25, 0, 25);
  const physicalSecurityRisk = clamp((perimeterRisk + accessControlSubRisk + surveillanceSubRisk + barrierSubRisk), 0, 100);

  // 2. Calculate Emergency Response Risk (weighted equally for sub-components)
  const drillSubRisk = clamp((maxDrills - emergencyDrillFrequency) / maxDrills * 33.33, 0, 33.33);
  const reviewSubRisk = clamp((maxReviews - emergencyPlanReviewFrequency) / maxReviews * 33.33, 0, 33.33);
  const trainingSubRisk = clamp((maxPercent - staffEmergencyTrainingCoverage) / maxPercent * 33.33, 0, 33.33);
  const emergencyResponseRisk = clamp((drillSubRisk + reviewSubRisk + trainingSubRisk), 0, 100);

  // 3. Calculate Mental Health Support Risk
  let counselorRisk;
  if (counselorStudentRatio <= idealCounselorRatio) {
    counselorRisk = 0; // Ideal or better ratio
  } else {
    counselorRisk = clamp((counselorStudentRatio - idealCounselorRatio) / (worstCounselorRatio - idealCounselorRatio) * 50, 0, 50);
  }
  const mhProgramSubRisk = clamp((maxRating - mhProgramAvailability) / maxRating * 50, 0, 50);
  const mentalHealthRisk = clamp((counselorRisk + mhProgramSubRisk), 0, 100);

  // 4. Calculate Community Engagement Risk
  const communityEngagementRisk = clamp((maxRating - communityPartnershipScore) / maxRating * 100, 0, 100);

  // 5. Calculate Overall Safety Risk Score (weighted average)
  const overallSafetyRiskScore = clamp(
    (physicalSecurityRisk * 0.30) +
    (emergencyResponseRisk * 0.30) +
    (mentalHealthRisk * 0.25) +
    (communityEngagementRisk * 0.15),
    0, 100
  );

  return {
    overallSafetyRiskScore: overallSafetyRiskScore,
    physicalSecurityRisk: physicalSecurityRisk,
    emergencyResponseRisk: emergencyResponseRisk,
    mentalHealthRisk: mentalHealthRisk,
    communityEngagementRisk: communityEngagementRisk
  };
}
