const validatedPollen = Math.max(0, Math.min(inputs.pollenCount || 0, 1000));
const pollenFactor = Math.min(validatedPollen / 200, 2.0); 

const validatedSensitivity = Math.max(1, Math.min(inputs.sensitivity || 1, 10));
const sensitivityFactor = validatedSensitivity / 10; 

const validatedHistoricalSeverity = Math.max(1, Math.min(inputs.historicalSeverity || 1, 5));
const historicalFactor = validatedHistoricalSeverity / 5; 

const medicationMultiplier = (inputs.medicationUsage || '').toLowerCase() === 'yes' ? 0.6 : 1.0; 

const validatedOutdoorHours = Math.max(0, Math.min(inputs.outdoorHours || 0, 24));
const outdoorHoursFactor = Math.min(validatedOutdoorHours / 4, 1.5); 

const validatedWindSpeed = Math.max(0, inputs.windSpeed || 0);
const windMultiplier = (validatedWindSpeed > 0) ? (1 + Math.min(validatedWindSpeed / 15, 0.5)) : 1.0; 

const peakSeasonMultiplier = (inputs.isPeakSeason || '').toLowerCase() === 'yes' ? 1.3 : 1.0; 

let baseLikelihood = (
    (pollenFactor * 0.45) + 
    (sensitivityFactor * 0.25) + 
    (historicalFactor * 0.15) + 
    (outdoorHoursFactor * 0.15)
);

let adjustedLikelihood = baseLikelihood * medicationMultiplier * peakSeasonMultiplier * windMultiplier;
adjustedLikelihood = Math.max(0, Math.min(adjustedLikelihood * 100, 100));

let baseSeverity = (
    (sensitivityFactor * 0.4) + 
    (historicalFactor * 0.3) + 
    (pollenFactor * 0.2) + 
    (outdoorHoursFactor * 0.1)
);

let adjustedSeverity = baseSeverity * medicationMultiplier * peakSeasonMultiplier * windMultiplier;
adjustedSeverity = Math.max(1.0, Math.min(adjustedSeverity * 5, 5.0)); 

let riskCategory = "Low";
if (adjustedLikelihood > 35 && adjustedSeverity > 2.0) {
    riskCategory = "Moderate";
}
if (adjustedLikelihood > 65 && adjustedSeverity > 3.5) {
    riskCategory = "High";
}
if (adjustedLikelihood > 85 && adjustedSeverity > 4.5) {
    riskCategory = "Severe";
}

if (medicationMultiplier < 1.0 && adjustedLikelihood < 20 && adjustedSeverity < 2.0) {
    riskCategory = "Very Low"; 
}

return {
    likelihood: Math.round(adjustedLikelihood),
    predictedSeverity: parseFloat(adjustedSeverity.toFixed(1)),
    riskCategory: riskCategory
};
