function calculateRisk(inputs) {
  const { 
    localTemperature, localHumidity, handHygieneFrequency, maskUsageFrequency,
    indoorCrowdExposureHours, outdoorCrowdExposureHours, vaccinationStatus,
    underlyingHealthConditions, sleepHours, dietQuality
  } = inputs;

  let totalRisk = 0;

  // 1. Environmental Risk Score (Max 25 points)
  let environmentalRiskScore = 0;
  let tempMultiplier = 1;
  if (localTemperature < 10) tempMultiplier += (10 - localTemperature) * 0.08;
  else if (localTemperature > 25) tempMultiplier += (localTemperature - 25) * 0.03;
  tempMultiplier = Math.min(tempMultiplier, 2.5); // Cap for extreme temps

  let humidityMultiplier = 1;
  if (localHumidity < 30) humidityMultiplier += (30 - localHumidity) * 0.05;
  else if (localHumidity > 60) humidityMultiplier += (localHumidity - 60) * 0.02;
  humidityMultiplier = Math.min(humidityMultiplier, 2.0); // Cap for extreme humidity

  environmentalRiskScore = (10 * tempMultiplier * 0.6) + (10 * humidityMultiplier * 0.4); // Base 10 points
  environmentalRiskScore = Math.min(environmentalRiskScore, 25);
  totalRisk += environmentalRiskScore;

  // 2. Exposure Risk Score (Max 40 points)
  let exposureRiskScore = 0;
  const indoorRisk = indoorCrowdExposureHours * 3.5;
  const outdoorRisk = outdoorCrowdExposureHours * 0.9;
  exposureRiskScore = Math.min(indoorRisk + outdoorRisk, 40);
  totalRisk += exposureRiskScore;

  // 3. Vulnerability Score (Max 35 points)
  let vulnerabilityScore = 0;
  if (underlyingHealthConditions === 1) vulnerabilityScore += 7;
  if (underlyingHealthConditions === 2) vulnerabilityScore += 15;
  if (underlyingHealthConditions === 3) vulnerabilityScore += 28;

  if (vaccinationStatus === 0) vulnerabilityScore += 10; // Not vaccinated
  else if (vaccinationStatus === 1) vulnerabilityScore += 3; // Partially vaccinated
  // if (vaccinationStatus === 2) vulnerabilityScore += 0; // Fully vaccinated

  vulnerabilityScore = Math.min(vulnerabilityScore, 35);
  totalRisk += vulnerabilityScore;

  // 4. Protective Behavior Adjustment (Multiplier, reduces risk by up to 60%)
  let totalBehaviorReduction = 0;
  // Hand Hygiene: 0.03 reduction per wash, max 0.3 for 10+ washes
  const hygieneReduction = Math.min(handHygieneFrequency * 0.03, 0.3);
  // Mask Usage: up to 0.4 reduction
  const maskReduction = maskUsageFrequency * 0.4;

  totalBehaviorReduction = Math.min(hygieneReduction + maskReduction, 0.6); // Cap combined reduction
  let behaviorMultiplier = 1 - totalBehaviorReduction;
  totalRisk *= behaviorMultiplier;

  // 5. Immune System Support Adjustment (Multiplier, reduces risk by up to 25%)
  let totalImmuneReduction = 0;
  // Sleep: optimal 7-9 hours gives 0.15 reduction. Less/more gives less.
  let sleepReduction = 0;
  if (sleepHours >= 7 && sleepHours <= 9) sleepReduction = 0.15;
  else if (sleepHours < 7) sleepReduction = (sleepHours - 4) * 0.03; // 4h = 0, 6h = 0.06
  else if (sleepHours > 9) sleepReduction = (12 - sleepHours) * 0.02; // 12h = 0.06, 10h = 0.04
  sleepReduction = Math.min(Math.max(sleepReduction, 0), 0.15); 

  // Diet Quality: 0.05 per level, max 0.15
  let dietReduction = dietQuality * 0.05;
  dietReduction = Math.min(dietReduction, 0.15);

  totalImmuneReduction = Math.min(sleepReduction + dietReduction, 0.25); // Cap combined reduction
  let immuneMultiplier = 1 - totalImmuneReduction;
  totalRisk *= immuneMultiplier;

  // Final Risk Score Scaling and Clamping
  let riskScore = Math.round(Math.min(Math.max(totalRisk, 0), 100));

  // Determine Risk Category
  let riskCategory;
  if (riskScore <= 25) riskCategory = 'Low Risk';
  else if (riskScore <= 50) riskCategory = 'Moderate Risk';
  else if (riskScore <= 75) riskCategory = 'High Risk';
  else riskCategory = 'Very High Risk';

  // Personalized Recommendations
  let recommendations = [];

  if (riskCategory === 'Low Risk') {
    recommendations.push('Maintain your current excellent habits to minimize risk.');
  } else if (riskCategory === 'Moderate Risk') {
    recommendations.push('Consider reviewing areas for improvement to further reduce your risk.');
  } else if (riskCategory === 'High Risk') {
    recommendations.push('Immediate action is recommended to lower your transmission risk. Focus on key protective measures.');
  } else { // Very High Risk
    recommendations.push('Your risk factors are significantly elevated. Strict adherence to protective measures and potential reduction of exposure are strongly advised.');
  }

  // Specific recommendations based on highest contributing factors or lowest protective factors
  if (environmentalRiskScore > 18) {
    recommendations.push('Environmental factors (temperature/humidity) are contributing significantly. Be extra vigilant when conditions are unfavorable.');
  }
  if (exposureRiskScore > 30) {
    recommendations.push('High exposure to crowds, especially indoors, is a major risk factor. Consider reducing time in crowded spaces or enhancing mask use.');
  }
  if (vulnerabilityScore > 20) {
    recommendations.push('Your personal vulnerability (e.g., health conditions, vaccination status) is a key concern. Consult healthcare professionals for further guidance.');
  }
  if (behaviorMultiplier > 0.6) { // Means low reduction from protective behaviors
    if (handHygieneFrequency < 5) recommendations.push('Increase hand washing/sanitizing frequency, especially after touching public surfaces.');
    if (maskUsageFrequency < 0.5) recommendations.push('Consider more consistent mask usage in crowded or indoor public settings.');
  }
  if (immuneMultiplier > 0.8) { // Means low reduction from immune support
    if (sleepHours < 7 || sleepHours > 9) recommendations.push('Aim for 7-9 hours of quality sleep per night to bolster your immune system.');
    if (dietQuality < 2) recommendations.push('Improve your diet with nutrient-rich foods to support immune function.');
  }

  // Deduplicate and format recommendations
  recommendations = [...new Set(recommendations)];

  return {
    riskScore: riskScore,
    riskCategory: riskCategory,
    personalizedRecommendations: recommendations.join(' ')
  };
}
