function calculateRisk(inputs) {
  const { 
    communication_method_type,
    threat_actor_sophistication,
    data_sensitivity,
    jurisdiction_risk_level,
    encryption_strength_level,
    metadata_exposure_risk,
    anonymity_effort_level,
    user_security_awareness
  } = inputs;

  // Normalize inputs to 0-1 scale for easier calculation
  const threatActorNormalized = threat_actor_sophistication / 10;
  const dataSensitivityNormalized = data_sensitivity / 10;
  const jurisdictionRiskNormalized = jurisdiction_risk_level / 10;
  const encryptionStrengthNormalized = encryption_strength_level / 5;
  const metadataExposureNormalized = metadata_exposure_risk / 5;
  const anonymityEffortNormalized = anonymity_effort_level / 5;
  const userAwarenessNormalized = user_security_awareness / 5;

  // Base scores/penalties based on communication method (0-10 scale for sub-scores, 0-100 for overall risk contribution)
  let baseCensorshipResistance = 5;
  let baseSurveillanceEvasion = 5;
  let baseDataCompromise = 5;
  let overallRiskBase = 50; // Neutral starting point for overall risk

  const method = communication_method_type.toLowerCase().trim();

  if (method.includes('e2ee') || method.includes('encrypted messenger') || method.includes('signal') || method.includes('threema')) {
    baseCensorshipResistance = 6;
    baseSurveillanceEvasion = 7;
    baseDataCompromise = 8;
    overallRiskBase = 30; 
  } else if (method.includes('satellite internet') || method.includes('starlink')) {
    baseCensorshipResistance = 8;
    baseSurveillanceEvasion = 6; // ISP knows location/usage
    baseDataCompromise = 6;
    overallRiskBase = 40;
  } else if (method.includes('vpn')) {
    baseCensorshipResistance = 7;
    baseSurveillanceEvasion = 5; // Depends heavily on VPN provider trust
    baseDataCompromise = 5;
    overallRiskBase = 45;
  } else if (method.includes('tor')) {
    baseCensorshipResistance = 9;
    baseSurveillanceEvasion = 9;
    baseDataCompromise = 7;
    overallRiskBase = 25;
  } else if (method.includes('sms') || method.includes('text message')) {
    baseCensorshipResistance = 2;
    baseSurveillanceEvasion = 1;
    baseDataCompromise = 1;
    overallRiskBase = 90;
  } else if (method.includes('email')) {
    baseCensorshipResistance = 3;
    baseSurveillanceEvasion = 2;
    baseDataCompromise = 2;
    overallRiskBase = 80;
  } else if (method.includes('public wi-fi') || method.includes('unsecured')) {
    baseCensorshipResistance = 1;
    baseSurveillanceEvasion = 1;
    baseDataCompromise = 1;
    overallRiskBase = 95;
  } 
  // Default values are for an 'unknown' or 'generic' method

  // Calculate Censorship Resistance Score (higher is better, max 10)
  let censorshipResistance = baseCensorshipResistance;
  censorshipResistance -= jurisdictionRiskNormalized * 4; // Higher jurisdiction risk reduces resistance significantly
  censorshipResistance += anonymityEffortNormalized * 3; // More anonymity improves resistance
  censorshipResistance += (1 - metadataExposureNormalized) * 1; // Lower metadata exposure helps
  censorshipResistance = Math.max(0, Math.min(10, censorshipResistance));

  // Calculate Surveillance Evasion Score (higher is better, max 10)
  let surveillanceEvasion = baseSurveillanceEvasion;
  surveillanceEvasion -= threatActorNormalized * 3; // Higher threat actor sophistication reduces evasion
  surveillanceEvasion -= jurisdictionRiskNormalized * 4; // Higher jurisdiction risk reduces evasion
  surveillanceEvasion += anonymityEffortNormalized * 4; // More anonymity improves evasion significantly
  surveillanceEvasion += (1 - metadataExposureNormalized) * 3; // Lower metadata exposure improves evasion
  surveillanceEvasion += userAwarenessNormalized * 1; // Awareness helps avoid traps
  surveillanceEvasion = Math.max(0, Math.min(10, surveillanceEvasion));

  // Calculate Data Compromise Risk Score (higher is better, max 10)
  let dataCompromise = baseDataCompromise;
  dataCompromise -= threatActorNormalized * 3; // Higher threat actor sophistication increases compromise risk
  dataCompromise += encryptionStrengthNormalized * 5; // Stronger encryption significantly reduces risk
  dataCompromise += userAwarenessNormalized * 2; // Awareness reduces human error vulnerabilities
  dataCompromise -= metadataExposureNormalized * 1; // Metadata exposure can sometimes aid in compromise (e.g., targeted phishing)
  dataCompromise = Math.max(0, Math.min(10, dataCompromise));

  // Calculate Overall Risk Score (lower is better, max 100)
  let overallRisk = overallRiskBase;
  // Factors that increase overall risk
  overallRisk += threatActorNormalized * 20;
  overallRisk += dataSensitivityNormalized * 25; // High sensitivity significantly increases impact of risk
  overallRisk += jurisdictionRiskNormalized * 15;
  overallRisk += metadataExposureNormalized * 10;
  // Factors that decrease overall risk
  overallRisk -= encryptionStrengthNormalized * 15;
  overallRisk -= anonymityEffortNormalized * 10;
  overallRisk -= userAwarenessNormalized * 10;

  // Adjust overall risk based on the calculated sub-scores
  // Lower sub-scores (meaning worse performance) should increase overall risk.
  // Max score for sub-scores is 10, so convert to risk contribution (0=good, 10=bad)
  overallRisk += (10 - censorshipResistance) * 2;
  overallRisk += (10 - surveillanceEvasion) * 2.5;
  overallRisk += (10 - dataCompromise) * 3;

  overallRisk = Math.max(0, Math.min(100, overallRisk));

  return {
    overall_risk_score: overallRisk,
    censorship_resistance_score: censorshipResistance,
    surveillance_evasion_score: surveillanceEvasion,
    data_compromise_risk_score: dataCompromise
  };
}
