function calculateSecurityROICalc(inputs) {
  const initial_upgrade_cost = parseFloat(inputs.initial_upgrade_cost) || 0;
  const annual_maintenance_cost = parseFloat(inputs.annual_maintenance_cost) || 0;
  const asset_value_at_risk = parseFloat(inputs.asset_value_at_risk) || 0;
  const annual_probability_of_loss_pre = (parseFloat(inputs.annual_probability_of_loss_pre) || 0) / 100;
  const annual_probability_of_loss_post = (parseFloat(inputs.annual_probability_of_loss_post) || 0) / 100;
  const avg_loss_reduction_rate = (parseFloat(inputs.avg_loss_reduction_rate) || 0) / 100;
  const annual_insurance_premium_pre = parseFloat(inputs.annual_insurance_premium_pre) || 0;
  const annual_insurance_premium_post_reduction = (parseFloat(inputs.annual_insurance_premium_post_reduction) || 0) / 100;
  const project_horizon_years = parseInt(inputs.project_horizon_years) || 1;

  // Input validation and clamping
  const clamped_annual_probability_of_loss_pre = Math.max(0, Math.min(1, annual_probability_of_loss_pre));
  const clamped_annual_probability_of_loss_post = Math.max(0, Math.min(1, annual_probability_of_loss_post));
  const clamped_avg_loss_reduction_rate = Math.max(0, Math.min(1, avg_loss_reduction_rate));
  const clamped_annual_insurance_premium_post_reduction = Math.max(0, Math.min(1, annual_insurance_premium_post_reduction));

  // Ensure post-upgrade probability is not higher than pre-upgrade probability
  const effective_annual_probability_of_loss_post = Math.min(clamped_annual_probability_of_loss_pre, clamped_annual_probability_of_loss_post);

  // 1. Calculate Expected Annual Loss (EAL) before and after upgrade
  const expected_annual_loss_pre = asset_value_at_risk * clamped_annual_probability_of_loss_pre;
  const expected_annual_loss_post = asset_value_at_risk * effective_annual_probability_of_loss_post * (1 - clamped_avg_loss_reduction_rate);

  // 2. Determine Annual Savings
  const annual_loss_reduction = Math.max(0, expected_annual_loss_pre - expected_annual_loss_post);
  const annual_insurance_savings = annual_insurance_premium_pre * clamped_annual_insurance_premium_post_reduction;
  const total_annual_savings = annual_loss_reduction + annual_insurance_savings;

  // 3. Compute Total Cost of Upgrade over project horizon
  const total_cost_of_upgrade = initial_upgrade_cost + (annual_maintenance_cost * project_horizon_years);

  // 4. Calculate Net Benefit and ROI percentage
  const total_operational_cost_over_horizon = initial_upgrade_cost + (annual_maintenance_cost * project_horizon_years);
  const total_benefits_over_horizon = total_annual_savings * project_horizon_years;
  const net_benefit_over_horizon = total_benefits_over_horizon - total_operational_cost_over_horizon;

  let roi_percentage = 0;
  if (initial_upgrade_cost > 0) {
    roi_percentage = (net_benefit_over_horizon / initial_upgrade_cost) * 100;
  } else if (net_benefit_over_horizon > 0) {
    roi_percentage = Infinity; // Infinite ROI if initial cost is zero and there are benefits
  } else {
    roi_percentage = 0; // No ROI if no initial cost and no net benefit
  }

  // 5. Estimate Payback Period
  let payback_period_years;
  const net_annual_cash_flow = total_annual_savings - annual_maintenance_cost;

  if (net_annual_cash_flow > 0) {
    payback_period_years = initial_upgrade_cost / net_annual_cash_flow;
    if (payback_period_years < 0) payback_period_years = Infinity; // Should not happen with current logic, but as a safeguard
  } else if (net_annual_cash_flow === 0 && initial_upgrade_cost > 0) {
    payback_period_years = Infinity; // Never pays back if annual savings equal maintenance costs
  } else if (net_annual_cash_flow < 0 && initial_upgrade_cost > 0) {
    payback_period_years = Infinity; // Never pays back if annual costs exceed savings
  } else if (initial_upgrade_cost === 0) {
    payback_period_years = 0; // Instant payback if no initial cost
  } else {
    payback_period_years = Infinity;
  }

  // Cap payback period at project horizon if it exceeds it
  payback_period_years = (payback_period_years > project_horizon_years) ? Infinity : payback_period_period_years;
  if (payback_period_years === Infinity) {
      payback_period_years = 'Not Achieved';
  }

  return {
    total_cost_of_upgrade: total_operational_cost_over_horizon,
    expected_annual_loss_pre: expected_annual_loss_pre,
    expected_annual_loss_post: expected_annual_loss_post,
    annual_loss_reduction: annual_loss_reduction,
    annual_insurance_savings: annual_insurance_savings,
    total_annual_savings: total_annual_savings,
    net_benefit_over_horizon: net_benefit_over_horizon,
    roi_percentage: roi_percentage,
    payback_period_years: payback_period_years
  };
}
